E_D("DROP TABLE IF EXISTS `shengxiao_data`;");
E_C("CREATE TABLE `shengxiao_data` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(250) NOT NULL COMMENT '标题',
  `content` text NOT NULL COMMENT '内容',
  `tid` int(11) NOT NULL COMMENT '栏目',
  `click` int(11) NOT NULL COMMENT '点击数',
  `order` int(11) NOT NULL COMMENT '排序',
  `status` int(11) NOT NULL COMMENT '状态',
  `uptime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `istop` int(11) NOT NULL COMMENT '推荐位',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=855 DEFAULT CHARSET=utf8");
E_D("replace into `shengxiao_data` values('10','2015年8月运势预测生肖鼠篇','<div class=\"sbody\">\r\n\r\n<div class=\"adb adb_1\"></div>\r\n<div class=''adb adb_2''></div>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp; 【导语】<strong>2015年8月<u>运势</u>预测<u>生肖</u>鼠篇</strong>。想知道2015年8月运势预测<u>生肖鼠</u>篇的走势会如何呢？生肖鼠，位于12生肖之首的第1位。生肖鼠人勤劳且注重积存，外表缄默寡言，冲动却能自控。出生年份包括2008年、1996年、1984年、1972年、1960年、1948年&hellip;&hellip;即将到来的阳历8月，你们的运势会有哪些变化？2015年8月运势预测生肖鼠篇，请随114啦星座小编来了解。</p>\r\n<p style=\"text-align: center\">\r\n	<span style=\"color: #ff0000\"><strong>2015年8月运势预测生肖鼠篇</strong></span></p>\r\n<p style=\"text-align: center\">\r\n	<span style=\"color: #ff0000\"><strong><img alt=\"2015年8月运势预测生肖鼠篇\" src=\"http://www.xingzuowu.com/uploads/150724/5296-150H4120Q6254.jpg\" style=\"width: 450px; height: 300px\" title=\"2015年8月运势预测生肖鼠篇\" /></strong></span></p>\r\n<p>\r\n	<strong>&nbsp;&nbsp;&nbsp; 2015年8月运势预测生肖鼠篇之【<u>爱情运势</u>】</strong></p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp; 不管是否单身，爱情运势通常都是年轻一代所关注的重点。小编也来迎合年轻一族的口味，先来谈谈8月生肖鼠的爱情。值得高兴的是，生肖鼠<u>本月</u>的爱情夹杂的是<u>好运</u>和福气。单身的生肖鼠可以借助本月大好的势头，主动积极参与各项文艺类型的活动。举例说明，比如参加咖啡文化交流、逛逛艺术博物馆等。说不定能邂逅志趣相投的异性哦！</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp; 至于已经有伴侣的生肖鼠，本月情商为爱情加分。之前堆积的误会终于有机会一一化解，而且还懂得制造浪漫惊喜来活跃气氛哦！有恋人但未婚者，本月有望涉及谈婚论嫁的大计。假如还觉得好运福气不够，那么生肖鼠（不论有伴与否）可以佩戴亮红色的天然极品红豆杉佛珠，这样便能够帮助提升爱情运，改善人缘。</p>\r\n<p>\r\n	<strong>&nbsp;&nbsp;&nbsp; 2015年8月运势预测生肖鼠篇之【<u>事业</u>运势】</strong></p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp; 生肖鼠踏入阳历的8月份之后，恰好遇到能够带来较强贵人运的吉星&mdash;&mdash;龙德。既然有给力的贵人运，想必本月生肖鼠的事业运也不会差到哪里去。本月总能在遇到阻滞的关键时刻，有贵人出手相助。就好比如创业者的生肖鼠，本月会遇到很多渠道解决资金问题。至于位处管理层的生肖鼠，也能开拓出较好的新市场计划。</p>\r\n<p>\r\n	&nbsp;&nbsp;<strong>&nbsp; 2015年8月运势预测生肖鼠篇之【钱<u>财运</u>势】</strong></p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp; 除了以上的好运庇护之外，生肖鼠本月的正财运也是眉笑颜开的节奏。本月随着努力的打拼，生肖鼠从正职工作岗位的收入颇为可观。再加上本月额外支出较少，所以储蓄数目便有所增长。小编建议生肖鼠朋友，本月可以适当地在教育增值方面投资自己。而月底的时候，财神上门来，如果关注股票基金的朋友，可以留意时局，说不定能大赚一笔哦。</p>\r\n<p>\r\n	&nbsp;&nbsp;<strong>&nbsp; 2015年8月运势预测生肖鼠篇之【健康运势】</strong></p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp; 最后，我们来关注生肖鼠这位8月幸运儿的健康运。所谓&ldquo;身体是革命的本钱&rdquo;，要是健康出了问题，以上的好运都将会成为浮云。不过这样的担忧并没有出现在生肖鼠的身上，本月健康运极佳，整个人精力充沛精神焕发。但也不能过分大意，尤其是月底的时候，容易因工作忙碌操劳而影响健康，务必要保持睡眠的质量。另外，生肖鼠的老年人，可以适当地补补身子。（完）</p>\r\n<p style=\"text-align: center\">\r\n	<strong></strong></p>\r\n\r\n<div class=\"adb adb_3\"></div>\r\n\r\n</div>\r\n<div class=\"pagelist\"></div>','1','0','0','0','2015-09-15 18:29:19','0');");
E_D("replace into `shengxiao_data` values('11','1948年属鼠的人2016年运程','<div class=\"sbody\">\r\n\r\n<div class=\"adb adb_1\"></div>\r\n<div class=''adb adb_2''></div>\r\n<p>\r\n	1948年<u>属鼠</u>的人2016年运程</p>\r\n<p>\r\n	<u>运势</u>关键词：从容不迫、渐入佳境</p>\r\n<p>\r\n	<strong>1948年属鼠的人2016年趋吉避凶预测</strong></p>\r\n<p>\r\n	吉运方面，今年有金匮与将星同临，<u>财运</u>势如破竹一路飙升，将星入命，代表官运亨通，从政的属鼠人今年可以大展拳脚，有实现宏图之志的可能。三台星照临，代表有权有势的人能够执掌高位，好交朋友，为人干脆利落。人际关系拓展迅速，能够巩固现有的人脉资源，<u>事业</u>运长盛不衰，国家机关或者企事业单位负责人，只要好好发挥个人实力，能够在短时间内有极大成就，成为社会瞩目的人。</p>\r\n<p>\r\n	凶势方面，今年虽然有三大吉星坐镇，财运、官运都非常好，但是过去的一些烂摊子这个时候却可能拖你后腿。五鬼星照临，会有人口不安、疾病、死亡等凶兆，属鼠人在外冲击拼搏太盛，家宅方面却不得安宁，在这一年易出现急病或者突发意外，甚至血光之灾，属鼠人一定要多做防范，最好能有人一直陪伴左右。属鼠人本身如果生机不足，也非常容易遭受灾祸，小到伤风感冒，大到车祸事故，都是要注意的地方。天哭星，主忧伤刑克、丧亡孝服，加会丧门，可能导致情绪失控，招来不必要的麻烦。建议平时多做养气功夫。</p>\r\n<p>\r\n	<strong>48年属鼠的人2016年婚姻运：</strong></p>\r\n<p>\r\n	已婚的属鼠人丙申年受到桃花运的正面影响，能够和身边的人保持良好和谐的关系，一些存在家庭矛盾也可以趁这个时候好好理一理。夫妻感情还算和睦，生活可能越发平淡，这个时候不妨抽时间，和家人一起外出看看，世界这么大，感受一下自身的渺小和生命的短暂，可以让你们更加珍惜彼此，有利于感情生活的回暖。</p>\r\n<p>\r\n	<strong>48年属鼠的2016年财运运程</strong></p>\r\n<p>\r\n	今年偏财要比正财更加有利，所以主投资的属鼠人将会比主经商的属鼠人有更大的收益。投资方面可以多考虑短期与长期相结合，股票、基金和房地产等投资理财产品都可以尝试，需要注意千万不要轻信所谓的专家，一切最好自己了解清楚然后再出手。</p>\r\n<p>\r\n	<strong>1948年属鼠人的<u>性格</u>：</strong></p>\r\n<p>\r\n	属鼠人性格：性柔和，为个坦诚，单纯，具有敏悦的直观，以感觉来判断事物的能力强，属性急、急功型、虚荣心强，常因异性之事而失败，经济、财运佳。&nbsp;</p>\r\n<p>\r\n	<strong>1948年出生的人猴年运势</strong></p>\r\n<p>\r\n	1948年出生的属鼠朋友，今年流年运逢印枭运，事业方面有贵人支持，能产生很大的助力，名声上也会产生较广泛的有益影响。今年有吉星坐镇，家庭方面气氛和谐，经济条件良好的<u>属蛇</u>人今年不仅能够有不错的健康状况，而且能够享受天伦之乐，平安度过一整年。健康方面还是需要多加注意，尽量不要吸烟、饮酒，谨防肺部、肝部疾病，定期去医院检查是个不错的习惯。</p>\r\n<p>\r\n	<strong>1948年属鼠的人2016年旺运锦囊</strong></p>\r\n<p>\r\n	1948年属鼠人可以佩戴【慈元阁玄龙望尊】玉坠保个人平安顺畅，此玉坠整体为一条腾云驾雾之龙，飞龙在天，有包藏宇宙之机，吞吐天地之志，举首望日，意为冲破阴霾，蓬荜生辉，能保护属鼠人平安度过丙申年。属鼠人还可选用【三合莲花】项链搭配玉坠，更能稳固运势，大放威光，驱邪纳福，发挥更强功效。同时，属鼠人可在家中或办公室摆放【慈元阁玄龙望尊】摆件调和<u>风水</u>，以保家宅运、旺事业运。</p>\r\n\r\n<div class=\"adb adb_3\"></div>\r\n\r\n</div>\r\n<div class=\"pagelist\"></div>','1','0','0','0','2015-08-31 17:36:32','0');");
E_D("replace into `shengxiao_data` values('12','1960年属鼠的人2016年运程','<div class=\"sbody\">\r\n\r\n<div class=\"adb adb_1\"></div>\r\n<div class=''adb adb_2''></div>\r\n<p>\r\n	1960年<u>属鼠</u>的人2016年运程</p>\r\n<p>\r\n	<u>运势</u>关键词：从容不迫、渐入佳境</p>\r\n<p>\r\n	<strong>1960年属鼠的人2016年<u>财运</u>：</strong></p>\r\n<p>\r\n	丙申年属鼠人的财运有渐入佳境的迹象，并且很有机会偏财会比正财多，因此可以多注意金融投资行情。除了股票、基金之外，不动产方面也是早买早赚到，可以多考虑一下投资房产，选择市场持续利好的城市，做一些长期的投资。有创业打算的属鼠人，不妨与他人合伙，共同承担风险，有利于减少资金投入，保障财产安全。今年在财运上要小心朋友背信、倒会，或借钱不还，所以辛苦得来的收入可以及早分散投资，留一部分以备急用。千万不要将钱财花在享受、赌博等上面，正所谓花钱如流水，又俗话说十赌九输，耽于享受和沉迷赌博，都可能会给你的财富带来毁灭性的灾难，切记切记。</p>\r\n<p>\r\n	<strong>1960年生属鼠人2016年<u>事业</u>运程：</strong></p>\r\n<p>\r\n	经商的朋友虽难称的上是一本万利，但是比起往年可算是踏实多了，今年你必需时刻警惕自己不再犯下以往的过失，并且提升自己的竞争力，不要原地踏步，你的锋芒将会渐露。上班族更可以趁今年显露你的才华，用心去追求与表现，今年可以容易获得晋升喔！在求职上也将较去年得心应手。金匮将星齐现，在机关单位里面努力的小职员，今年有很多机遇能够更上一层楼。计划独立经营的属鼠人，今年有将星坐镇，利于发挥领导才能，组建坚实团队，开创事业新局面，前期投入较大，甚至入不敷出，可以考虑与人合作，共同分担前期投入。创业计划如果非常好的，可以考虑引进天使投资，但是一定要经过正规法律程序，明确各方责任和职权，以免后期难以统一管理，项目无法开展，导致计划不了了之。</p>\r\n<p>\r\n	<strong>60年属鼠的人16年健康运程：</strong></p>\r\n<p>\r\n	身体状况大致还算不错，多注意内分泌系统的病变，如果发现身体不适，长时间无法规律作息，应及早咨询专业人士。今年天哭照临，要多提防意外、车祸、开刀等血光之灾，切忌喝酒睡眠不足之下开车，同时避免探病或参加丧礼，在阴气汇聚的地方停留会对自身不利。今年身体方面如果没什么大问题，只是心中总是提心吊胆无法平静，可以常去寺庙或教堂祈福，与大师交谈，心神自当可以平静。</p>\r\n<p>\r\n	<strong>1960年属鼠的人2016年运势</strong></p>\r\n<p>\r\n	1960年出生的属鼠朋友，今年运逢官杀运，压力增大，工作方面阻碍较多。去岁的太岁之害逐渐化解，生活方面开始逐步转好。在职的属鼠人，容易受到五鬼影响，出现情绪失控，与人出现矛盾，事业进展不顺。人际关系方面危机重重，需要放下过多的尊严和自负，对于真正的朋友还是要多加在意。感情方面一如既往，不会出现大的变动。</p>\r\n<p>\r\n	<strong>1960年属鼠的人2016年旺运锦囊</strong></p>\r\n<p>\r\n	1960年属鼠人可以佩戴【慈元阁玄龙望尊】玉坠保个人平安顺畅，此玉坠整体为一条腾云驾雾之龙，飞龙在天，有包藏宇宙之机，吞吐天地之志，举首望日，意为冲破阴霾，蓬荜生辉，能保护属鼠人平安度过丙申年。属鼠人还可选用【三合莲花】项链搭配玉坠，更能稳固运势，大放威光，驱邪纳福，发挥更强功效。同时，属鼠人可在家中或办公室摆放【慈元阁玄龙望尊】摆件调和<u>风水</u>，以保家宅运、旺事业运。</p>\r\n\r\n<div class=\"adb adb_3\"></div>\r\n\r\n</div>\r\n<div class=\"pagelist\"></div>','1','0','0','0','2015-08-31 17:36:32','0');");
E_D("replace into `shengxiao_data` values('13','1972年属鼠的人2016年运程','<div class=\"sbody\">\r\n\r\n<div class=\"adb adb_1\"></div>\r\n<div class=''adb adb_2''></div>\r\n<p>\r\n	<strong>1972年<u>属鼠</u>的人2016年运程</strong></p>\r\n<p>\r\n	<strong>1972年属鼠的人是什么命？</strong></p>\r\n<p>\r\n	1972年壬子鼠年出生：山上之鼠，为人身闲心苦，幼年多难，有惊无险，中运克勤克俭，衣食足用，晚年方成安康，不得父母余荫，六亲手足助力少，男招好妻，女命一生贤能，夫妻有扶助，子女不稀，一半助力。春、夏生人衣食有余，秋、冬出世克成家业，日生多忧烦，夜生享乐，六月生人为八败。</p>\r\n<p>\r\n	<strong>72年属鼠的人2016年<u>事业</u>运程：</strong></p>\r\n<p>\r\n	经商的朋友虽难称的上是一本万利，但是比起往年可算是踏实多了，今年你必需时刻警惕自己不再犯下以往的过失，并且提升自己的竞争力，不要原地踏步，你的锋芒将会渐露。</p>\r\n<p>\r\n	上班族更可以趁今年显露你的才华，用心去追求与表现，今年可以容易获得晋升喔！在求职上也将较去年得心应手。金匮将星齐现，在机关单位里面努力的小职员，今年有很多机遇能够更上一层楼。</p>\r\n<p>\r\n	计划独立经营的属鼠人，今年有将星坐镇，利于发挥领导才能，组建坚实团队，开创事业新局面，前期投入较大，甚至入不敷出，可以考虑与人合作，共同分担前期投入。创业计划如果非常好的，可以考虑引进天使投资，但是一定要经过正规法律程序，明确各方责任和职权，以免后期难以统一管理，项目无法开展，导致计划不了了之。</p>\r\n<p>\r\n	<strong>1972年属鼠2016年健康<u>运势</u>：</strong></p>\r\n<p>\r\n	身体状况大致还算不错，多注意内分泌系统的病变，如果发现身体不适，长时间无法规律作息，应及早咨询专业人士。今年天哭照临，要多提防意外、车祸、开刀等血光之灾，切忌喝酒睡眠不足之下开车，同时避免探病或参加丧礼，在阴气汇聚的地方停留会对自身不利。今年身体方面如果没什么大问题，只是心中总是提心吊胆无法平静，可以常去寺庙或教堂祈福，与大师交谈，心神自当可以平静。</p>\r\n<p>\r\n	<strong>1972年属鼠人2016年<u>财运</u>运程：</strong></p>\r\n<p>\r\n	丙申年属鼠人的财运有渐入佳境的迹象，并且很有机会偏财会比正财多，因此可以多注意金融投资行情。除了股票、基金之外，不动产方面也是早买早赚到，可以多考虑一下投资房产，选择市场持续利好的城市，做一些长期的投资。有创业打算的属鼠人，不妨与他人合伙，共同承担风险，有利于减少资金投入，保障财产安全。今年在财运上要小心朋友背信、倒会，或借钱不还，所以辛苦得来的收入可以及早分散投资，留一部分以备急用。千万不要将钱财花在享受、赌博等上面，正所谓花钱如流水，又俗话说十赌九输，耽于享受和沉迷赌博，都可能会给你的财富带来毁灭性的灾难，切记切记。</p>\r\n<p>\r\n	<strong>72年属鼠的人<u>性格</u>特征：</strong></p>\r\n<p>\r\n	表面沉默寡言，实际上从来是我们想象的那么安静。他们性格开朗、快乐，善于交际。很珍惜与亲朋好友的关系，并对之有着深深的依恋。受好结伙、集会、聚会，积极参与并成功地组织。在社交场合，属鼠人总是以自己的聪明机敏使气氛活跃，大家开心。</p>\r\n<p>\r\n	<strong>1972年属鼠的人2016年感情运势：</strong></p>\r\n<p>\r\n	今年不仅时运开始再转好，同时异性缘也特别强，追求浪漫并不难，未婚男女红鸾星动，应多把握时机，已婚的夫妻更可运用今年好好经营婚姻关系，甚至于计划生子，可以有添生贵子的机会。在人际上要必需谨言慎行，避免招惹是非，预防亲友之叛离。今年感情方面的斩获诚可贵，但还是适宜把重心放在事业和人际关系上。</p>\r\n<p>\r\n	<strong>1972年属鼠的人2016年总运程：</strong></p>\r\n<p>\r\n	1972年出生的属鼠朋友，今年运逢偏财运，半合水局，代表思维方面有些散漫，可能会有不切实际的想法，今年生活不能太过随意，需要注意自己的言行举止，不要因为人气和地位的突然提升而迷失了自我，破坏了朋友之间的情谊。继续保持低调和勤奋的表现，能够事业上更进一步。</p>\r\n<p>\r\n	<strong>1972年属鼠的人2016年旺运锦囊</strong></p>\r\n<p>\r\n	1972年属鼠人可以佩戴【慈元阁玄龙望尊】玉坠保个人平安顺畅，此玉坠整体为一条腾云驾雾之龙，飞龙在天，有包藏宇宙之机，吞吐天地之志，举首望日，意为冲破阴霾，蓬荜生辉，能保护属鼠人平安度过丙申年。属鼠人还可选用【三合莲花】项链搭配玉坠，更能稳固运势，大放威光，驱邪纳福，发挥更强功效。同时，属鼠人可在家中或办公室摆放【慈元阁玄龙望尊】摆件调和<u>风水</u>，以保家宅运、旺事业运。若想化煞挡灾，寻得贵人，可以佩戴【慈元阁八方六圣】【紫薇利鼠】手链。开车的朋友可在车内配挂【慈元阁厚利平途】车挂，此车挂可保人车平安，出入如意。属鼠人若想借助吉年时运，在事业官运财运上平步青云、顺风顺水、扭颓为胜还可恭请【慈元阁万方朝福王侯鼎】以大展宏图。</p>\r\n\r\n<div class=\"adb adb_3\"></div>\r\n\r\n</div>\r\n<div class=\"pagelist\"></div>','1','0','0','0','2015-08-31 17:36:32','0');");
E_D("replace into `shengxiao_data` values('14','1984年属鼠的人2016年运程','<div class=\"sbody\">\r\n\r\n<div class=\"adb adb_1\"></div>\r\n<div class=''adb adb_2''></div>\r\n<p>\r\n	<strong>1984年<u>属鼠</u>的人2016年运程</strong></p>\r\n<p>\r\n	<strong>1984年属鼠的人2016年总运程：</strong></p>\r\n<p>\r\n	1984年出生的属鼠朋友，今年偏<u>财运</u>不错，虽然不会有爆发的可能，但是短期内也能有不错的收获。需要警惕来自下属的陷害和妨碍，平时为人方面尽量不要太过严厉，晓之以理动之以情才能建立起最坚实的团队。偏财比正财要好，所以<u>事业</u>之外，不妨多关注一下各种投资渠道，选择最适合自己也最有兴趣的一种仔细研究，能有不错收益。</p>\r\n<p>\r\n	<strong>84年属鼠的人2016年吉运方面：</strong></p>\r\n<p>\r\n	今年有金匮与将星同临，财<u>运势</u>如破竹一路飙升，将星入命，代表官运亨通，从政的属鼠人今年可以大展拳脚，有实现宏图之志的可能。三台星照临，代表有权有势的人能够执掌高位，好交朋友，为人干脆利落，处理事务更近快速稳当，能够得到上级更进一步的提升和欣赏。人际关系拓展迅速，能够巩固现有的人脉资源，事业运长盛不衰，国家机关或者企事业单位负责人，只要好好发挥个人实力，能够在短时间内有极大成就，成为社会瞩目的人。</p>\r\n<p>\r\n	感情运势尚佳，能有比较理想的姻缘出现，单身属鼠人可以借此机会表达内心想法，即便最终只是朋友，也能了却一桩心事，放下负担，重新出发，能够有一番更加美好的际遇。</p>\r\n<p>\r\n	<strong>1984年属鼠的人2016年凶势方面：</strong></p>\r\n<p>\r\n	今年虽然有三大吉星坐镇，财运、官运都非常好，但是过去的一些烂摊子这个时候却可能拖你后腿。</p>\r\n<p>\r\n	五鬼星照临，会有人口不安、疾病、死亡等凶兆，属鼠人在外冲击拼搏太盛，家宅方面却不得安宁，家中老人在这一年易出现急病或者突发意外，甚至血光之灾，属鼠人一定要多做防范，最好能有人一直陪伴老人左右。属鼠人本身如果生机不足，也非常容易遭受灾祸，小到伤风感冒，大到车祸事故，都是要注意的地方。</p>\r\n<p>\r\n	天哭星，主忧伤刑克、丧亡孝服，加会丧门，这一年前进途中容易出现破败挫折，无论是突如其来的成功或者失败，都可能导致情绪失控，进而难以把握为人分寸，容易与人发生争斗，从而招来不必要的麻烦。建议平时多做养气功夫，谨言慎行看，做到胜不骄败不馁。对待他人从一而终，认真维系自己的人脉关系，只要平时多烧香，关键时刻才能临时抱佛脚。</p>\r\n<p>\r\n	<strong>1984年属鼠2016年财运运程：</strong></p>\r\n<p>\r\n	丙申年属鼠人的财运有渐入佳境的迹象，并且很有机会偏财会比正财多，因此可以多注意金融投资行情。除了股票、基金之外，不动产方面也是早买早赚到，可以多考虑一下投资房产，选择市场持续利好的城市，做一些长期的投资。有创业打算的属鼠人，不妨与他人合伙，共同承担风险，有利于减少资金投入，保障财产安全。今年在财运上要小心朋友背信、倒会，或借钱不还，所以辛苦得来的收入可以及早分散投资，留一部分以备急用。千万不要将钱财花在享受、赌博等上面，正所谓花钱如流水，又俗话说十赌九输，耽于享受和沉迷赌博，都可能会给你的财富带来毁灭性的灾难，切记切记。</p>\r\n<p>\r\n	<strong>84年属鼠的人2016年事业运势：</strong></p>\r\n<p>\r\n	工薪职场人士：今年金匮将星共同出现，代表官运昌顺，对体制内的属鼠人非常有利，即便是初入职场的属鼠人士，只要踏实做事，也能够得到领导的赏识，甚至可能在短时间内成为贴身的人员。事业运不错，虽然在前进的过程中还是会出现一些问题，特别是有职场小人从中作梗，这些都是受过去遗留问题的影响，上半年可以采用坚决的方式处理好这些问题，等到下半年则可以大展身手，事业步步高升。</p>\r\n<p>\r\n	投资经商人士：今年偏财要比正财更加有利，所以主投资的属鼠人将会比主经商的属鼠人有更大的收益。投资方面可以多考虑短期与长期相结合，股票、基金和房地产等投资理财产品都可以尝试，需要注意千万不要轻信所谓的专家，一切最好自己了解清楚然后再出手。自主经营的属鼠人今年大多数时间要走流程，会和很多官场的人打交道，注意不要采用行贿的方式，处理好的话能够在未来很长一段时间都顺风顺水。</p>\r\n<p>\r\n	<strong>1984年属鼠的贵人属相：</strong></p>\r\n<p>\r\n	鼠和猴：十二<u>生肖</u>中鼠的贵人，他们的关系良好，但猴会常常利用鼠，吃亏的当然是鼠，但鼠却似乎并不是太在意。用另一种说法解释就是&ldquo;周瑜打黄盖，一个愿打，一个愿挨。&rdquo;别人看着他们是非常有意思的一对朋友。如果两人就。这样继续交往下去，也是一件不错的事情。</p>\r\n<p>\r\n	鼠和龙：他们会成为好朋友，大家都欣赏对方的才干。比如说，在计划做某一件事的时候。双方会不约而同地想到了一起，这种契合程度往往会让事情最终出现非常理想的结果，别人看着也羡慕的要命。</p>\r\n<p>\r\n	鼠与牛：<u>十二生肖</u>中鼠的贵人，他们没有什么要说的话，即使在一起，气氛也会变得很奇怪。原因是牛太沉默，老实忠诚。鼠则显得狡诈，做事往往对不起牛。因此双方不会有什么值得交流的地方，没有多少要说的话也便可以理解了。</p>\r\n<p>\r\n	<strong>1984年属鼠的人2016年健康运势：</strong></p>\r\n<p>\r\n	身体状况大致还算不错，多注意内分泌系统的病变，如果发现身体不适，长时间无法规律作息，应及早咨询专业人士。今年天哭照临，要多提防意外、车祸、开刀等血光之灾，切忌喝酒睡眠不足之下开车，同时避免探病或参加丧礼，在阴气汇聚的地方停留会对自身不利。今年身体方面如果没什么大问题，只是心中总是提心吊胆无法平静，可以常去寺庙或教堂祈福，与大师交谈，心神自当可以平静。</p>\r\n<p>\r\n	<strong>1984年属鼠的人2016年感情运程：</strong></p>\r\n<p>\r\n	单身男女：今年属鼠的单身男女桃花运特别旺，情感方面容易有好的结果出现。属鼠男性在遇到心爱的异性之后，能够一改软弱的<u>性格</u>印象，勇敢追求，用实际才华和真心打动对方，今年容易出现从恋爱到结婚的快速过渡。而没有合适对象的属鼠人建议利用闲暇时间多出去走走，旅途中除了风景，还有比风景更美的人儿，在山水之间结识情投意合的对象，也符合属鼠人的情感追求。</p>\r\n<p>\r\n	已婚人士：已婚的属鼠人丙申年受到桃花运的正面影响，能够和身边的人保持良好和谐的关系，一些存在家庭矛盾也可以趁这个时候好好理一理。同事之间相处融洽，虽然桃花太多也会给属鼠人带来困扰，不过短时间内难以形成大的影响，除非属鼠人本身乐在其中，这种状态才是最危险的。夫妻感情还算和睦，对于结婚五年以上的属鼠人来说，生活可能越发平淡，这个时候不妨抽时间，和家人一起外出旅游，到处看看，世界这么大，感受一下自身的渺小和生命的短暂，可以让你们更加珍惜彼此，有利于感情生活的回暖。</p>\r\n<p>\r\n	<strong>1984年属鼠的人2016年旺运锦囊</strong></p>\r\n<p>\r\n	1984年属鼠人可以佩戴【慈元阁玄龙望尊】玉坠保个人平安顺畅，此玉坠整体为一条腾云驾雾之龙，飞龙在天，有包藏宇宙之机，吞吐天地之志，举首望日，意为冲破阴霾，蓬荜生辉，能保护属鼠人平安度过丙申年。属鼠人还可选用【三合莲花】项链搭配玉坠，更能稳固运势，大放威光，驱邪纳福，发挥更强功效。同时，属鼠人可在家中或办公室摆放【慈元阁玄龙望尊】摆件调和<u>风水</u>，以保家宅运、旺事业运。若想化煞挡灾，寻得贵人，可以佩戴【慈元阁八方六圣】【紫薇利鼠】手链。开车的朋友可在车内配挂【慈元阁厚利平途】车挂，此车挂可保人车平安，出入如意。属鼠人若想借助吉年时运，在事业官运财运上平步青云、顺风顺水、扭颓为胜还可恭请【慈元阁万方朝福王侯鼎】以大展宏图。</p>\r\n\r\n<div class=\"adb adb_3\"></div>\r\n\r\n</div>\r\n<div class=\"pagelist\"></div>','1','0','0','0','2015-08-31 17:36:32','0');");
E_D("replace into `shengxiao_data` values('15','1996年属鼠的人2016年运程','<div class=\"sbody\">\r\n\r\n<div class=\"adb adb_1\"></div>\r\n<div class=''adb adb_2''></div>\r\n<p>\r\n	<strong>1996年<u>属鼠</u>的人2016年运程</strong></p>\r\n<p>\r\n	<strong>1996年属鼠的2016年<u>事业</u>运</strong></p>\r\n<p>\r\n	事业运不错，虽然在前进的过程中还是会出现一些问题，特别是有职场小人从中作梗，这些都是受过去遗留问题的影响，上半年可以采用坚决的方式处理好这些问题，等到下半年则可以大展身手，事业步步高升。上班族可以趁今年显露才华，用心去追求与表现，今年可以容易获得晋升喔！在求职上也将较去年得心应手。创业计划如果非常好的，可以考虑引进天使投资，但是一定要经过正规法律程序，明确各方责任和职权，以免后期难以统一管理，项目无法开展，导致计划不了了之。</p>\r\n<p>\r\n	<strong>96年属鼠的人2016年健康运程</strong></p>\r\n<p>\r\n	身体状况大致还算不错，多注意内分泌系统的病变，如果发现身体不适，长时间无法规律作息，应及早咨询专业人士。今年天哭照临，要多提防意外、车祸、开刀等血光之灾，避免探病或参加丧礼，在阴气汇聚的地方停留会对自身不利。今年身体方面如果没什么大问题，只是心中总是提心吊胆无法平静，平时可以多读一些有关哲学方面的书籍，或者和家人多多沟通，能够对身心规整有所帮助。</p>\r\n<p>\r\n	<strong>96年属鼠2016年感情<u>运势</u></strong></p>\r\n<p>\r\n	今年属鼠的单身男女桃花运特别旺，情感方面容易有好的结果出现。属鼠男性在遇到心爱的异性之后，能够一改软弱的<u>性格</u>印象，勇敢追求，用实际才华和真心打动对方，今年容易出现从恋爱到结婚的快速过渡。而没有合适对象的属鼠人建议利用闲暇时间多出去走走，旅途中除了风景，还有比风景更美的人儿，在山水之间结识情投意合的对象，也符合属鼠人的情感追求。</p>\r\n<p>\r\n	<strong>96年属鼠2016年学业运势</strong></p>\r\n<p>\r\n	属鼠的学生们今年学业运不强，注意力不在学习上，对艺术、体育、娱乐方面的关注要多过学习，所以课余生活比较丰富。建议属鼠学生的家长与孩子做好沟通和辅导，千万不要采取粗暴的方式禁止孩子玩乐，寓教于乐才是最好的学习方式。对于属鼠学生来说，今年课外活动的时候要注意安全，出现皮外伤及时寻求帮助、处理，以免伤口感染。处于叛逆期的属鼠人和家人关系会比较紧张，一方面自己要注意对待家人的态度，另一方面家长最好咨询专业人士这种情况的处理方式，尽量不要给孩子的成长造成负面影响。</p>\r\n<p>\r\n	<strong>1996年属鼠的人性格</strong></p>\r\n<p>\r\n	属鼠优点：渴望求知，做事态度积极，勤奋努力；自尊自爱，能自我约束，能替别人着想，待人和蔼；善于结交各式各样的朋友适应性强，机智、谨慎，善于筹划；性格外向，多情善感，细心，做事有条理。</p>\r\n<p>\r\n	属鼠缺点：目光短浅，一事当先，先为自己打算；多疑、不够坦诚，有些爱虚荣；对直觉 过直觉过份自信。比较性急，牵及到欲望方面的利益，有时不顾情面。</p>\r\n<p>\r\n	属鼠的人，观察事物比较细心。待人接物大方热情，感情十分丰富，对自己所爱的人十分仁慈与宽容。对于属鼠的朋友来说，<u>生肖</u>上彼此相合的好朋友分别是：<u>属牛</u>的、<u>属猴</u>的和<u>属龙</u>的人。</p>\r\n<p>\r\n	属鼠的你细心谨慎，与属牛人在一起，对方的宽容包涵，能够抚慰你那敏感的心。与属猴的在一起，对方的开朗，能引导你们一起去探索人生的乐趣。与属龙的在一起，越发能体现爱情的浪漫，花前月下，留连忘返。</p>\r\n<p>\r\n	<strong>1996年属鼠的人2016年总体运程</strong></p>\r\n<p>\r\n	1996年出生的属鼠朋友，今年运逢比肩，事业方面面临竞争，收入会慢慢增加。流年运势正处于上升趋势，特别是感情方面，能够非常不错的表现。单身的属鼠人今年红鸾星动，容易遇到命中的另一半，好好把握机会，或许初恋就是一辈子。人际关系方面需要多加注意，这个时候变动比较多，很多即将带来的问题都会对自己和朋友产生考验，需要仔细对待。</p>\r\n<p>\r\n	<strong>1996年属鼠的人2016年旺运锦囊</strong></p>\r\n<p>\r\n	1996年属鼠人可以佩戴【慈元阁玄龙望尊】玉坠保个人平安顺畅，此玉坠整体为一条腾云驾雾之龙，飞龙在天，有包藏宇宙之机，吞吐天地之志，举首望日，意为冲破阴霾，蓬荜生辉，能保护属鼠人平安度过丙申年。属鼠人还可选用【三合莲花】项链搭配玉坠，更能稳固运势，大放威光，驱邪纳福，发挥更强功效。若想化煞挡灾，寻得贵人，可以佩戴【慈元阁八方六圣】【紫薇利鼠】手链。</p>\r\n\r\n<div class=\"adb adb_3\"></div>\r\n\r\n</div>\r\n<div class=\"pagelist\"></div>','1','0','0','0','2015-08-31 17:36:32','0');");
E_D("replace into `shengxiao_data` values('16','属鼠人2016年运势_属鼠的人2016年运程','<div class=\"sbody\">\r\n\r\n<div class=\"adb adb_1\"></div>\r\n<div class=''adb adb_2''></div>\r\n<p>\r\n	<u>属鼠</u>人2016年<u>运势</u>_属鼠的人2016年运程</p>\r\n<p>\r\n	从容不迫、渐入佳境</p>\r\n<p>\r\n	出生年份：1936、1948、1960、1972、1984、1996、2008</p>\r\n<p>\r\n	<strong>属鼠人2016年总体运程预测</strong></p>\r\n<p>\r\n	肖鼠的人今年适逢吉星「金匮」、「三台」、「将星」坐镇，对于财禄、官运和权位的提升有相当的帮助，同时又岁逢三合，对于人际的拓展将会有正面的影响。虽说是一个不错的流年，过去的阻碍也会渐渐地化解，今年对于您的<u>事业</u>也有提升的作用，但是过往的两年实在是让你受创颇大，同时今年又有五鬼、天哭、官符的作弄，容易引起「口舌、是非、小人」，所以情绪容易失控而引起争斗，以致招来小人和官非。因此今年反倒较像是一个调养年，并不适合做出巨大的变动，你可以藉由这一年充分地作准备，扩展社交圈，这将对你的生活或是工作奠定良好的助益。另外、今年要小心自己的言行，不要因为一时的人气与权位的窜升而目无他人，导致朋友恶言相向。</p>\r\n<p>\r\n	<strong><u>生肖</u>属鼠2016年吉运预测</strong></p>\r\n<p>\r\n	吉运方面，今年有金匮与将星同临，<u>财运</u>势如破竹一路飙升，将星入命，代表官运亨通，从政的属鼠人今年可以大展拳脚，有实现宏图之志的可能。三台星照临，代表有权有势的人能够执掌高位，好交朋友，为人干脆利落，处理事务更近快速稳当，能够得到上级更进一步的提升和欣赏。人际关系拓展迅速，能够巩固现有的人脉资源，事业运长盛不衰，国家机关或者企事业单位负责人，只要好好发挥个人实力，能够在短时间内有极大成就，成为社会瞩目的人。感情运势尚佳，能有比较理想的姻缘出现，单身属鼠人可以借此机会表达内心想法，即便最终只是朋友，也能了却一桩心事，放下负担，重新出发，能够有一番更加美好的际遇。</p>\r\n<p>\r\n	<strong>生肖属鼠2016年凶势预测</strong></p>\r\n<p>\r\n	凶势方面，今年虽然有三大吉星坐镇，财运、官运都非常好，但是过去的一些烂摊子这个时候却可能拖你后腿。五鬼星照临，会有人口不安、疾病、死亡等凶兆，属鼠人在外冲击拼搏太盛，家宅方面却不得安宁，家中老人在这一年易出现急病或者突发意外，甚至血光之灾，属鼠人一定要多做防范，最好能有人一直陪伴老人左右。属鼠人本身如果生机不足，也非常容易遭受灾祸，小到伤风感冒，大到车祸事故，都是要注意的地方。天哭星，主忧伤刑克、丧亡孝服，加会丧门，这一年前进途中容易出现破败挫折，无论是突如其来的成功或者失败，都可能导致情绪失控，进而难以把握为人分寸，容易与人发生争斗，从而招来不必要的麻烦。建议平时多做养气功夫，谨言慎行看，做到胜不骄败不馁。对待他人从一而终，认真维系自己的人脉关系，只要平时多烧香，关键时刻才能临时抱佛脚。</p>\r\n<p>\r\n	<strong>属鼠人2016年财运运势</strong></p>\r\n<p>\r\n	丙申年属鼠人的财运有渐入佳境的迹象，并且很有机会偏财会比正财多，因此可以多注意金融投资行情。除了股票、基金之外，不动产方面也是早买早赚到，可以多考虑一下投资房产，选择市场持续利好的城市，做一些长期的投资。有创业打算的属鼠人，不妨与他人合伙，共同承担风险，有利于减少资金投入，保障财产安全。今年在财运上要小心朋友背信、倒会，或借钱不还，所以辛苦得来的收入可以及早分散投资，留一部分以备急用。千万不要将钱财花在享受、赌博等上面，正所谓花钱如流水，又俗话说十赌九输，耽于享受和沉迷赌博，都可能会给你的财富带来毁灭性的灾难，切记切记。</p>\r\n<p>\r\n	<strong>属鼠人2016年事业运程解析</strong></p>\r\n<p>\r\n	经商的朋友虽难称的上是一本万利，但是比起往年可算是踏实多了，今年你必需时刻警惕自己不再犯下以往的过失，并且提升自己的竞争力，不要原地踏步，你的锋芒将会渐露。上班族更可以趁今年显露你的才华，用心去追求与表现，今年可以容易获得晋升喔！在求职上也将较去年得心应手。金匮将星齐现，在机关单位里面努力的小职员，今年有很多机遇能够更上一层楼。计划独立经营的属鼠人，今年有将星坐镇，利于发挥领导才能，组建坚实团队，开创事业新局面，前期投入较大，甚至入不敷出，可以考虑与人合作，共同分担前期投入。创业计划如果非常好的，可以考虑引进天使投资，但是一定要经过正规法律程序，明确各方责任和职权，以免后期难以统一管理，项目无法开展，导致计划不了了之。</p>\r\n<p>\r\n	<strong>属鼠的人2016年健康运势</strong></p>\r\n<p>\r\n	身体状况大致还算不错，多注意内分泌系统的病变，如果发现身体不适，长时间无法规律作息，应及早咨询专业人士。今年天哭照临，要多提防意外、车祸、开刀等血光之灾，切忌喝酒睡眠不足之下开车，同时避免探病或参加丧礼，在阴气汇聚的地方停留会对自身不利。今年身体方面如果没什么大问题，只是心中总是提心吊胆无法平静，可以常去寺庙或教堂祈福，与大师交谈，心神自当可以平静。</p>\r\n<p>\r\n	<strong>属鼠的人2016年猴年感情运</strong></p>\r\n<p>\r\n	今年不仅时运开始再转好，同时异性缘也特别强，追求浪漫并不难，未婚男女红鸾星动，应多把握时机，已婚的夫妻更可运用今年好好经营婚姻关系，甚至于计划生子，可以有添生贵子的机会。在人际上要必需谨言慎行，避免招惹是非，预防亲友之叛离。今年感情方面的斩获诚可贵，但还是适宜把重心放在事业和人际关系上。</p>\r\n<p>\r\n	<strong>属鼠人2016年旺运锦囊</strong></p>\r\n<p>\r\n	属鼠人今年运势吉凶四六开，要远离是非，工作中不参与到人事的变动与争斗中，由于这一年火势盛旺，要多注意肝、脾的保护。感情方面可能缺乏吉星拱照。有缘人可为自己、家人或友人请购慈元阁属鼠开光吉祥物，以保丙申年平安度过。属鼠人可以佩戴【慈元阁玄龙望尊】玉坠保个人平安顺畅，此玉坠整体为一条腾云驾雾之龙，飞龙在天，有包藏宇宙之机，吞吐天地之志，举首望日，意为冲破阴霾，蓬荜生辉，能保护属鼠人平安度过丙申年。属鼠人还可选用【三合莲花】项链搭配玉坠，更能稳固运势，大放威光，驱邪纳福，发挥更强功效。同时，属鼠人可在家中或办公室摆放【慈元阁玄龙望尊】摆件调和<u>风水</u>，以保家宅运、旺事业运。若想化煞挡灾，寻得贵人，可以佩戴【慈元阁八方六圣】【紫薇利鼠】手链。开车的朋友可在车内配挂【慈元阁厚利平途】车挂，此车挂可保人车平安，出入如意。属鼠人若想借助吉年时运，在事业官运财运上平步青云、顺风顺水、扭颓为胜还可恭请【慈元阁万方朝福王侯鼎】以大展宏图。以上吉祥物由&ldquo;慈元阁&rdquo;独家设计开光发售，请认准生肖<u>开运</u>领导品牌&ldquo;慈元阁&rdquo;，谨防盗版假冒！使用盗版吉祥物，非但不能开运，反而会沾染盗版者之业障，影响猴年运势，请缘主明辨切勿贪图小便宜！</p>\r\n<p>\r\n	单身男女：今年属鼠的单身男女桃花运特别旺，情感方面容易有好的结果出现。属鼠男性在遇到心爱的异性之后，能够一改软弱的<u>性格</u>印象，勇敢追求，用实际才华和真心打动对方，今年容易出现从恋爱到结婚的快速过渡。而没有合适对象的属鼠人建议利用闲暇时间多出去走走，旅途中除了风景，还有比风景更美的人儿，在山水之间结识情投意合的对象，也符合属鼠人的情感追求。单身属鼠人若想化煞挡灾，寻得贵人，可佩戴【慈元阁八方六圣】【紫薇利鼠】手链。</p>\r\n<p>\r\n	已婚人士：已婚的属鼠人丙申年受到桃花运的正面影响，能够和身边的人保持良好和谐的关系，一些存在家庭矛盾也可以趁这个时候好好理一理。同事之间相处融洽，虽然桃花太多也会给属鼠人带来困扰，不过短时间内难以形成大的影响，除非属鼠人本身乐在其中，这种状态才是最危险的。夫妻感情还算和睦，对于结婚五年以上的属鼠人来说，生活可能越发平淡，这个时候不妨抽时间，和家人一起外出旅游，到处看看，世界这么大，感受一下自身的渺小和生命的短暂，可以让你们更加珍惜彼此，有利于感情生活的回暖。已婚的属鼠人可以佩戴【慈元阁玄龙望尊】玉坠保个人平安顺畅。同时，属鼠人可在家中或办公室摆放【慈元阁玄龙望尊】摆件调和风水，以保家宅运、旺事业运。</p>\r\n<p>\r\n	工薪职场人士：今年金匮将星共同出现，代表官运昌顺，对体制内的属鼠人非常有利，即便是初入职场的属鼠人士，只要踏实做事，也能够得到领导的赏识，甚至可能在短时间内成为贴身的人员。事业运不错，虽然在前进的过程中还是会出现一些问题，特别是有职场小人从中作梗，这些都是受过去遗留问题的影响，上半年可以采用坚决的方式处理好这些问题，等到下半年则可以大展身手，事业步步高升。工薪职场的属鼠人可在家中或办公室摆放【慈元阁玄龙望尊】摆件调和风水，以保家宅运、旺事业运。</p>\r\n<p>\r\n	投资经商人士：今年偏财要比正财更加有利，所以主投资的属鼠人将会比主经商的属鼠人有更大的收益。投资方面可以多考虑短期与长期相结合，股票、基金和房地产等投资理财产品都可以尝试，需要注意千万不要轻信所谓的专家，一切最好自己了解清楚然后再出手。自主经营的属鼠人今年大多数时间要走流程，会和很多官场的人打交道，注意不要采用行贿的方式，处理好的话能够在未来很长一段时间都顺风顺水。经商的属鼠人若想事业官运财运上平步青云、顺风顺水、扭颓为胜还可恭请【慈元阁万方朝福王侯鼎】以大展宏图。开车的朋友可在车内配挂【慈元阁厚利平途】车挂，此车挂可保人车平安，出入如意。</p>\r\n<p>\r\n	学生一族：属鼠的学生们今年学业运不强，注意力不在学习上，对艺术、体育、娱乐方面的关注要多过学习，所以课余生活比较丰富。建议属鼠学生的家长与孩子做好沟通和辅导，千万不要采取粗暴的方式禁止孩子玩乐，寓教于乐才是最好的学习方式。对于属鼠学生来说，今年课外活动的时候要注意安全，出现皮外伤及时寻求帮助、处理，以免伤口感染。处于叛逆期的属鼠人和家人关系会比较紧张，一方面自己要注意对待家人的态度，另一方面家长最好咨询专业人士这种情况的处理方式，尽量不要给孩子的成长造成负面影响。属鼠的同学可以佩戴【慈元阁玄龙望尊】玉坠保个人平安顺畅。</p>\r\n<p>\r\n	<strong>各年份出生的属鼠人2016年运势</strong></p>\r\n<p>\r\n	<strong>1948年属鼠人2016年运势</strong></p>\r\n<p>\r\n	1948年出生的属鼠朋友，今年流年运逢印枭运，事业方面有贵人支持，能产生很大的助力，名声上也会产生较广泛的有益影响。今年有吉星坐镇，家庭方面气氛和谐，经济条件良好的<u>属蛇</u>人今年不仅能够有不错的健康状况，而且能够享受天伦之乐，平安度过一整年。健康方面还是需要多加注意，尽量不要吸烟、饮酒，谨防肺部、肝部疾病，定期去医院检查是个不错的习惯。1948年属鼠人可以佩戴【慈元阁玄龙望尊】玉坠保个人平安顺畅，此玉坠整体为一条腾云驾雾之龙，飞龙在天，有包藏宇宙之机，吞吐天地之志，举首望日，意为冲破阴霾，蓬荜生辉，能保护属鼠人平安度过丙申年。属鼠人还可选用【三合莲花】项链搭配玉坠，更能稳固运势，大放威光，驱邪纳福，发挥更强功效。同时，属鼠人可在家中或办公室摆放【慈元阁玄龙望尊】摆件调和风水，以保家宅运、旺事业运。</p>\r\n<p>\r\n	<strong>1960年属鼠2016年运程</strong></p>\r\n<p>\r\n	1960年出生的属鼠朋友，今年运逢官杀运，压力增大，工作方面阻碍较多。去岁的太岁之害逐渐化解，生活方面开始逐步转好。在职的属鼠人，容易受到五鬼影响，出现情绪失控，与人出现矛盾，事业进展不顺。人际关系方面危机重重，需要放下过多的尊严和自负，对于真正的朋友还是要多加在意。感情方面一如既往，不会出现大的变动。1960年属鼠人可以佩戴【慈元阁玄龙望尊】玉坠保个人平安顺畅，此玉坠整体为一条腾云驾雾之龙，飞龙在天，有包藏宇宙之机，吞吐天地之志，举首望日，意为冲破阴霾，蓬荜生辉，能保护属鼠人平安度过丙申年。属鼠人还可选用【三合莲花】项链搭配玉坠，更能稳固运势，大放威光，驱邪纳福，发挥更强功效。同时，属鼠人可在家中或办公室摆放【慈元阁玄龙望尊】摆件调和风水，以保家宅运、旺事业运。</p>\r\n<p>\r\n	<strong>1972年生属鼠人2016年运势</strong></p>\r\n<p>\r\n	1972年出生的属鼠朋友，今年运逢偏财运，半合水局，代表思维方面有些散漫，可能会有不切实际的想法，今年生活不能太过随意，需要注意自己的言行举止，不要因为人气和地位的突然提升而迷失了自我，破坏了朋友之间的情谊。继续保持低调和勤奋的表现，能够事业上更进一步。1972年属鼠人可以佩戴【慈元阁玄龙望尊】玉坠保个人平安顺畅，此玉坠整体为一条腾云驾雾之龙，飞龙在天，有包藏宇宙之机，吞吐天地之志，举首望日，意为冲破阴霾，蓬荜生辉，能保护属鼠人平安度过丙申年。属鼠人还可选用【三合莲花】项链搭配玉坠，更能稳固运势，大放威光，驱邪纳福，发挥更强功效。同时，属鼠人可在家中或办公室摆放【慈元阁玄龙望尊】摆件调和风水，以保家宅运、旺事业运。若想化煞挡灾，寻得贵人，可以佩戴【慈元阁八方六圣】【紫薇利鼠】手链。开车的朋友可在车内配挂【慈元阁厚利平途】车挂，此车挂可保人车平安，出入如意。属鼠人若想借助吉年时运，在事业官运财运上平步青云、顺风顺水、扭颓为胜还可恭请【慈元阁万方朝福王侯鼎】以大展宏图。</p>\r\n<p>\r\n	<strong>84年属鼠的人2016年运势</strong></p>\r\n<p>\r\n	1984年出生的属鼠朋友，今年偏财运不错，虽然不会有爆发的可能，但是短期内也能有不错的收获。需要警惕来自下属的陷害和妨碍，平时为人方面尽量不要太过严厉，晓之以理动之以情才能建立起最坚实的团队。偏财比正财要好，所以事业之外，不妨多关注一下各种投资渠道，选择最适合自己也最有兴趣的一种仔细研究，能有不错收益。1984年属鼠人可以佩戴【慈元阁玄龙望尊】玉坠保个人平安顺畅，此玉坠整体为一条腾云驾雾之龙，飞龙在天，有包藏宇宙之机，吞吐天地之志，举首望日，意为冲破阴霾，蓬荜生辉，能保护属鼠人平安度过丙申年。属鼠人还可选用【三合莲花】项链搭配玉坠，更能稳固运势，大放威光，驱邪纳福，发挥更强功效。同时，属鼠人可在家中或办公室摆放【慈元阁玄龙望尊】摆件调和风水，以保家宅运、旺事业运。若想化煞挡灾，寻得贵人，可以佩戴【慈元阁八方六圣】【紫薇利鼠】手链。开车的朋友可在车内配挂【慈元阁厚利平途】车挂，此车挂可保人车平安，出入如意。属鼠人若想借助吉年时运，在事业官运财运上平步青云、顺风顺水、扭颓为胜还可恭请【慈元阁万方朝福王侯鼎】以大展宏图。</p>\r\n<p>\r\n	<strong>1996年属鼠的人2016年运程</strong></p>\r\n<p>\r\n	1996年出生的属鼠朋友，今年运逢比肩，事业方面面临竞争，收入会慢慢增加。流年运势正处于上升趋势，特别是感情方面，能够非常不错的表现。单身的属鼠人今年红鸾星动，容易遇到命中的另一半，好好把握机会，或许初恋就是一辈子。人际关系方面需要多加注意，这个时候变动比较多，很多即将带来的问题都会对自己和朋友产生考验，需要仔细对待。1996年属鼠人可以佩戴【慈元阁玄龙望尊】玉坠保个人平安顺畅，此玉坠整体为一条腾云驾雾之龙，飞龙在天，有包藏宇宙之机，吞吐天地之志，举首望日，意为冲破阴霾，蓬荜生辉，能保护属鼠人平安度过丙申年。属鼠人还可选用【三合莲花】项链搭配玉坠，更能稳固运势，大放威光，驱邪纳福，发挥更强功效。若想化煞挡灾，寻得贵人，可以佩戴【慈元阁八方六圣】【紫薇利鼠】手链。</p>\r\n<p>\r\n	<strong>2008年属鼠人2016年运势</strong></p>\r\n<p>\r\n	2008年出生的小朋友，今年运逢印枭运，尤其会得到父母的疼爱。还在上小学，正处于人生阶段中最美的童年时期，家人可能忙于工作对自己的关爱不是很多，不过和朋友一起玩耍也能帮助属鼠小朋友建立比较不错的心里状态，培养一个乐观积极的人生态度。健康方面需要注意意外受伤，很容易被一些障碍物伤到。1996年属鼠人可以佩戴【慈元阁玄龙望尊】玉坠保个人平安顺畅，此玉坠整体为一条腾云驾雾之龙，飞龙在天，有包藏宇宙之机，吞吐天地之志，举首望日，意为冲破阴霾，蓬荜生辉，能保护属鼠人平安度过丙申年。属鼠人还可选用【三合莲花】项链搭配玉坠，更能稳固运势，大放威光，驱邪纳福，发挥更强功效。若想化煞挡灾，寻得贵人，可以佩戴【慈元阁八方六圣】【紫薇利鼠】手链。</p>\r\n\r\n<div class=\"adb adb_3\"></div>\r\n\r\n</div>\r\n<div class=\"pagelist\"></div>','1','0','0','0','2015-08-31 17:36:32','0');");
E_D("replace into `shengxiao_data` values('17','关于12生肖鼠的来历','<div class=\"sbody\">\r\n\r\n<div class=\"adb adb_1\"></div>\r\n<div class=''adb adb_2''></div>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp; 【导语】天下动物很多，古人为何选择了这十二种动物为属相？十二<u>生肖</u>源于何时，今已难于细考。长期以来，不少人将《论衡》视为最早记载<u>十二生肖</u>的文献。十二生肖的产生，有着天文学的背景。在原始时代，先民们体验着寒暑交替的循环往复。一起看看<u>生肖鼠</u>的由来。</p>\r\n<p>\r\n	<br />\r\n	&nbsp;&nbsp;&nbsp; 生肖鼠在十二生肖属于首位，与十二地支中配属，&ldquo;子&rdquo;，故一天十二时辰中之&ldquo;子时&rdquo;&mdash;&mdash;晚上十至凌晨一点，又称&ldquo;鼠时&rdquo;。</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp; 据说，在很早很早以前，生肖这件事归四大天王掌管，当年执事的天王姓什么，那年出生的人就属什么。后来，四大天王回归天宫镇守南天门后，玉皇大帝就决定挑十二动物代表人间生肖，并赐封为神，为体现公平原则，玉帝下令，在规定的日子，人间的动物都可以到天宫应选　，并且以动物们赶到天宫的先后作为排名次的顺序，只取最先赶到的前十二名。</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp; 那时鼠和猫是一对好朋友，他们约定一同到天宫去争取当生肖，并约定到时老鼠来叫猫一同出发。可是，机灵的小老鼠想，人间的许多动物都比自己漂亮，并且还对人类有用，比如，猫念经、兔拜月、狗守屋、龙蛇治水、猴镇山、牛马耕田、猪羊供人食用，自己应当想个法子才能争取到属相。</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp; 于是，在规定到天宫的那天一早，小老鼠悄悄地起来，也没去叫猫，自个儿偷偷跳上老牛的角中藏起来，韧性最好、最勤奋的牛带自己去上天宫去，老牛果然第一个赶到天宫大门。天亮了，四大天王刚打开大宫门，牛还没有来得及抬蹄，小老鼠从牛角中一跃而下，直奔天宫大殿。尽管玉帝不愿意封这个小小的老鼠为生肖。可是群王口中无戏言，自己定下的规定不能更改，玉帝也只好宣布鼠为生肖之首。</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp; 猫在家等了半天，不见鼠的影子，只好自己赶去天宫，由于等待老鼠耽误了时间，等猫赶到天宫时，十二生肖的名额刂已排满，没有猫的份儿了。从此，猫恨透了老鼠，一见到就扑去咬，以泄心中这愤，而老鼠呢，也觉得有些不起猫朋友，见到猫就逃。直到今天猫和老鼠的还是殆对头，人们也觉得老鼠的这生肖之首来得不怎么光明正大，也对老鼠失去了好感，其他动物也疏远了它。</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp; 于是，便离开了人和别的家禽家畜下建立了自己的家园，过着独立自主的生活。直至今天，它们子孙也还是生活在地下。这一来反倒免去了和其他动物的纠缠、争斗，所以鼠的家族始终昌顺、繁衍不息。不管怎么说，老鼠毕竟是凭自己的机警和聪明坐上了生肖的第一把交椅。（完）</p>\r\n\r\n<div class=\"adb adb_3\"></div>\r\n\r\n</div>\r\n<div class=\"pagelist\"></div>','1','0','0','0','2015-08-31 17:36:32','0');");
E_D("replace into `shengxiao_data` values('18','属鼠的人2015年运程','<div class=\"sbody\">\r\n\r\n<div class=\"adb adb_1\"></div>\r\n<div class=''adb adb_2''></div>\r\n<strong><u>属鼠</u>的人2015年运程_2015属鼠人全年<u>运势</u></strong><br />\r\n&nbsp;<br />\r\n出生年份： 2008&nbsp; 1996&nbsp; 1984&nbsp; 1972&nbsp;&nbsp; 1960&nbsp; 1948<br />\r\n&nbsp;<br />\r\n2015年乙未年，对于鼠年生人来讲，子未相害，又逢【太阴】化忌，此年各项运程都要大打折扣，不仅会感觉到前所未有的压力，对于多项事情的态度也不像以往那样的积极的面对，更多的是消极的等待和埋怨，因此务必要调整好心态，迎接诸事不顺的挑战。<u>事业</u>上容易受到小人排挤，平日认为顺利成章的事情很容易产生节外生枝，需做好应对一切变化的准备。【太阴】为财星，一旦化忌<u>财运</u>很不理想，破财迹象明显，而且有些需要投资的项目表面看起来赚钱的机会很大，但是实际操作陷阱不少，切记谨慎投资，也不可与人合伙，容易遭到合伙人的算计，此年建议还是平稳过渡为上。此年容易路与桃花，但大多数都是过眼云烟，成功的机会不大，如果有心仪的对象，切记不可贸然表白，最好等到运势比较好的月份再发起攻击，已婚的朋友们要注意多跟配偶沟通，而且言语上要以关心为主，不可过于较真容易产生口角，控制情绪。平时多做体育锻炼，此年属鼠人子未相害，若三方位逢【大耗】【小耗】【天邢】等煞耀会照，则对脾胃的伤害较大，应注意内脏的养护。<br />\r\n&nbsp;<br />\r\n<strong>属鼠的人事业运：</strong><br />\r\n&nbsp;<br />\r\n此年对于属鼠的人来说年干乙【天机】化禄，整年充满了不确定的因素，动星当头，所有的事情都趋于不稳定的结果，因此对于工作中应该时刻保持处理应变的心态，而且很容易产生工作地点或者岗位上的调动，由于受到子未相害的影响，事业上的变动往往是向着复杂的不容易解决的方向变动，而且办事的阻碍要比以往大很多，因此建议保持一颗平常心，切记心浮气躁。<br />\r\n&nbsp;<br />\r\n<strong>属鼠的人财运：</strong><br />\r\n&nbsp;<br />\r\n属鼠人在乙未年财运相对较差，给人的感觉是处处都会让你破财，所以平时就应当注意节俭，以备不时之需。有投资的想法的朋友需谨慎用钱，在羊年容易产生节外生枝，会增加开销，很难守住财，而且如果是合伙的生意容易受到朋友的欺骗，即使已经投资的项目，在往来账目上都要清晰，总之此年风险较大，需理性对待。<br />\r\n<strong>&nbsp;<br />\r\n属鼠的人健康运：</strong><br />\r\n&nbsp;<br />\r\n乙未年【太阴】化忌，因此【太阴】所代表的的人体的器官都要小心的养护，肾脏，泌尿系统，尤其是女性妇科，切记不能掉以轻心，如果在此年犯病，很不容易治愈。其次丑未相害，对于脾胃伤害也是很大，建议平时多锻炼，女性也可多吃滋补养颜的食品，少生气少上火，若肝火过旺则容易克制未土，因此每天保持一个好的心情最为重要。<br />\r\n&nbsp;<br />\r\n<strong>属鼠的人爱情运：</strong><br />\r\n&nbsp;<br />\r\n属鼠人在乙未年要谨防桃花煞和桃花劫的袭来，此年桃花运尚可，但是成功率不是很高，而且容易伤财，要谨慎交友，不要被眼前的现象欺骗，尤其是女性朋友，过多的桃花不但不会增加婚运，反而让你麻烦事更多，甚至引起不必要的误会与纷争。男性朋友在工作中也会遇到心仪的女性，如果想表白最好是在运势好的月份。<br />\r\n&nbsp;<br />\r\n<strong>1948年戊子年：</strong><br />\r\n&nbsp;<br />\r\n戊子年生年天干四化【贪狼】化禄、【太阴】化权、【右弼】化科、【天机】化忌，在乙未年要控制好自己的情绪，此年很容易急躁，尤其是对子女，情绪上一定要注意，外出的时候要留意脚下，避免磕碰，【天机】化忌对于老人的影响还是比较大的，出门的时候最好少坐车多不行，减少发生意外的机会。注意神经系统、大脑方面的疾病发生。<br />\r\n&nbsp;<br />\r\n<strong>1960年庚子年：</strong><br />\r\n&nbsp;<br />\r\n庚子年生年天干四化【太阳】化禄、【武曲】化权、【太阴】化科、【天同】化忌，在乙未年男性的运程要稍微好于女性，事业上要稳扎稳打，不可急于求成，有些存在风险的事情最好在羊年不要沾染，正财运稍好，不要过分的苛求偏财运，会适得其反。投资理财方面要步步谨慎，最好亲力亲为。此年容易跟亲戚朋友的关系紧张，平时有条件的建议多搞聚会，培养感情。身体方面注意消化系统和泌尿系统方面的保养。<br />\r\n<strong>&nbsp;<br />\r\n1972年壬子年：</strong><br />\r\n&nbsp;<br />\r\n壬子年生年天干四化【天梁】化禄、【紫薇】化权、【左辅】化科、【武曲】化忌，在乙未年凡事不可要求的太过完美，切记不要较真，容易出现得不偿失的情况。工作中要注意跟上下级的关系，不能急于求成。由于正财星化忌，工作中难免出现疏漏，尤其是财务工作人员，一定要账目清晰，否则容易吃亏。不要轻信一些投机取巧的赚钱方式，需一步一个脚印。所有的事情都要落实到纸面，切勿妄加臆断理所当然。<br />\r\n&nbsp;<br />\r\n<strong>1984年甲子年：<br />\r\n&nbsp;</strong><br />\r\n甲子年生年天干四化【廉贞】化禄、【破军】化权、【武曲】化科、【太阳】化忌，在乙未年会有一种心高气傲的情绪，着急做出一番事业但是却心有余力不足，实际操作过程中事情会出现很多分歧点，一定要静下心来求助比自己年长的人，因为此年【紫薇】化科，你的上级领导或者年纪比你大的人往往就是你的贵人。求财的时候也要多多考虑，要有长远的目光，不可因小失大。感情上子未相害，最好不要在羊年结婚，感情上也会磕磕绊绊拖拖拉拉的，做好心理准备。<br />\r\n<strong>&nbsp;<br />\r\n1996年丙子年：</strong><br />\r\n&nbsp;<br />\r\n丙子年生年天干四化【天同】化禄、【天机】化权、【文昌】化科、【廉贞】化忌，在乙未年工作起来会比较辛苦，经常要变换地点的出差。还在上学的孩子要住此年用功学习，会有很好地收获，成绩名次会有显著提高。父母要注意为孩子减压，此年会因为功课紧张的缘故而压力过大，身体上少吃油腻辛辣的食物，运动的时候注意做好保护措施，谨防血光。<br />\r\n&nbsp;<br />\r\n<strong>2008年戊子年：</strong><br />\r\n&nbsp;<br />\r\n丙子年生年天干四化【贪狼】化禄、【太阴】化权、【右弼】化科、【天机】化忌，在乙未年孩子需要家长大量的时间陪同，此年容易上火，情绪急躁，而且外出的时候要时刻看护好孩子，注意来往车辆。孩子的心情起伏较大，父母应有足够的耐心，多沟通多鼓励，给予孩子足够的信心。<br />\r\n<strong>&nbsp;<br />\r\n属鼠流月运势预测：</strong><br />\r\n&nbsp;<br />\r\n<strong>农历正月&nbsp;&nbsp;戊寅月（阳历2015年2月19日-- 2015年3月19日）</strong><br />\r\n&nbsp;<br />\r\n<u>本月</u>运势走向不尽人意，想要开创事业的朋友要小心了，几乎任何事情都不能完成，尤其是跟人交往，或者是找上级部门批复等一类的事物，最好不要在此月办理，容易四处碰壁以致信心受创。财运方面不要有过多的期待，前一年有尚未完结的账务，本月也很难解决，不能报太大的希望。而且已婚人士容易跟配偶产生口角，需多理解配偶，退一步海阔天空。<br />\r\n&nbsp;<br />\r\n<strong>农历二月&nbsp;&nbsp;己卯月（阳历2015年3月20日-- 2015年4月18日）</strong><br />\r\n&nbsp;<br />\r\n本月各项运程较上个月有所好转，但是迹象不是特别明显，在行运的过程中要注意身边的能够帮助到你的男性朋友，或许贵人就在其中，工作中要注意少说话多做事，以免被小人抓到机会，尤其是跟你同级别的同事，不可对其多言。有投资意向的朋友本月可以尝试着进入项目中去，但是也要有一颗谨慎对待的心，以便发生意外足以应对。健康方面不可暴饮暴食，也最好不要饮酒，因为本月消化系统容易出现问题。<br />\r\n&nbsp;<br />\r\n<strong>农历三月&nbsp;&nbsp;庚辰月（阳历2015年4月19日-- 2015年5月17日）<br />\r\n&nbsp;</strong><br />\r\n本月可以说是属鼠人在羊年相对运势较好的月份之一，之前积压的相对棘手的问题此月可以着手处理，可能会有意想不到的惊喜，工作过程中跟同事和上下级关系也相对得心应手，因此若事业上有某些想法的朋友可在本月趁热打铁，更进一步。财运方面破财的地方较多，注意开销。本月桃花运也不错，有心仪对象的朋友可大胆表白，但是要选择的流日一定要躲过忌冲，可提高成功率。身体上注意心血管疾病的发生，老年人尤其注意。<br />\r\n&nbsp;<br />\r\n<strong>农历四月&nbsp;&nbsp;辛巳月（阳历2015年5月18日-- 2015年6月15日）</strong><br />\r\n&nbsp;<br />\r\n本月运势较上个月稍微平和，财运较好，有想投资理财的朋友可继续上月的<u>好运</u>，但是偏财运较差，切记博彩，事业上多留意领导对你的指点，本月贵人运好一些，或许能够得到被提升的机会，注意把握。本月要注意控制情绪，不论是朋友同事之间，还是配偶家人，都要注意用词，不可火气过大，注意各方面关系的协调，健康上多吃清淡少吃辛辣，注意养护肝脏。<br />\r\n&nbsp;<br />\r\n<strong>农历五月&nbsp;&nbsp;壬午月（阳历2015年6月16日-- 2015年7月15日）</strong><br />\r\n&nbsp;<br />\r\n属鼠人在此月事业上容易产生跟同事之间的冲突，一定要谨慎对待，注意谈话间的用词适当，本月容易出现资金短缺的现象，平时注意开销节俭，而且工作过程中经常会遇到意想不到的节外生枝，需要对事情进行全方位的估计方可进行。桃花运不是很理想，想要表白的朋友尽量克制，躲过此月。身体方面注意骨骼、肌肉、牙齿的保护。<br />\r\n<strong>&nbsp;<br />\r\n农历六月&nbsp;&nbsp;癸未月（阳历2015年7月16日-- 2015年8月13日）<br />\r\n&nbsp;</strong><br />\r\n本月依旧延续上个月运程不佳的情况，工作中不可急于求成，要多跟领导沟通领会领导意图，不可擅作主张，而且此月月干【贪狼】化忌，很容易被同事或者客户请到酒色娱乐场所，要注意把持，此月跟配偶及家人的关系相对融洽，只是朋友之间不可说的过多，言多必失，容易产生误会。身体方面注意肠胃、肾脏的保养。<br />\r\n&nbsp;<br />\r\n<strong>农历七月&nbsp;&nbsp;甲申月（阳历2015年8月14日-- 2015年9月12日）</strong><br />\r\n&nbsp;<br />\r\n本月较上个月运程有所好转，工作上紧张的情绪有所缓解，日常事务也变得得心应手，跟同事的关系也逐渐融洽，资金方面一切正常，是个少有的偏财运好的月份，喜欢博彩的朋友本月可以少量投资，或许就有意想不到的回报。偏财运好的月份桃花运都不会差，因此还单身的朋友可以多到交友广泛的场所活动，可能会有情定终身的缘分出现。<br />\r\n<strong>&nbsp;<br />\r\n农历八月&nbsp;&nbsp;乙酉月（阳历2015年9月13日-- 2015年10月12日）<br />\r\n&nbsp;</strong><br />\r\n此月注意言多必失祸从口出，日常交流要注意用词适当，平时不要再你以为安全的时候嚼舌根，隔墙有耳的事情时有发生，也容易受到小人暗算，本月各项运程都充满很大的不确定因素，因此需要小心谨慎的度过，正财和偏财都不够理想，还是稳扎稳打、按部就班的处理事情，切记心浮气躁，容易惹祸上身。桃花运多为烂桃花，尽量远离。身体方面注意呼吸道、气管的健康防护。<br />\r\n&nbsp;<br />\r\n<strong>农历九月&nbsp;&nbsp;丙戌月（阳历2015年10月13日-- 2015年11月11日）</strong><br />\r\n&nbsp;<br />\r\n本月事业上压力较大，领导分配的工作会比以往增加，而且难度也较大，因此工作中容易产生抵触情绪，需调整好心态，做好心理准备。财运方面也不理想，有投资经营意向的朋友，建议不要在本月开创事业，利润不是很可观。感情上磕磕碰碰也比较多，夫妻间容易因为小时而拌嘴，希望多理解对方。身体上无大碍，保持好的心情即可。<br />\r\n&nbsp;<br />\r\n<strong>农历十月&nbsp;&nbsp;丁亥月（阳历2015年11月12日-- 2015年12月10日）<br />\r\n&nbsp;</strong><br />\r\n本月运程比较平缓，如果不急不躁事情都能如期的解决，但是也不可掉以轻心，毕竟流月的运程都是在流年管辖范围内，流年相害流月即使运程好，也要比正常大打折扣，本月会偶尔会出现办事异常顺利而且财运异常好的情况，也可能出现突然间时运不济的情况，大起大落之势要引起足够的重视。身体上注意不可暴饮暴食，少吃油腻，本月容易伤肝。<br />\r\n&nbsp;<br />\r\n<strong>农历十一月&nbsp;&nbsp;戊子月（阳历2015年12月11日-- 2016年1月9日）</strong><br />\r\n&nbsp;<br />\r\n本月总体运程较差，一切都要小心应对，尤其是资金账务方面，容易引起纠纷，甚至需要司法程序来解决，而且跟客户等社会各方面的关系处理的都不够理想，影响升迁和赚钱。本月破财迹象明显，建议一切从简，大部分破财来自于家人。身体状况会明显感觉疲劳，平时多休息，少出去酒色场所。<br />\r\n&nbsp;<br />\r\n<strong>农历十二月&nbsp;&nbsp;己丑月（阳历2016年1月10日-- 2016年2月7日）</strong><br />\r\n&nbsp;<br />\r\n本月工作中比上个月较为轻松，跟同事上级关系处理相对缓和，而且事情办理的相对顺利一些，财运方面正财运较好偏财运较差，想玩博彩的朋友本月建议不要投资过大，而且本月要注意外出时的人身安全，容易有意外情况发生，比如车祸之类。身体方面容易伤风感冒之类，注意肺部气管呼吸道的疾病。<br />\r\n\r\n<div class=\"adb adb_3\"></div>\r\n\r\n</div>\r\n<div class=\"pagelist\"></div>','1','0','0','0','2015-08-31 17:36:32','0');");
E_D("replace into `shengxiao_data` values('19','属鼠人2015年运势','<div class=\"sbody\">\r\n\r\n<div class=\"adb adb_1\"></div>\r\n<div class=''adb adb_2''></div>\r\n&nbsp; &nbsp; 【导语】生活不管是过去还是现在，从来都不为人停留，而未来也经常在你毫无准备的时候从你身边走过，要想把握现在和未来，就需要时刻准备着。<br />\r\n&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; 或许2015年还没有到，或许对你来说还遥远着呢。但是我们还是应该去遥望2015年，提前了解<u>属鼠</u>的人2015年运程，才可以为自己未来做准备。<br />\r\n&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; 属鼠的人2015年运程_2015属鼠人全年<u>运势</u><br />\r\n&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; 出生年份：&nbsp;2008&nbsp; 1996&nbsp; 1984&nbsp; 1972&nbsp;&nbsp; 1960&nbsp; 1948<br />\r\n&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; 2015年乙未年，对于鼠年生人来讲，子未相害，又逢【太阴】化忌，此年各项运程都要大打折扣，不仅会感觉到前所未有的压力，对于多项事情的态度也不像以往那样的积极的面对，更多的是消极的等待和埋怨，因此务必要调整好心态，迎接诸事不顺的挑战。<u>事业</u>上容易受到小人排挤，平日认为顺利成章的事情很容易产生节外生枝，需做好应对一切变化的准备。【太阴】为财星，一旦化忌<u>财运</u>很不理想，破财迹象明显，而且有些需要投资的项目表面看起来赚钱的机会很大，但是实际操作陷阱不少，切记谨慎投资，也不可与人合伙，容易遭到合伙人的算计，此年建议还是平稳过渡为上。此年容易路与桃花，但大多数都是过眼云烟，成功的机会不大，如果有心仪的对象，切记不可贸然表白，最好等到运势比较好的月份再发起攻击，已婚的朋友们要注意多跟配偶沟通，而且言语上要以关心为主，不可过于较真容易产生口角，控制情绪。平时多做体育锻炼，此年属鼠人子未相害，若三方位逢【大耗】【小耗】【天邢】等煞耀会照，则对脾胃的伤害较大，应注意内脏的养护。<br />\r\n&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; 属鼠的人事业运：<br />\r\n&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; 此年对于属鼠的人来说年干乙【天机】化禄，整年充满了不确定的因素，动星当头，所有的事情都趋于不稳定的结果，因此对于工作中应该时刻保持处理应变的心态，而且很容易产生工作地点或者岗位上的调动，由于受到子未相害的影响，事业上的变动往往是向着复杂的不容易解决的方向变动，而且办事的阻碍要比以往大很多，因此建议保持一颗平常心，切记心浮气躁。<br />\r\n&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; 属鼠的人财运：<br />\r\n&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; 属鼠人在乙未年财运相对较差，给人的感觉是处处都会让你破财，所以平时就应当注意节俭，以备不时之需。有投资的想法的朋友需谨慎用钱，在羊年容易产生节外生枝，会增加开销，很难守住财，而且如果是合伙的生意容易受到朋友的欺骗，即使已经投资的项目，在往来账目上都要清晰，总之此年风险较大，需理性对待。<br />\r\n&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; 属鼠的人健康运：<br />\r\n&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; 乙未年【太阴】化忌，因此【太阴】所代表的的人体的器官都要小心的养护，肾脏，泌尿系统，尤其是女性妇科，切记不能掉以轻心，如果在此年犯病，很不容易治愈。其次丑未相害，对于脾胃伤害也是很大，建议平时多锻炼，女性也可多吃滋补养颜的食品，少生气少上火，若肝火过旺则容易克制未土，因此每天保持一个好的心情最为重要。<br />\r\n&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; 属鼠的人爱情运：<br />\r\n&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; 属鼠人在乙未年要谨防桃花煞和桃花劫的袭来，此年桃花运尚可，但是成功率不是很高，而且容易伤财，要谨慎交友，不要被眼前的现象欺骗，尤其是女性朋友，过多的桃花不但不会增加婚运，反而让你麻烦事更多，甚至引起不必要的误会与纷争。男性朋友在工作中也会遇到心仪的女性，如果想表白最好是在运势好的月份。<br />\r\n&nbsp;<br />\r\n2015年属鼠人旺运锦囊：<br />\r\n&nbsp;<br />\r\n属鼠的人今年运程吉凶三七开，全因2015为乙未之年，乙未为土，土掩于水，子未相害，子水更加的弱势，全年运程多受阻滞，又遇&ldquo;大耗&rdquo;凶星临宫，今年要小心为妙。属鼠人羊年害太岁，建议请购【慈元阁杨仙化太岁锦囊】化解太岁之害。有缘人可为自己、家人或友人请购慈元阁属鼠开光吉祥物，以保乙未年平安顺畅度过。属鼠人可以佩戴【慈元阁文鼎九牛】昆仑白玉坠保个人平安顺畅，此玉坠整体为一头矫健之健牛，跃跃欲试，勇猛有力，有撞破山河之力，保个人平安顺畅。而鼎为积德高贵之物，古语有云&ldquo;高门鼎贵&rdquo;，寓意有力稳定，有助于属鼠的在乙未年，保持积极态度。昆仑白玉亦为世间名器，可保平安，增运添财。同时，属鼠人可在家中或办公室摆放【慈元阁文鼎九牛】摆件调和<u>风水</u>，以保家宅运、旺事业运。属鼠人若想化煞挡灾，寻得贵人，可佩戴【慈元阁五法六尊】【文殊智慧尊】手链。开车的朋友可在车内配挂【慈元阁路路瓶安】琉璃车挂，以化险为夷，人车平安。属鼠人若想事业官运财运上平步青云、顺风顺水、扭颓为胜还可恭请【慈元阁八方赐福轩辕阵】以大展宏图。以上吉祥物由&ldquo;慈元阁&rdquo;独家设计开光发售，请认准<u>生肖</u><u>开运</u>领导品牌&ldquo;慈元阁&rdquo;，谨防盗版假冒！使用盗版吉祥物，非但不能开运，反而会沾染盗版者之业障，影响羊年运势，请缘主明辨切勿贪图小便宜！<br />\r\n&nbsp;<br />\r\n不同年份出生的<u>属鼠人2015年运势</u>：<br />\r\n&nbsp;<br />\r\n1948年戊子年：<br />\r\n&nbsp;<br />\r\n戊子年生年天干四化【贪狼】化禄、【太阴】化权、【右弼】化科、【天机】化忌，在乙未年要控制好自己的情绪，此年很容易急躁，尤其是对子女，情绪上一定要注意，外出的时候要留意脚下，避免磕碰，【天机】化忌对于老人的影响还是比较大的，出门的时候最好少坐车多不行，减少发生意外的机会。注意神经系统、大脑方面的疾病发生。1948年出生的属鼠人羊年害太岁，建议请购【慈元阁杨仙化太岁锦囊】化解太岁之害。佩戴【慈元阁文鼎九牛】昆仑白玉坠可保个人平安顺畅。同时，可在家中或办公室摆放【慈元阁文鼎九牛】摆件调和风水，以保家宅运、旺事业运。。<br />\r\n&nbsp;<br />\r\n1960年庚子年：<br />\r\n&nbsp;<br />\r\n庚子年生年天干四化【太阳】化禄、【武曲】化权、【太阴】化科、【天同】化忌，在乙未年男性的运程要稍微好于女性，事业上要稳扎稳打，不可急于求成，有些存在风险的事情最好在羊年不要沾染，正财运稍好，不要过分的苛求偏财运，会适得其反。投资理财方面要步步谨慎，最好亲力亲为。此年容易跟亲戚朋友的关系紧张，平时有条件的建议多搞聚会，培养感情。身体方面注意消化系统和泌尿系统方面的保养。1960年出生的属鼠人羊年害太岁，建议请购【慈元阁杨仙化太岁锦囊】化解太岁之害。佩戴【慈元阁文鼎九牛】昆仑白玉坠可保个人平安顺畅。同时，可在家中或办公室摆放【慈元阁文鼎九牛】摆件调和风水，以保家宅运、旺事业运。若想化煞挡灾，寻得贵人，可佩戴【慈元阁五法六尊】【文殊智慧尊】手链。开车的朋友可在车内配挂【慈元阁路路瓶安】琉璃车挂，以化险为夷，人车平安。若想事业官运财运上平步青云、顺风顺水、扭颓为胜还可恭请【慈元阁八方赐福轩辕阵】以大展宏图。<br />\r\n&nbsp;<br />\r\n1972年壬子年：<br />\r\n&nbsp;<br />\r\n壬子年生年天干四化【天梁】化禄、【紫薇】化权、【左辅】化科、【武曲】化忌，在乙未年凡事不可要求的太过完美，切记不要较真，容易出现得不偿失的情况。工作中要注意跟上下级的关系，不能急于求成。由于正财星化忌，工作中难免出现疏漏，尤其是财务工作人员，一定要账目清晰，否则容易吃亏。不要轻信一些投机取巧的赚钱方式，需一步一个脚印。所有的事情都要落实到纸面，切勿妄加臆断理所当然。1972年出生的属鼠人羊年害太岁，建议请购【慈元阁杨仙化太岁锦囊】化解太岁之害。佩戴【慈元阁文鼎九牛】昆仑白玉坠可保个人平安顺畅。同时，可在家中或办公室摆放【慈元阁文鼎九牛】摆件调和风水，以保家宅运、旺事业运。若想化煞挡灾，寻得贵人，可佩戴【慈元阁五法六尊】【文殊智慧尊】手链。开车的朋友可在车内配挂【慈元阁路路瓶安】琉璃车挂，以化险为夷，人车平安。若想事业官运财运上平步青云、顺风顺水、扭颓为胜还可恭请【慈元阁八方赐福轩辕阵】以大展宏图<br />\r\n&nbsp;<br />\r\n1984年甲子年：<br />\r\n&nbsp;<br />\r\n甲子年生年天干四化【廉贞】化禄、【破军】化权、【武曲】化科、【太阳】化忌，在乙未年会有一种心高气傲的情绪，着急做出一番事业但是却心有余力不足，实际操作过程中事情会出现很多分歧点，一定要静下心来求助比自己年长的人，因为此年【紫薇】化科，你的上级领导或者年纪比你大的人往往就是你的贵人。求财的时候也要多多考虑，要有长远的目光，不可因小失大。感情上子未相害，最好不要在羊年结婚，感情上也会磕磕绊绊拖拖拉拉的，做好心理准备。1984年出生的属鼠人羊年害太岁，建议请购【慈元阁杨仙化太岁锦囊】化解太岁之害。佩戴【慈元阁文鼎九牛】昆仑白玉坠可保个人平安顺畅。同时，可在家中或办公室摆放【慈元阁文鼎九牛】摆件调和风水，以保家宅运、旺事业运。若想化煞挡灾，寻得贵人，可佩戴【慈元阁五法六尊】【文殊智慧尊】手链。开车的朋友可在车内配挂【慈元阁路路瓶安】琉璃车挂，以化险为夷，人车平安。若想事业官运财运上平步青云、顺风顺水、扭颓为胜还可恭请【慈元阁八方赐福轩辕阵】以大展宏图。<br />\r\n&nbsp;<br />\r\n1996年丙子年：<br />\r\n&nbsp;<br />\r\n丙子年生年天干四化【天同】化禄、【天机】化权、【文昌】化科、【廉贞】化忌，在乙未年工作起来会比较辛苦，经常要变换地点的出差。还在上学的孩子要住此年用功学习，会有很好地收获，成绩名次会有显著提高。父母要注意为孩子减压，此年会因为功课紧张的缘故而压力过大，身体上少吃油腻辛辣的食物，运动的时候注意做好保护措施，谨防血光。1996年出生的属鼠人羊年害太岁，建议请购【慈元阁杨仙化太岁锦囊】化解太岁之害。佩戴【慈元阁文鼎九牛】昆仑白玉坠保个人平安顺畅，此玉坠整体为一头矫健之健牛，跃跃欲试，勇猛有力，有撞破山河之力，保个人平安顺畅。而鼎为积德高贵之物，古语有云&ldquo;高门鼎贵&rdquo;，寓意有力稳定，有助于属鼠的在乙未年，保持积极态度。昆仑白玉亦为世间名器，可保平安，增运添财。若想化煞挡灾，寻得贵人，可佩戴【慈元阁五法六尊】【文殊智慧尊】手链。<br />\r\n&nbsp;<br />\r\n2008年戊子年：<br />\r\n&nbsp;<br />\r\n丙子年生年天干四化【贪狼】化禄、【太阴】化权、【右弼】化科、【天机】化忌，在乙未年孩子需要家长大量的时间陪同，此年容易上火，情绪急躁，而且外出的时候要时刻看护好孩子，注意来往车辆。孩子的心情起伏较大，父母应有足够的耐心，多沟通多鼓励，给予孩子足够的信心。2008年出生的属鼠人羊年害太岁，建议请购【慈元阁杨仙化太岁锦囊】化解太岁之害。佩戴【慈元阁文鼎九牛】昆仑白玉坠可保个人平安顺畅。若想化煞挡灾，寻得贵人，可佩戴【慈元阁五法六尊】【文殊智慧尊】手链。<br />\r\n&nbsp;<br />\r\n属鼠流月运势预测：<br />\r\n&nbsp;<br />\r\n农历正月&nbsp; 戊寅月（阳历2015年2月19日-- 2015年3月19日）<br />\r\n&nbsp;<br />\r\n<u>本月</u>运势走向不尽人意，想要开创事业的朋友要小心了，几乎任何事情都不能完成，尤其是跟人交往，或者是找上级部门批复等一类的事物，最好不要在此月办理，容易四处碰壁以致信心受创。财运方面不要有过多的期待，前一年有尚未完结的账务，本月也很难解决，不能报太大的希望。而且已婚人士容易跟配偶产生口角，需多理解配偶，退一步海阔天空。正确摆放慈元阁八方赐福轩辕阵摆件的缘主，想必已经感受到了吉祥物的灵性。<br />\r\n&nbsp;<br />\r\n农历二月&nbsp; 己卯月（阳历2015年3月20日-- 2015年4月18日）<br />\r\n&nbsp;<br />\r\n本月各项运程较上个月有所好转，但是迹象不是特别明显，在行运的过程中要注意身边的能够帮助到你的男性朋友，或许贵人就在其中，工作中要注意少说话多做事，以免被小人抓到机会，尤其是跟你同级别的同事，不可对其多言。有投资意向的朋友本月可以尝试着进入项目中去，但是也要有一颗谨慎对待的心，以便发生意外足以应对。健康方面不可暴饮暴食，也最好不要饮酒，因为本月消化系统容易出现问题。正确摆放慈元阁文鼎九牛摆件，想必家运事业的不顺已经得到改善。<br />\r\n&nbsp;<br />\r\n农历三月&nbsp; 庚辰月（阳历2015年4月19日-- 2015年5月17日）<br />\r\n&nbsp;<br />\r\n本月可以说是属鼠人在羊年相对运势较好的月份之一，之前积压的相对棘手的问题此月可以着手处理，可能会有意想不到的惊喜，工作过程中跟同事和上下级关系也相对得心应手，因此若事业上有某些想法的朋友可在本月趁热打铁，更进一步。财运方面破财的地方较多，注意开销。本月桃花运也不错，有心仪对象的朋友可大胆表白，但是要选择的流日一定要躲过忌冲，可提高成功率。身体上注意心血管疾病的发生，老年人尤其注意。正确摆放慈元阁八方赐福轩辕阵摆件的缘主，想必已经感受到了吉祥物的灵性。<br />\r\n&nbsp;<br />\r\n农历四月&nbsp; 辛巳月（阳历2015年5月18日-- 2015年6月15日）<br />\r\n&nbsp;<br />\r\n本月运势较上个月稍微平和，财运较好，有想投资理财的朋友可继续上月的<u>好运</u>，但是偏财运较差，切记博彩，事业上多留意领导对你的指点，本月贵人运好一些，或许能够得到被提升的机会，注意把握。本月要注意控制情绪，不论是朋友同事之间，还是配偶家人，都要注意用词，不可火气过大，注意各方面关系的协调，健康上多吃清淡少吃辛辣，注意养护肝脏。正确佩戴慈元阁文鼎九牛玉坠的缘主，想必已经感受到了吉祥物的灵性。<br />\r\n&nbsp;<br />\r\n农历五月&nbsp; 壬午月（阳历2015年6月16日-- 2015年7月15日）<br />\r\n&nbsp;<br />\r\n属鼠人在此月事业上容易产生跟同事之间的冲突，一定要谨慎对待，注意谈话间的用词适当，本月容易出现资金短缺的现象，平时注意开销节俭，而且工作过程中经常会遇到意想不到的节外生枝，需要对事情进行全方位的估计方可进行。桃花运不是很理想，想要表白的朋友尽量克制，躲过此月。身体方面注意骨骼、肌肉、牙齿的保护。正确佩戴慈元阁文殊智慧尊手链的缘主，感情运和贵人运应该已经有所提升了。<br />\r\n&nbsp;<br />\r\n农历六月&nbsp; 癸未月（阳历2015年7月16日-- 2015年8月13日）<br />\r\n&nbsp;<br />\r\n本月依旧延续上个月运程不佳的情况，工作中不可急于求成，要多跟领导沟通领会领导意图，不可擅作主张，而且此月月干【贪狼】化忌，很容易被同事或者客户请到酒色娱乐场所，要注意把持，此月跟配偶及家人的关系相对融洽，只是朋友之间不可说的过多，言多必失，容易产生误会。身体方面注意肠胃、肾脏的保养。正确摆放慈元阁文鼎九牛摆件，想必家运事业的不顺已经得到改善。<br />\r\n&nbsp;<br />\r\n农历七月&nbsp; 甲申月（阳历2015年8月14日-- 2015年9月12日）<br />\r\n&nbsp;<br />\r\n本月较上个月运程有所好转，工作上紧张的情绪有所缓解，日常事务也变得得心应手，跟同事的关系也逐渐融洽，资金方面一切正常，是个少有的偏财运好的月份，喜欢博彩的朋友本月可以少量投资，或许就有意想不到的回报。偏财运好的月份桃花运都不会差，因此还单身的朋友可以多到交友广泛的场所活动，可能会有情定终身的缘分出现。正确佩戴慈元阁文殊智慧尊手链的缘主，感情运和贵人运应该已经有所提升了。<br />\r\n&nbsp;<br />\r\n农历八月&nbsp; 乙酉月（阳历2015年9月13日-- 2015年10月12日）<br />\r\n&nbsp;<br />\r\n此月注意言多必失祸从口出，日常交流要注意用词适当，平时不要再你以为安全的时候嚼舌根，隔墙有耳的事情时有发生，也容易受到小人暗算，本月各项运程都充满很大的不确定因素，因此需要小心谨慎的度过，正财和偏财都不够理想，还是稳扎稳打、按部就班的处理事情，切记心浮气躁，容易惹祸上身。桃花运多为烂桃花，尽量远离。身体方面注意呼吸道、气管的健康防护。正确佩戴慈元阁文鼎九牛玉坠的缘主，想必已经感受到了吉祥物的灵性。<br />\r\n&nbsp;<br />\r\n农历九月&nbsp; 丙戌月（阳历2015年10月13日-- 2015年11月11日）<br />\r\n&nbsp;<br />\r\n本月事业上压力较大，领导分配的工作会比以往增加，而且难度也较大，因此工作中容易产生抵触情绪，需调整好心态，做好心理准备。财运方面也不理想，有投资经营意向的朋友，建议不要在本月开创事业，利润不是很可观。感情上磕磕碰碰也比较多，夫妻间容易因为小时而拌嘴，希望多理解对方。身体上无大碍，保持好的心情即可。正确摆放慈元阁文鼎九牛摆件，想必家运，财运，事业的不顺已经得到改善。<br />\r\n&nbsp;<br />\r\n农历十月&nbsp; 丁亥月（阳历2015年11月12日-- 2015年12月10日）<br />\r\n&nbsp;<br />\r\n本月运程比较平缓，如果不急不躁事情都能如期的解决，但是也不可掉以轻心，毕竟流月的运程都是在流年管辖范围内，流年相害流月即使运程好，也要比正常大打折扣，本月会偶尔会出现办事异常顺利而且财运异常好的情况，也可能出现突然间时运不济的情况，大起大落之势要引起足够的重视。身体上注意不可暴饮暴食，少吃油腻，本月容易伤肝。正确佩戴慈元阁文鼎九牛玉坠的缘主，想必已经感受到了吉祥物的灵性。<br />\r\n&nbsp;<br />\r\n农历十一月&nbsp; 戊子月（阳历2015年12月11日-- 2016年1月9日）<br />\r\n&nbsp;<br />\r\n本月总体运程较差，一切都要小心应对，尤其是资金账务方面，容易引起纠纷，甚至需要司法程序来解决，而且跟客户等社会各方面的关系处理的都不够理想，影响升迁和赚钱。本月破财迹象明显，建议一切从简，大部分破财来自于家人。身体状况会明显感觉疲劳，平时多休息，少出去酒色场所。正确摆放慈元阁文鼎九牛摆件，想必家运事业的不顺已经得到改善。<br />\r\n&nbsp;<br />\r\n农历十二月&nbsp; 己丑月（阳历2016年1月10日-- 2016年2月7日）<br />\r\n&nbsp;<br />\r\n本月工作中比上个月较为轻松，跟同事上级关系处理相对缓和，而且事情办理的相对顺利一些，财运方面正财运较好偏财运较差，想玩博彩的朋友本月建议不要投资过大，而且本月要注意外出时的人身安全，容易有意外情况发生，比如车祸之类。身体方面容易伤风感冒之类，注意肺部气管呼吸道的疾病。正确佩戴慈元阁文鼎九牛玉坠的缘主，想必已经感受到了吉祥物的灵性。\r\n<p>\r\n	<strong>十二属相2015年运势</strong><br />\r\n	<u>属鼠人2015年运势</u> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<u>属牛人2015年运势</u> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<u>属虎人2015年运势</u> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<u>属兔人2015年运势</u><br />\r\n	<u>属龙人2015年运势</u> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<u>属蛇人2015年运势</u> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<u>属马人2015年运势</u> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<u>属羊人2015年运势</u><br />\r\n	<u>属猴人2015年运势</u> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<u>属鸡人2015年运势</u> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<u>属狗人2015年运势</u> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<u>属猪人2015年运势</u></p>\r\n\r\n<div class=\"adb adb_3\"></div>\r\n\r\n</div>\r\n<div class=\"pagelist\"></div>','1','0','0','0','2015-08-31 17:36:32','0');");
E_D("replace into `shengxiao_data` values('20','属鼠人2015年运势','<div class=\"sbody\">\r\n\r\n<div class=\"adb adb_1\"></div>\r\n<div class=''adb adb_2''></div>\r\n&nbsp; &nbsp; 【导语】生活不管是过去还是现在，从来都不为人停留，而未来也经常在你毫无准备的时候从你身边走过，要想把握现在和未来，就需要时刻准备着。<br />\r\n&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; 或许2015年还没有到，或许对你来说还遥远着呢。但是我们还是应该去遥望2015年，提前了解<u>属鼠</u>的人2015年运程，才可以为自己未来做准备。<br />\r\n&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; 属鼠的人2015年运程_2015属鼠人全年<u>运势</u><br />\r\n&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; 出生年份：&nbsp;2008&nbsp; 1996&nbsp; 1984&nbsp; 1972&nbsp;&nbsp; 1960&nbsp; 1948<br />\r\n&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; 2015年乙未年，对于鼠年生人来讲，子未相害，又逢【太阴】化忌，此年各项运程都要大打折扣，不仅会感觉到前所未有的压力，对于多项事情的态度也不像以往那样的积极的面对，更多的是消极的等待和埋怨，因此务必要调整好心态，迎接诸事不顺的挑战。<u>事业</u>上容易受到小人排挤，平日认为顺利成章的事情很容易产生节外生枝，需做好应对一切变化的准备。【太阴】为财星，一旦化忌<u>财运</u>很不理想，破财迹象明显，而且有些需要投资的项目表面看起来赚钱的机会很大，但是实际操作陷阱不少，切记谨慎投资，也不可与人合伙，容易遭到合伙人的算计，此年建议还是平稳过渡为上。此年容易路与桃花，但大多数都是过眼云烟，成功的机会不大，如果有心仪的对象，切记不可贸然表白，最好等到运势比较好的月份再发起攻击，已婚的朋友们要注意多跟配偶沟通，而且言语上要以关心为主，不可过于较真容易产生口角，控制情绪。平时多做体育锻炼，此年属鼠人子未相害，若三方位逢【大耗】【小耗】【天邢】等煞耀会照，则对脾胃的伤害较大，应注意内脏的养护。<br />\r\n&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; 属鼠的人事业运：<br />\r\n&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; 此年对于属鼠的人来说年干乙【天机】化禄，整年充满了不确定的因素，动星当头，所有的事情都趋于不稳定的结果，因此对于工作中应该时刻保持处理应变的心态，而且很容易产生工作地点或者岗位上的调动，由于受到子未相害的影响，事业上的变动往往是向着复杂的不容易解决的方向变动，而且办事的阻碍要比以往大很多，因此建议保持一颗平常心，切记心浮气躁。<br />\r\n&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; 属鼠的人财运：<br />\r\n&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; 属鼠人在乙未年财运相对较差，给人的感觉是处处都会让你破财，所以平时就应当注意节俭，以备不时之需。有投资的想法的朋友需谨慎用钱，在羊年容易产生节外生枝，会增加开销，很难守住财，而且如果是合伙的生意容易受到朋友的欺骗，即使已经投资的项目，在往来账目上都要清晰，总之此年风险较大，需理性对待。<br />\r\n&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; 属鼠的人健康运：<br />\r\n&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; 乙未年【太阴】化忌，因此【太阴】所代表的的人体的器官都要小心的养护，肾脏，泌尿系统，尤其是女性妇科，切记不能掉以轻心，如果在此年犯病，很不容易治愈。其次丑未相害，对于脾胃伤害也是很大，建议平时多锻炼，女性也可多吃滋补养颜的食品，少生气少上火，若肝火过旺则容易克制未土，因此每天保持一个好的心情最为重要。<br />\r\n&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; 属鼠的人爱情运：<br />\r\n&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; 属鼠人在乙未年要谨防桃花煞和桃花劫的袭来，此年桃花运尚可，但是成功率不是很高，而且容易伤财，要谨慎交友，不要被眼前的现象欺骗，尤其是女性朋友，过多的桃花不但不会增加婚运，反而让你麻烦事更多，甚至引起不必要的误会与纷争。男性朋友在工作中也会遇到心仪的女性，如果想表白最好是在运势好的月份。<br />\r\n&nbsp;<br />\r\n2015年属鼠人旺运锦囊：<br />\r\n&nbsp;<br />\r\n属鼠的人今年运程吉凶三七开，全因2015为乙未之年，乙未为土，土掩于水，子未相害，子水更加的弱势，全年运程多受阻滞，又遇&ldquo;大耗&rdquo;凶星临宫，今年要小心为妙。属鼠人羊年害太岁，建议请购【慈元阁杨仙化太岁锦囊】化解太岁之害。有缘人可为自己、家人或友人请购慈元阁属鼠开光吉祥物，以保乙未年平安顺畅度过。属鼠人可以佩戴【慈元阁文鼎九牛】昆仑白玉坠保个人平安顺畅，此玉坠整体为一头矫健之健牛，跃跃欲试，勇猛有力，有撞破山河之力，保个人平安顺畅。而鼎为积德高贵之物，古语有云&ldquo;高门鼎贵&rdquo;，寓意有力稳定，有助于属鼠的在乙未年，保持积极态度。昆仑白玉亦为世间名器，可保平安，增运添财。同时，属鼠人可在家中或办公室摆放【慈元阁文鼎九牛】摆件调和<u>风水</u>，以保家宅运、旺事业运。属鼠人若想化煞挡灾，寻得贵人，可佩戴【慈元阁五法六尊】【文殊智慧尊】手链。开车的朋友可在车内配挂【慈元阁路路瓶安】琉璃车挂，以化险为夷，人车平安。属鼠人若想事业官运财运上平步青云、顺风顺水、扭颓为胜还可恭请【慈元阁八方赐福轩辕阵】以大展宏图。以上吉祥物由&ldquo;慈元阁&rdquo;独家设计开光发售，请认准<u>生肖</u><u>开运</u>领导品牌&ldquo;慈元阁&rdquo;，谨防盗版假冒！使用盗版吉祥物，非但不能开运，反而会沾染盗版者之业障，影响羊年运势，请缘主明辨切勿贪图小便宜！<br />\r\n&nbsp;<br />\r\n不同年份出生的<u>属鼠人2015年运势</u>：<br />\r\n&nbsp;<br />\r\n1948年戊子年：<br />\r\n&nbsp;<br />\r\n戊子年生年天干四化【贪狼】化禄、【太阴】化权、【右弼】化科、【天机】化忌，在乙未年要控制好自己的情绪，此年很容易急躁，尤其是对子女，情绪上一定要注意，外出的时候要留意脚下，避免磕碰，【天机】化忌对于老人的影响还是比较大的，出门的时候最好少坐车多不行，减少发生意外的机会。注意神经系统、大脑方面的疾病发生。1948年出生的属鼠人羊年害太岁，建议请购【慈元阁杨仙化太岁锦囊】化解太岁之害。佩戴【慈元阁文鼎九牛】昆仑白玉坠可保个人平安顺畅。同时，可在家中或办公室摆放【慈元阁文鼎九牛】摆件调和风水，以保家宅运、旺事业运。。<br />\r\n&nbsp;<br />\r\n1960年庚子年：<br />\r\n&nbsp;<br />\r\n庚子年生年天干四化【太阳】化禄、【武曲】化权、【太阴】化科、【天同】化忌，在乙未年男性的运程要稍微好于女性，事业上要稳扎稳打，不可急于求成，有些存在风险的事情最好在羊年不要沾染，正财运稍好，不要过分的苛求偏财运，会适得其反。投资理财方面要步步谨慎，最好亲力亲为。此年容易跟亲戚朋友的关系紧张，平时有条件的建议多搞聚会，培养感情。身体方面注意消化系统和泌尿系统方面的保养。1960年出生的属鼠人羊年害太岁，建议请购【慈元阁杨仙化太岁锦囊】化解太岁之害。佩戴【慈元阁文鼎九牛】昆仑白玉坠可保个人平安顺畅。同时，可在家中或办公室摆放【慈元阁文鼎九牛】摆件调和风水，以保家宅运、旺事业运。若想化煞挡灾，寻得贵人，可佩戴【慈元阁五法六尊】【文殊智慧尊】手链。开车的朋友可在车内配挂【慈元阁路路瓶安】琉璃车挂，以化险为夷，人车平安。若想事业官运财运上平步青云、顺风顺水、扭颓为胜还可恭请【慈元阁八方赐福轩辕阵】以大展宏图。<br />\r\n&nbsp;<br />\r\n1972年壬子年：<br />\r\n&nbsp;<br />\r\n壬子年生年天干四化【天梁】化禄、【紫薇】化权、【左辅】化科、【武曲】化忌，在乙未年凡事不可要求的太过完美，切记不要较真，容易出现得不偿失的情况。工作中要注意跟上下级的关系，不能急于求成。由于正财星化忌，工作中难免出现疏漏，尤其是财务工作人员，一定要账目清晰，否则容易吃亏。不要轻信一些投机取巧的赚钱方式，需一步一个脚印。所有的事情都要落实到纸面，切勿妄加臆断理所当然。1972年出生的属鼠人羊年害太岁，建议请购【慈元阁杨仙化太岁锦囊】化解太岁之害。佩戴【慈元阁文鼎九牛】昆仑白玉坠可保个人平安顺畅。同时，可在家中或办公室摆放【慈元阁文鼎九牛】摆件调和风水，以保家宅运、旺事业运。若想化煞挡灾，寻得贵人，可佩戴【慈元阁五法六尊】【文殊智慧尊】手链。开车的朋友可在车内配挂【慈元阁路路瓶安】琉璃车挂，以化险为夷，人车平安。若想事业官运财运上平步青云、顺风顺水、扭颓为胜还可恭请【慈元阁八方赐福轩辕阵】以大展宏图<br />\r\n&nbsp;<br />\r\n1984年甲子年：<br />\r\n&nbsp;<br />\r\n甲子年生年天干四化【廉贞】化禄、【破军】化权、【武曲】化科、【太阳】化忌，在乙未年会有一种心高气傲的情绪，着急做出一番事业但是却心有余力不足，实际操作过程中事情会出现很多分歧点，一定要静下心来求助比自己年长的人，因为此年【紫薇】化科，你的上级领导或者年纪比你大的人往往就是你的贵人。求财的时候也要多多考虑，要有长远的目光，不可因小失大。感情上子未相害，最好不要在羊年结婚，感情上也会磕磕绊绊拖拖拉拉的，做好心理准备。1984年出生的属鼠人羊年害太岁，建议请购【慈元阁杨仙化太岁锦囊】化解太岁之害。佩戴【慈元阁文鼎九牛】昆仑白玉坠可保个人平安顺畅。同时，可在家中或办公室摆放【慈元阁文鼎九牛】摆件调和风水，以保家宅运、旺事业运。若想化煞挡灾，寻得贵人，可佩戴【慈元阁五法六尊】【文殊智慧尊】手链。开车的朋友可在车内配挂【慈元阁路路瓶安】琉璃车挂，以化险为夷，人车平安。若想事业官运财运上平步青云、顺风顺水、扭颓为胜还可恭请【慈元阁八方赐福轩辕阵】以大展宏图。<br />\r\n&nbsp;<br />\r\n1996年丙子年：<br />\r\n&nbsp;<br />\r\n丙子年生年天干四化【天同】化禄、【天机】化权、【文昌】化科、【廉贞】化忌，在乙未年工作起来会比较辛苦，经常要变换地点的出差。还在上学的孩子要住此年用功学习，会有很好地收获，成绩名次会有显著提高。父母要注意为孩子减压，此年会因为功课紧张的缘故而压力过大，身体上少吃油腻辛辣的食物，运动的时候注意做好保护措施，谨防血光。1996年出生的属鼠人羊年害太岁，建议请购【慈元阁杨仙化太岁锦囊】化解太岁之害。佩戴【慈元阁文鼎九牛】昆仑白玉坠保个人平安顺畅，此玉坠整体为一头矫健之健牛，跃跃欲试，勇猛有力，有撞破山河之力，保个人平安顺畅。而鼎为积德高贵之物，古语有云&ldquo;高门鼎贵&rdquo;，寓意有力稳定，有助于属鼠的在乙未年，保持积极态度。昆仑白玉亦为世间名器，可保平安，增运添财。若想化煞挡灾，寻得贵人，可佩戴【慈元阁五法六尊】【文殊智慧尊】手链。<br />\r\n&nbsp;<br />\r\n2008年戊子年：<br />\r\n&nbsp;<br />\r\n丙子年生年天干四化【贪狼】化禄、【太阴】化权、【右弼】化科、【天机】化忌，在乙未年孩子需要家长大量的时间陪同，此年容易上火，情绪急躁，而且外出的时候要时刻看护好孩子，注意来往车辆。孩子的心情起伏较大，父母应有足够的耐心，多沟通多鼓励，给予孩子足够的信心。2008年出生的属鼠人羊年害太岁，建议请购【慈元阁杨仙化太岁锦囊】化解太岁之害。佩戴【慈元阁文鼎九牛】昆仑白玉坠可保个人平安顺畅。若想化煞挡灾，寻得贵人，可佩戴【慈元阁五法六尊】【文殊智慧尊】手链。<br />\r\n&nbsp;<br />\r\n属鼠流月运势预测：<br />\r\n&nbsp;<br />\r\n农历正月&nbsp; 戊寅月（阳历2015年2月19日-- 2015年3月19日）<br />\r\n&nbsp;<br />\r\n<u>本月</u>运势走向不尽人意，想要开创事业的朋友要小心了，几乎任何事情都不能完成，尤其是跟人交往，或者是找上级部门批复等一类的事物，最好不要在此月办理，容易四处碰壁以致信心受创。财运方面不要有过多的期待，前一年有尚未完结的账务，本月也很难解决，不能报太大的希望。而且已婚人士容易跟配偶产生口角，需多理解配偶，退一步海阔天空。正确摆放慈元阁八方赐福轩辕阵摆件的缘主，想必已经感受到了吉祥物的灵性。<br />\r\n&nbsp;<br />\r\n农历二月&nbsp; 己卯月（阳历2015年3月20日-- 2015年4月18日）<br />\r\n&nbsp;<br />\r\n本月各项运程较上个月有所好转，但是迹象不是特别明显，在行运的过程中要注意身边的能够帮助到你的男性朋友，或许贵人就在其中，工作中要注意少说话多做事，以免被小人抓到机会，尤其是跟你同级别的同事，不可对其多言。有投资意向的朋友本月可以尝试着进入项目中去，但是也要有一颗谨慎对待的心，以便发生意外足以应对。健康方面不可暴饮暴食，也最好不要饮酒，因为本月消化系统容易出现问题。正确摆放慈元阁文鼎九牛摆件，想必家运事业的不顺已经得到改善。<br />\r\n&nbsp;<br />\r\n农历三月&nbsp; 庚辰月（阳历2015年4月19日-- 2015年5月17日）<br />\r\n&nbsp;<br />\r\n本月可以说是属鼠人在羊年相对运势较好的月份之一，之前积压的相对棘手的问题此月可以着手处理，可能会有意想不到的惊喜，工作过程中跟同事和上下级关系也相对得心应手，因此若事业上有某些想法的朋友可在本月趁热打铁，更进一步。财运方面破财的地方较多，注意开销。本月桃花运也不错，有心仪对象的朋友可大胆表白，但是要选择的流日一定要躲过忌冲，可提高成功率。身体上注意心血管疾病的发生，老年人尤其注意。正确摆放慈元阁八方赐福轩辕阵摆件的缘主，想必已经感受到了吉祥物的灵性。<br />\r\n&nbsp;<br />\r\n农历四月&nbsp; 辛巳月（阳历2015年5月18日-- 2015年6月15日）<br />\r\n&nbsp;<br />\r\n本月运势较上个月稍微平和，财运较好，有想投资理财的朋友可继续上月的<u>好运</u>，但是偏财运较差，切记博彩，事业上多留意领导对你的指点，本月贵人运好一些，或许能够得到被提升的机会，注意把握。本月要注意控制情绪，不论是朋友同事之间，还是配偶家人，都要注意用词，不可火气过大，注意各方面关系的协调，健康上多吃清淡少吃辛辣，注意养护肝脏。正确佩戴慈元阁文鼎九牛玉坠的缘主，想必已经感受到了吉祥物的灵性。<br />\r\n&nbsp;<br />\r\n农历五月&nbsp; 壬午月（阳历2015年6月16日-- 2015年7月15日）<br />\r\n&nbsp;<br />\r\n属鼠人在此月事业上容易产生跟同事之间的冲突，一定要谨慎对待，注意谈话间的用词适当，本月容易出现资金短缺的现象，平时注意开销节俭，而且工作过程中经常会遇到意想不到的节外生枝，需要对事情进行全方位的估计方可进行。桃花运不是很理想，想要表白的朋友尽量克制，躲过此月。身体方面注意骨骼、肌肉、牙齿的保护。正确佩戴慈元阁文殊智慧尊手链的缘主，感情运和贵人运应该已经有所提升了。<br />\r\n&nbsp;<br />\r\n农历六月&nbsp; 癸未月（阳历2015年7月16日-- 2015年8月13日）<br />\r\n&nbsp;<br />\r\n本月依旧延续上个月运程不佳的情况，工作中不可急于求成，要多跟领导沟通领会领导意图，不可擅作主张，而且此月月干【贪狼】化忌，很容易被同事或者客户请到酒色娱乐场所，要注意把持，此月跟配偶及家人的关系相对融洽，只是朋友之间不可说的过多，言多必失，容易产生误会。身体方面注意肠胃、肾脏的保养。正确摆放慈元阁文鼎九牛摆件，想必家运事业的不顺已经得到改善。<br />\r\n&nbsp;<br />\r\n农历七月&nbsp; 甲申月（阳历2015年8月14日-- 2015年9月12日）<br />\r\n&nbsp;<br />\r\n本月较上个月运程有所好转，工作上紧张的情绪有所缓解，日常事务也变得得心应手，跟同事的关系也逐渐融洽，资金方面一切正常，是个少有的偏财运好的月份，喜欢博彩的朋友本月可以少量投资，或许就有意想不到的回报。偏财运好的月份桃花运都不会差，因此还单身的朋友可以多到交友广泛的场所活动，可能会有情定终身的缘分出现。正确佩戴慈元阁文殊智慧尊手链的缘主，感情运和贵人运应该已经有所提升了。<br />\r\n&nbsp;<br />\r\n农历八月&nbsp; 乙酉月（阳历2015年9月13日-- 2015年10月12日）<br />\r\n&nbsp;<br />\r\n此月注意言多必失祸从口出，日常交流要注意用词适当，平时不要再你以为安全的时候嚼舌根，隔墙有耳的事情时有发生，也容易受到小人暗算，本月各项运程都充满很大的不确定因素，因此需要小心谨慎的度过，正财和偏财都不够理想，还是稳扎稳打、按部就班的处理事情，切记心浮气躁，容易惹祸上身。桃花运多为烂桃花，尽量远离。身体方面注意呼吸道、气管的健康防护。正确佩戴慈元阁文鼎九牛玉坠的缘主，想必已经感受到了吉祥物的灵性。<br />\r\n&nbsp;<br />\r\n农历九月&nbsp; 丙戌月（阳历2015年10月13日-- 2015年11月11日）<br />\r\n&nbsp;<br />\r\n本月事业上压力较大，领导分配的工作会比以往增加，而且难度也较大，因此工作中容易产生抵触情绪，需调整好心态，做好心理准备。财运方面也不理想，有投资经营意向的朋友，建议不要在本月开创事业，利润不是很可观。感情上磕磕碰碰也比较多，夫妻间容易因为小时而拌嘴，希望多理解对方。身体上无大碍，保持好的心情即可。正确摆放慈元阁文鼎九牛摆件，想必家运，财运，事业的不顺已经得到改善。<br />\r\n&nbsp;<br />\r\n农历十月&nbsp; 丁亥月（阳历2015年11月12日-- 2015年12月10日）<br />\r\n&nbsp;<br />\r\n本月运程比较平缓，如果不急不躁事情都能如期的解决，但是也不可掉以轻心，毕竟流月的运程都是在流年管辖范围内，流年相害流月即使运程好，也要比正常大打折扣，本月会偶尔会出现办事异常顺利而且财运异常好的情况，也可能出现突然间时运不济的情况，大起大落之势要引起足够的重视。身体上注意不可暴饮暴食，少吃油腻，本月容易伤肝。正确佩戴慈元阁文鼎九牛玉坠的缘主，想必已经感受到了吉祥物的灵性。<br />\r\n&nbsp;<br />\r\n农历十一月&nbsp; 戊子月（阳历2015年12月11日-- 2016年1月9日）<br />\r\n&nbsp;<br />\r\n本月总体运程较差，一切都要小心应对，尤其是资金账务方面，容易引起纠纷，甚至需要司法程序来解决，而且跟客户等社会各方面的关系处理的都不够理想，影响升迁和赚钱。本月破财迹象明显，建议一切从简，大部分破财来自于家人。身体状况会明显感觉疲劳，平时多休息，少出去酒色场所。正确摆放慈元阁文鼎九牛摆件，想必家运事业的不顺已经得到改善。<br />\r\n&nbsp;<br />\r\n农历十二月&nbsp; 己丑月（阳历2016年1月10日-- 2016年2月7日）<br />\r\n&nbsp;<br />\r\n本月工作中比上个月较为轻松，跟同事上级关系处理相对缓和，而且事情办理的相对顺利一些，财运方面正财运较好偏财运较差，想玩博彩的朋友本月建议不要投资过大，而且本月要注意外出时的人身安全，容易有意外情况发生，比如车祸之类。身体方面容易伤风感冒之类，注意肺部气管呼吸道的疾病。正确佩戴慈元阁文鼎九牛玉坠的缘主，想必已经感受到了吉祥物的灵性。\r\n<p>\r\n	<strong>十二属相2015年运势</strong><br />\r\n	<u>属鼠人2015年运势</u> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<u>属牛人2015年运势</u> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<u>属虎人2015年运势</u> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<u>属兔人2015年运势</u><br />\r\n	<u>属龙人2015年运势</u> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<u>属蛇人2015年运势</u> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<u>属马人2015年运势</u> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<u>属羊人2015年运势</u><br />\r\n	<u>属猴人2015年运势</u> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<u>属鸡人2015年运势</u> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<u>属狗人2015年运势</u> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<u>属猪人2015年运势</u></p>\r\n\r\n<div class=\"adb adb_3\"></div>\r\n\r\n</div>\r\n<div class=\"pagelist\"></div>','1','0','0','0','2015-08-31 17:36:32','0');");
E_D("replace into `shengxiao_data` values('21','生肖鼠的人2015年的运程','<div class=\"sbody\">\r\n\r\n<div class=\"adb adb_1\"></div>\r\n<div class=''adb adb_2''></div>\r\n<div>\r\n	<u>生肖</u><u>属鼠</u>的人，2015年虽然害太岁，但也有众多吉星高照，如&ldquo;天乙贵人&rdquo;和&ldquo;太极贵人&rdquo;等，故此流年运程较为畅旺，读书做事均可得心应手；但2015年是非颇多，必须谨口慎言，提防口舌招尤。同时流年有&ldquo;咸池&rdquo;入宫，要提防偏桃花对感情的破坏。<span style=\"color:#ff0000;\">详情〉〉</span>\r\n	<div>\r\n		&nbsp;</div>\r\n	<div>\r\n		<strong><u>事业</u>方面</strong><br />\r\n		<br />\r\n		2015年夏季虽有困阻，但终可冲破困厄而得以大展鸿图，特别是在年尾将会有更进一步发展。2015年适宜往外发展，尤以前往西或西北方最为有利；但千万不可在外拈花惹草，以免后患无穷。</div>\r\n	<div>\r\n		&nbsp;</div>\r\n	<div>\r\n		<strong><u>财运</u>方面</strong><br />\r\n		<br />\r\n		2015年正财颇佳，职场上的属鼠人只要按部就班就会小有所得，今年运气不错，但对于大资金的投资还是应该多注意，与人合作也应慎重，求稳为主；偏财运佳，偶尔可以买彩票中奖几率大。</div>\r\n	<div>\r\n		&nbsp;</div>\r\n	<div>\r\n		<strong>健康方面</strong><br />\r\n		<br />\r\n		今年属鼠的人健康状况良好，老人身体也相对比较稳定，不会有大病，平日多留意肝胆、脾胃方面的情况，保持良好的作息时间即可。</div>\r\n	<div>\r\n		&nbsp;</div>\r\n	<div>\r\n		<strong>感情方面</strong><br />\r\n		<br />\r\n		2015年与异性特别投缘，感情生活多姿多彩；但千万不可过分放纵，以免惹下孽缘而难以回头。至于那些尚未有伴侣的男女，2015年将会有机缘碰到合适的对象。</div>\r\n	<div>\r\n		&nbsp;</div>\r\n	<div>\r\n		<strong>学业方面</strong><br />\r\n		<br />\r\n		2015年读书成绩颇佳；若肯专心温习，学业定可突飞猛进。但2015年易与同学朋友发生冲突，必须尽量忍让；此外并要注意身体健康。</div>\r\n	<div>\r\n		&nbsp;</div>\r\n	<div>\r\n		<strong>属鼠的家庭主妇</strong><br />\r\n		<br />\r\n		2015年喜事重重，但必须不可过分张扬，以免招人妒忌。夏季应小心理财，否则便有破财之虞。</div>\r\n	<div>\r\n		&nbsp;</div>\r\n	<div>\r\n		<strong>未婚的少女</strong><br />\r\n		<br />\r\n		2015年感情方面大有进展，甜蜜温馨；因命宫中红惊星动，谈婚论嫁甚为适宜，而以秋后最为吉利。<span style=\"color:#ff0000;\">2015年属鼠人运程大全及破解〉〉</span></div>\r\n</div>\r\n<br />\r\n您可能也喜欢：<br />\r\n<div>\r\n	属鼠人2015年运势&nbsp; &nbsp;&nbsp;属牛人2015年运势&nbsp; &nbsp;&nbsp;属虎人2015年运势&nbsp; &nbsp;&nbsp;属兔人2015年运势</div>\r\n<div>\r\n	属龙人2015年运势&nbsp; &nbsp;&nbsp;属蛇人2015年运势&nbsp; &nbsp;&nbsp;属马人2015年运势&nbsp; &nbsp;&nbsp;属羊人2015年运势</div>\r\n<div>\r\n	属猴人2015年运势&nbsp; &nbsp;&nbsp;属鸡人2015年运势&nbsp; &nbsp;&nbsp;属狗人2015年运势&nbsp; &nbsp;&nbsp;属猪人2015年运势</div>\r\n<br />\r\n\r\n<div class=\"adb adb_3\"></div>\r\n\r\n</div>\r\n<div class=\"pagelist\"></div>','1','0','0','0','2015-08-31 17:36:32','0');");
E_D("replace into `shengxiao_data` values('22','生肖鼠2015年的运程','<div class=\"sbody\">\r\n\r\n<div class=\"adb adb_1\"></div>\r\n<div class=''adb adb_2''></div>\r\n<div>\r\n	来到了2015年乙未年，幸得【紫微】星临照，<u>属鼠</u>的人整体羊年运程颇佳，较之去年有了不错的起色，诸事还算比较顺利；特别是学生群体，只要多加把劲儿、在学业上会有较大的提升。<span style=\"color:#ff0000;\">详情〉〉</span></div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	<strong>属鼠的人2015年健康<u>运势</u>&mdash;&mdash;&mdash;身体一般，易染小感</strong></div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	属鼠人在羊年要多锻炼，女性也可多吃滋补养颜的食品，少生气少上火，若肝火过旺则容易克制未土，每天保持一个好的心情，基本无大碍。</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	<strong>属鼠的人2015年<u>事业</u>运势&mdash;&mdash;&mdash;工作得心应手，有晋升机遇</strong></div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	恰逢【紫微】星到宫，工作上十分得心应手，有不俗的表现，只要做好本职工作，与上下级、同事处好关系，便能赢得领导的重用及信任。今年星行【龙德】，乃主贵人，可为属鼠的人带来事业上的贵人，带来晋升的机遇，因此今年必然是事业旺盛、提升发展的一年。但是、切记为人不可过于张扬高调，切勿得意忘形，不然很可能招致同事暗算，特别是在年中要更加注意。</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	<strong>属鼠的人2015年<u>财运</u>运势&mdash;&mdash;&mdash;贵人相助，财运畅通</strong></div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	属鼠人2015年理财宜保守为重，平时应当注意节俭，合理安排钱财就不会有什么大问题，整年财运总观都会顺风顺水的。</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	<strong>属鼠的人2015年<u>爱情运势</u>&mdash;&mdash;&mdash;桃花运佳，感情丰富多彩</strong></div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	此年恰好走入桃花星位，属鼠的人桃花运较佳，呈现多姿多彩的状态，抓紧机会拓宽自己的交际，单身的你、在此过程中就遇到了心仪的对象，迅速沉浸入爱河。恋人与已婚人士在今年易与另一半产生摩擦，有诸多的不满与争执，还好【紫微】星、【和事佬】眷顾，因此感情能得到很好的调节。对于感情成熟的恋人们来说，今年是结婚的好时机。<span style=\"color:#ff0000;\">2015年属鼠人运程大全及破解〉〉</span></div>\r\n<br />\r\n您可能也喜欢：<br />\r\n<div>\r\n	属鼠人2015年运势&nbsp; &nbsp;&nbsp;属牛人2015年运势&nbsp; &nbsp;&nbsp;属虎人2015年运势&nbsp; &nbsp;&nbsp;属兔人2015年运势</div>\r\n<div>\r\n	属龙人2015年运势&nbsp; &nbsp;&nbsp;属蛇人2015年运势&nbsp; &nbsp;&nbsp;属马人2015年运势&nbsp; &nbsp;&nbsp;属羊人2015年运势</div>\r\n<div>\r\n	属猴人2015年运势&nbsp; &nbsp;&nbsp;属鸡人2015年运势&nbsp; &nbsp;&nbsp;属狗人2015年运势&nbsp; &nbsp;&nbsp;属猪人2015年运势</div>\r\n<br />\r\n\r\n<div class=\"adb adb_3\"></div>\r\n\r\n</div>\r\n<div class=\"pagelist\"></div>','1','0','0','0','2015-08-31 17:36:33','0');");
E_D("replace into `shengxiao_data` values('23','1984年属鼠的人2015年运程','<div class=\"sbody\">\r\n\r\n<div class=\"adb adb_1\"></div>\r\n<div class=''adb adb_2''></div>\r\n<strong>1984年<u>属鼠</u>的人2015年运程<br />\r\n&nbsp;<br />\r\n1984年属鼠人整体运程：</strong><br />\r\n&nbsp;<br />\r\n1984年出生的属鼠人，天干四化【廉贞】化禄、【破军】化权、【武曲】化科、【太阳】化忌，在乙未年会有一种心高气傲的情绪，着急做出一番<u>事业</u>但是却心有余力不足，实际操作过程中事情会出现分歧点，一定要静下心来求助比自己年长的人，因为此年【紫薇】化科，你的上级领导或者年纪比你大的人往往就是你的贵人。求财的时候也要多考虑考虑，目光要长远，不要因小失大。<br />\r\n&nbsp;<br />\r\n<strong>1984年属鼠人事业运：</strong><br />\r\n&nbsp;<br />\r\n对于1984年出生属鼠的人来说【天机】化禄，整年充满了不确定的因素，动星当头，所有的事情都趋于不稳定的结果，因此对于工作中应该时刻保持着应变的心态，在羊年里容易产生工作地点或者岗位上的调动，由于受到子未相害的影响，事业上的变动往往是向着复杂的不容易解决的方向变动，而且办事的阻碍要比以往大很多，因此建议保持一颗平常心，切记心浮气躁。<br />\r\n<strong>&nbsp;<br />\r\n1984年属鼠人<u>财运</u>：</strong><br />\r\n&nbsp;<br />\r\n84年出生的属鼠人在今年里的财运相对较差，给人的感觉是处处都会让你破财，所以平时就应当注意节俭，以备不时之需。有投资想法的朋友需谨慎，在羊年容易产生节外生枝，会增加开销，很难守住财，而且如果是合伙的生意容易受到朋友的欺骗，即使已经投资的项目，在往来账目上都要清晰，总之此年风险较大，需理性对待。<br />\r\n&nbsp;<br />\r\n<strong>1984年属鼠人健康运：</strong><br />\r\n&nbsp;<br />\r\n今年里【太阴】化忌，因此【太阴】所代表的的人体的器官都要小心的养护，肾脏，泌尿系统，尤其是女性，切记不能掉以轻心，如果在此年犯病，很不容易治愈。其次子未相害，对于脾胃伤害也是很大，建议平时多锻炼，女性也可多吃滋补养颜的食品，少生气少上火，若肝火过旺则容易克制未土，因此每天保持一个好的心情最为重要。<br />\r\n<strong>&nbsp;<br />\r\n1984年属鼠人桃花运：</strong><br />\r\n&nbsp;<br />\r\n在桃花方面，在本年要谨防桃花煞和桃花劫的袭来，此年桃花运尚可，但是成功率不是很高，而且容易伤财，要谨慎交友，不要被眼前的现象欺骗，尤其是女性朋友，过多的桃花不但不会增加婚运，反而让你麻烦事更多，甚至引起不必要的误会。男性朋友在工作中也会遇到心仪的女性，如果想表白最好是在<u>运势</u>好的月份。<br />\r\n&nbsp;<br />\r\n<strong>1984年属鼠人是什么命：</strong><br />\r\n&nbsp;<br />\r\n84年的属鼠人，乃为海中金命之鼠也。简称&ldquo;金鼠&rdquo;，以甲子见癸亥，是不用火，逢空有蚌珠照月格，这句的解释就是：八字中带有甲子，又带有癸亥，是真正的海中金（癸亥是水，是水晶宫的意思），这样的格局是不能见火的（见火就破格了），八字里又有空亡，相当于这蚌壳里有了珍珠，就是所谓的蚌珠照月格。<br />\r\n&nbsp;<br />\r\n&nbsp;<br />\r\n&nbsp;<br />\r\n\r\n<div class=\"adb adb_3\"></div>\r\n\r\n</div>\r\n<div class=\"pagelist\"></div>','1','0','0','0','2015-08-31 17:36:33','0');");
E_D("replace into `shengxiao_data` values('24','1972年属鼠的人2015年运程','<div class=\"sbody\">\r\n\r\n<div class=\"adb adb_1\"></div>\r\n<div class=''adb adb_2''></div>\r\n<strong>1972年<u>属鼠</u>的人2015年运程<br />\r\n&nbsp;<br />\r\n1972年属鼠的人2015年全年运程：</strong><br />\r\n&nbsp;<br />\r\n壬子年生年天干四化【天梁】化禄、【紫薇】化权、【左辅】化科、【武曲】化忌，在乙未年凡事不可要求的太过完美，切记不要较真，容易出现得不偿失的情况。工作中要注意跟上下级的关系，不能急于求成。由于正财星化忌，工作中难免出现疏漏，尤其是财务工作人员，一定要账目清晰，否则容易吃亏。不要轻信一些投机取巧的赚钱方式，需一步一个脚印。所有的事情都要落实到纸面，切勿妄加臆断理所当然。<br />\r\n&nbsp;<br />\r\n1972年壬子年生属鼠的人在羊年里适合住的楼层：<br />\r\n&nbsp;<br />\r\n生于壬子年（1972年）的小鼠，五行属水，适合居住尾数为1、6的楼层。<br />\r\n&nbsp;<br />\r\n<strong>1972年属鼠的人2015年<u>事业</u>运：</strong><br />\r\n&nbsp;<br />\r\n此年对于属鼠的人来说年干乙【天机】化禄，整年充满了不确定的因素，动星当头，所有的事情都趋于不稳定的结果，因此对于工作中应该时刻保持处理应变的心态，而且很容易产生工作地点或者岗位上的调动，由于受到子未相害的影响，事业上的变动往往是向着复杂的不容易解决的方向变动，而且办事的阻碍要比以往大很多，因此建议保持一颗平常心，切记心浮气躁。<br />\r\n&nbsp;<br />\r\n<strong>1972年属鼠的人2015年<u>财运</u>：</strong><br />\r\n&nbsp;<br />\r\n壬子年生属鼠人在乙未年财运相对较差，给人的感觉是处处都会让你破财，所以平时就应当注意节俭，以备不时之需。有投资的想法的朋友需谨慎用钱，在羊年容易产生节外生枝，会增加开销，很难守住财，而且如果是合伙的生意容易受到朋友的欺骗，即使已经投资的项目，在往来账目上都要清晰，总之此年风险较大，需理性对待。<br />\r\n&nbsp;<br />\r\n<strong>1972年属鼠的人2015年健康运：</strong><br />\r\n&nbsp;<br />\r\n乙未年【太阴】化忌，因此【太阴】所代表的的人体的器官都要小心的养护，肾脏，泌尿系统，尤其是女性妇科，切记不能掉以轻心，如果在此年犯病，很不容易治愈。其次丑未相害，对于脾胃伤害也是很大，建议平时多锻炼，女性也可多吃滋补养颜的食品，少生气少上火，若肝火过旺则容易克制未土，因此每天保持一个好的心情最为重要。<br />\r\n<strong>&nbsp;<br />\r\n1972年属鼠的人2015年爱情运：</strong><br />\r\n&nbsp;<br />\r\n壬子年生属鼠人在乙未年要谨防桃花煞和桃花劫的袭来，此年桃花运尚可，但是成功率不是很高，而且容易伤财，要谨慎交友，不要被眼前的现象欺骗，尤其是女性朋友，过多的桃花不但不会增加婚运，反而让你麻烦事更多，甚至引起不必要的误会与纷争。男性朋友在工作中也会遇到心仪的女性，如果想表白最好是在<u>运势</u>好的月份。<br />\r\n&nbsp;<br />\r\n<strong>1972年属鼠的人<u>性格</u>解析：</strong><br />\r\n&nbsp;<br />\r\n属鼠的人记忆力很好，非常爱提问题，独具慧眼。你几乎了解周围的每一个人、每一件事，把它记录下来，并把这些当做是自己的嗜好。因此，属鼠的人成为优秀作家并不令人吃惊。<br />\r\n&nbsp;<br />\r\n&nbsp;<br />\r\n&nbsp;<br />\r\n&nbsp;<br />\r\n&nbsp;<br />\r\n\r\n<div class=\"adb adb_3\"></div>\r\n\r\n</div>\r\n<div class=\"pagelist\"></div>','1','0','0','0','2015-08-31 17:36:33','0');");
E_D("replace into `shengxiao_data` values('25','1960年属鼠的人2015年运程','<div class=\"sbody\">\r\n\r\n<div class=\"adb adb_1\"></div>\r\n<div class=''adb adb_2''></div>\r\n<strong>1960年<u>属鼠</u>的人2015年运程<br />\r\n&nbsp;<br />\r\n1960年出生的人今年整体运程简介：</strong><br />\r\n&nbsp;<br />\r\n庚子年生年天干四化【太阳】化禄、【武曲】化权、【太阴】化科、【天同】化忌，在乙未年男性的运程要稍微好于女性，<u>事业</u>上要稳扎稳打，不可急于求成，有些存在风险的事情最好在羊年不要沾染，正<u>财运</u>稍好，不要过分的苛求偏财运，会适得其反。投资理财方面要步步谨慎，最好亲力亲为。此年容易跟亲戚朋友的关系紧张，平时有条件的建议多搞聚会，培养感情。身体方面注意消化系统和泌尿系统方面的保养。<br />\r\n&nbsp;<br />\r\n<strong>1960年属鼠的人财运<u>运势</u>：</strong><br />\r\n&nbsp;<br />\r\n庚子年生属鼠人在乙未年财运相对较差，给人的感觉是处处都会让你破财，所以平时就应当注意节俭，以备不时之需。有投资的想法的朋友需谨慎用钱，在羊年容易产生节外生枝，会增加开销，很难守住财，而且如果是合伙的生意容易受到朋友的欺骗，即使已经投资的项目，在往来账目上都要清晰，总之此年风险较大，需理性对待。<br />\r\n&nbsp;<br />\r\n<strong>1960年属鼠人2015年事业运程：</strong><br />\r\n&nbsp;<br />\r\n此年对于庚子年生属鼠的人来说年干乙【天机】化禄，整年充满了不确定的因素，动星当头，所有的事情都趋于不稳定的结果，因此对于工作中应该时刻保持处理应变的心态，而且很容易产生工作地点或者岗位上的调动，由于受到子未相害的影响，事业上的变动往往是向着复杂的不容易解决的方向变动，而且办事的阻碍要比以往大很多，因此建议保持一颗平常心，切记心浮气躁。<br />\r\n&nbsp;<br />\r\n<strong>1960年属鼠人2015年<u>爱情运势</u>：</strong><br />\r\n&nbsp;<br />\r\n庚子年生属鼠人在乙未年要谨防桃花煞和桃花劫的袭来，此年桃花运尚可，但是成功率不是很高，而且容易伤财，要谨慎交友，不要被眼前的现象欺骗，尤其是女性朋友，过多的桃花不但不会增加婚运，反而让你麻烦事更多，甚至引起不必要的误会与纷争。男性朋友在工作中也会遇到心仪的女性，如果想表白最好是在运势好的月份。<br />\r\n&nbsp;<br />\r\n<strong>1960年出生的属鼠人2015年健康运程：</strong><br />\r\n&nbsp;<br />\r\n乙未年【太阴】化忌，因此【太阴】所代表的的人体的器官都要小心的养护，肾脏，泌尿系统，尤其是女性妇科，切记不能掉以轻心，如果在此年犯病，很不容易治愈。其次丑未相害，对于脾胃伤害也是很大，建议平时多锻炼，女性也可多吃滋补养颜的食品，少生气少上火，若肝火过旺则容易克制未土，因此每天保持一个好的心情最为重要。<br />\r\n\r\n<div class=\"adb adb_3\"></div>\r\n\r\n</div>\r\n<div class=\"pagelist\"></div>','1','0','0','0','2015-08-31 17:36:33','0');");
