E_D("DROP TABLE IF EXISTS `xingzuo_yunshi_data`;");
E_C("CREATE TABLE `xingzuo_yunshi_data` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(250) NOT NULL COMMENT '标题',
  `dsc` text NOT NULL COMMENT '描述',
  `time` varchar(250) NOT NULL COMMENT '时间',
  `more` text NOT NULL COMMENT '更多',
  `uptime` varchar(50) NOT NULL COMMENT '采集时间',
  `tid` int(11) NOT NULL COMMENT '栏目id',
  `img` varchar(250) NOT NULL COMMENT '缩略图',
  `jintian` longtext NOT NULL COMMENT '今日运势',
  `mingtian` longtext NOT NULL COMMENT '明天运势',
  `zhou` longtext NOT NULL COMMENT '本周运势',
  `yue` longtext NOT NULL COMMENT '月度运势',
  `nian` longtext NOT NULL COMMENT '年度运势',
  `love` text NOT NULL COMMENT '爱情运势',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=18 DEFAULT CHARSET=utf8");
E_D("replace into `xingzuo_yunshi_data` values('6','白羊座','白羊座的人乐观活泼、敢做敢当，干劲十足，属于剑及履及的行动派，是急行侠、探险家。但有时显得没有耐性，冲动易怒，太过冒险。','3月21日-4月20日','<ul class=\"clearfix\">   <br /><br /><br /><br /><br />\r\n				<li>关键词：自己</li><br /><br /><br /><br /><br />\r\n				<li>象　　征：公羊</li><br /><br /><br /><br /><br />\r\n				<li>四象性：火</li><br /><br /><br /><br /><br />\r\n				<li>掌管宫位：第一宫</li><br /><br /><br /><br /><br />\r\n<br /><br /><br /><br /><br />\r\n				<li>阴阳性：阳性</li><br /><br /><br /><br /><br />\r\n				<li>最大特征：控制</li><br /><br /><br /><br /><br />\r\n				<li>主管星：火星</li><br /><br /><br /><br /><br />\r\n				<li>颜　　色：红色</li><br /><br /><br /><br /><br />\r\n				<li>三方官：基本</li><br /><br /><br /><br /><br />\r\n				<li>掌管身体：头部</li><br /><br /><br /><br /><br />\r\n				<li>珠　宝：红宝石</li><br /><br /><br /><br /><br />\r\n				<li>幸运号码：5</li><br /><br /><br /><br /><br />\r\n				<li>金　属：铁</li><br /><br /><br /><br /><br />\r\n				<li>金　　句：我最大</li><br /><br /><br /><br /><br />\r\n			</ul>','2016-11-13 10:23:29','401','/static/upload/2016/xingzuo_yunshi_data_6_img.png','{\"date\":\"2016-11-13\",\"index\":{\"zonghe\":\"72\",\"aiqing\":\"75\",\"gongzuo\":\"72\",\"caiyun\":\"62\",\"jiankang\":\"79\",\"kaiyunfangwei\":\"正东\",\"xingyunshuzi\":\"6\",\"yanse\":\"白色\"},\"content\":\"合作会为你带来好运。这两天的气势较弱，加入处于强势的团队是增强你气势的好办法。不妨主动询问别人有没有需要帮忙的地方，你有能力溶入这个群体的。 \",\"img\":\"/up_img/xingzuoyunshi/201611/97t9N8n2G77.jpg\",\"yi\":\"宜签合同，宜读古书，宜回家吃饭\",\"ji\":\"忌花间一壶酒，忌回忆怀旧，忌购物\",\"shorttxt\":\"白羊座2016年11月13日整体运势\"}','{\"date\":\"2016-11-14\",\"index\":{\"zonghe\":\"68\",\"aiqing\":\"72\",\"gongzuo\":\"68\",\"caiyun\":\"58\",\"jiankang\":\"76\",\"kaiyunfangwei\":\"东南\",\"xingyunshuzi\":\"7\",\"yanse\":\"绿色\"},\"content\":\"好胜心的驱使下表现自我的意识变得强烈。为了博取旁人的认知而战斗，但贪心不足蜡烛两头烧的结果，却反而使你两边不讨好，假手他人也一样不会有太好的结果。凡事不要要求百分百，这两天不妨让自己得个60分就好。其实只要不贪心，专心在一个目标上应该获得的成果还不错的。 \",\"img\":\"/up_img/xingzuoyunshi/201611/376W08YW7jz.jpg\",\"yi\":\"宜野炊，宜午饭少吃，宜订机票\",\"ji\":\"忌问路，忌打扫卫生，忌骑自行车\",\"shorttxt\":\"白羊座2016年11月14日整体运势\"}','{\"date\":\"2016-11-13~2016-11-19\",\"index\":[{\"title\":\"整体运势\",\"star\":\"3\"},{\"title\":\"爱情运势\",\"star\":\"3\"},{\"title\":\"事业学业\",\"star\":\"4\"},{\"title\":\"财富运势\",\"star\":\"3\"},{\"title\":\"健康运势\",\"star\":\"3\"},{\"title\":\"幸运颜色\",\"value\":\"银白色\"},{\"title\":\"幸运星座\",\"value\":\"水瓶座\"},{\"title\":\"提防星座\",\"value\":\"金牛座\"}],\"content\":\"要是这周需要出行出差会易有临时状况，请记住一定要提前做好准备工作，以备不时之需。另外要是工作上有合作事宜，就要多跟合作伙伴沟通交流，仔细核对细节。其实本周大多的精力和时间都花费在旅行、学习、管理涉外事务上面，奔波劳碌是平常事，身体、精神上都会感到很疲倦，可要注意休息了。财务方面可能会有一些交通罚款或是信用卡欠单，那些关于合作的资金要跟对方洽谈好，双方尽量减免发生财务纠纷。\",\"url\":\"http://www.xzw.com/fortune/aries/2.html\",\"m_url\":\"http://m.xzw.com/fortune/aries/2.html\"}','{\"date\":\"2016-11-01~2016-11-30\",\"index\":[{\"title\":\"整体运势\",\"star\":\"4\"},{\"title\":\"爱情运势\",\"star\":\"4\"},{\"title\":\"事业学业\",\"star\":\"3\"},{\"title\":\"财富运势\",\"star\":\"3\"}],\"content\":\"来到11月份，羊羊整体运势很平顺，没有太多的波折。爱情迎来美好的桃花运，但最好平常心对待，不可强求，部分人或许会有一见钟情的恋爱发生。事业上小麻烦肯定有，只要能够冷静应对必能找到解决方法。下半月工作计划会有变动，做了许多的无用功，别急着抱怨，赶紧转变策略就好。好的财运带来好的心情，偶尔的朋友聚会活动，其中也有一笔不少的支出。\",\"url\":\"http://www.xzw.com/fortune/aries/3.html\",\"m_url\":\"http://m.xzw.com/fortune/aries/3.html\"}','{\"date\":\"2016-01-01~2016-12-31\",\"index\":[{\"title\":\"整体运势\",\"star\":\"4\"},{\"title\":\"爱情运势\",\"star\":\"4\"},{\"title\":\"事业学业\",\"star\":\"3\"},{\"title\":\"财富运势\",\"star\":\"3\"}],\"content\":\"在2016年，白羊座将会面临更多的挑战，上半年的困扰主要来人际或是环境变化，某些事情需要你做出抉择。来到下半年，职场与朋友圈中的社交活动多，无形中增加了开销，会有让你烦心的事情发生，而其中一大部分是跟人有关联。人的困扰左右你一整年的心情起伏，至于时间长短那就因人而异了，期间会有一些不可抗拒的压力存在，顺其自然看待地去看待每件事情吧，那么形势也会朝着好的方向发展，会慢慢走出低迷的状态，找到新希望。\",\"zt\":\"在2016年，白羊座将会面临更多的挑战，上半年的困扰主要来人际或是环境变化，某些事情需要你做出抉择。来到下半年，职场与朋友圈中的社交活动多，无形中增加了开销，会有让你烦心的事情发生，而其中一大部分是跟人有关联。人的困扰左右你一整年的心情起伏，至于时间长短那就因人而异了，期间会有一些不可抗拒的压力存在，顺其自然看待地去看待每件事情吧，那么形势也会朝着好的方向发展，会慢慢走出低迷的状态，找到新希望。\",\"aiqing\":\"感情上感动积极一些，会有好事发生。单身者可多留意职场异性，多参加聚会表现个人魅力；恋爱中的人到了开花结果的好时机。\",\"shiye\":\"工作上非常忙碌，容易陷入瓶颈当中，下半年会有部分人因不能充分施展才华而萌生跳槽的想法。\",\"caifu\":\"整体财运比较平稳，收支平衡，而且还有一笔不错的财富积蓄，可有一些尚未还清的债务，还是建议节俭一点较好。\",\"jiankang\":\"健康状态和体力下滑，睡眠质量欠佳，需要调整好作息时间，同时外出需要注意交通安全事项。\"}','{\"zh\":\"虽然今年的感情运势大多数时候都不太顺利，但是在年中之后的感情运势还是很不错的。不管是你和喜欢的人之间的关系会得到提升，而且两个人之间会有比以往更多的相处机会。如果已经有了交往对象，两个人的感情生活会趋向稳定。\",\"nan\":\"在上半年，你的感情运不太稳定。由于你的脾气太过于冲动，做事情或是说话的时候经常忘记考虑另一半的感受，所以两个人的感情才会出现裂痕。不过下半年的感情运会逐渐趋向于稳定，两个人的感情也会越来越好。平时多给对方一些温柔和耐心，两个人才能够走得更加长远。如果你是单身汉，那么你会收到不少聚会邀请，你的桃花运非常旺盛。\",\"nv\":\"在年中你的爱情运很不错哦，只是会因为太过心急而没有好好把握好机会。人际关系会因为工作忙碌而和某个人接触频繁，非常容易和对方产生革命情谊，逐渐发现对方的魅力，两个人的关系会逐渐升温。但如果觉得现在的自己可能不太适合过两个人的生活，而且目前也没有遇到积极地追求自己的对象的话，一切都不必太过勉强。\"}');");
E_D("replace into `xingzuo_yunshi_data` values('7','金牛座','金牛座的人给人的感觉稳重、务实，追求稳定与和谐，害怕变动，属于享受派。喜欢安定，最怕没有安全感。但有时显得固执己见，对钱财看得很重。','4月21日-5月21日','<ul class=\"clearfix\">   <br />\r\n				<li>关键词：金钱</li><br />\r\n				<li>象　　征：公牛</li><br />\r\n				<li>四象性：土</li><br />\r\n				<li>掌管宫位：第二宫</li><br />\r\n<br />\r\n				<li>阴阳性：阴性</li><br />\r\n				<li>最大特征：财富</li><br />\r\n				<li>主管星：金星</li><br />\r\n				<li>颜　　色：粉色</li><br />\r\n				<li>三方官：固定</li><br />\r\n				<li>掌管身体：颈项和喉咙</li><br />\r\n				<li>珠　宝：翡翠、玉</li><br />\r\n				<li>幸运号码：6</li><br />\r\n				<li>金　属：木</li><br />\r\n				<li>金　　句：有钱我最大</li><br />\r\n			</ul>','2016-11-07 9:35:53','402','/static/upload/2016/xingzuo_yunshi_data_7_img.png','{\"date\":\"2016-11-7\",\"index\":{\"zonghe\":\"86\",\"aiqing\":\"90\",\"gongzuo\":\"86\",\"caiyun\":\"76\",\"jiankang\":\"94\",\"kaiyunfangwei\":\"正北\",\"xingyunshuzi\":\"2\",\"yanse\":\"黄色\"},\"content\":\"这两天容易因自身的情绪起伏而容易与同事沟通不良，不过爱情运势不错，多多参与社交应酬，社团活动，可以从中遇到还不错的桃花对象。与人合作的企划案件须要配合对方而制定程序计划，或受他人影响而延误原有的进度，在烦忙的工作中小小偷闲。 \",\"img\":\"/up_img/xingzuoyunshi/jinniu/201611/l9Hb13032s3.jpg\",\"yi\":\"宜表演魔术，宜独处，宜致谢\",\"ji\":\"忌独自饮酒，忌猜谜，忌拍人肩膀\",\"shorttxt\":\"金牛座2016年11月7日整体运势\"}','{\"date\":\"2016-11-8\",\"index\":{\"zonghe\":\"67\",\"aiqing\":\"71\",\"gongzuo\":\"67\",\"caiyun\":\"57\",\"jiankang\":\"75\",\"kaiyunfangwei\":\"正南\",\"xingyunshuzi\":\"1\",\"yanse\":\"蓝色\"},\"content\":\"个人运气的力量转弱，不得不重视整体协调性的一天。即使错失个人表现的机会，这两天还是得忍耐着配合大家。还要注意对一张快嘴要谨慎一点，不小心说漏别人的秘密或是说了不该说的话，就算当时没有恶意，但事后将陆续发生问题的可能哟。爱情方面也是一样与其主动照顾对方还不如静待对方来关爱，这两天就被动一些吧。 \",\"img\":\"/up_img/xingzuoyunshi/jinniu/201611/f166G2p1t5v.jpg\",\"yi\":\"宜出游，宜搭顺风车，宜细心\",\"ji\":\"忌穿鲜艳袜子，忌登记结婚，忌爬山\",\"shorttxt\":\"金牛座2016年11月8日整体运势\"}','{\"date\":\"2016-11-06~2016-11-12\",\"index\":[{\"title\":\"整体运势\",\"star\":\"4\"},{\"title\":\"爱情运势\",\"star\":\"4\"},{\"title\":\"事业学业\",\"star\":\"3\"},{\"title\":\"财富运势\",\"star\":\"3\"},{\"title\":\"健康运势\",\"star\":\"4\"},{\"title\":\"幸运颜色\",\"value\":\"黑色\"},{\"title\":\"幸运星座\",\"value\":\"双子座\"},{\"title\":\"提防星座\",\"value\":\"狮子座\"}],\"content\":\"对你来说最糟糕的时期已经过去了，这周运势将会得到大大的改善，之前萦绕着你的烦心事能够告一段落，终于可以松一口气了。但话虽如此，还是不能掉以轻心，其实只是解决了表面，实质性的问题依然存在，而且在接下来有很多的新项目，必须要提高信心，做好迎战准备。这段时间会慢慢忙碌起来的，也许你会需要一个得力助手帮忙。家庭生活或是出行可能会产生一些相关费用，支出增多。\",\"url\":\"http://www.xzw.com/fortune/taurus/2.html\",\"m_url\":\"http://m.xzw.com/fortune/taurus/2.html\"}','{\"date\":\"2016-11-01~2016-11-30\",\"index\":[{\"title\":\"整体运势\",\"star\":\"3\"},{\"title\":\"爱情运势\",\"star\":\"4\"},{\"title\":\"事业学业\",\"star\":\"4\"},{\"title\":\"财富运势\",\"star\":\"3\"}],\"content\":\"这个月工作缺乏创新能力，很多感觉没办法摸准，事业受到很大的影响，但是很快能够恢复动力，调整状态，面对挑战毫不畏惧。这段时间风险投资要谨慎，只买对的不买贵的，切忌贪心跟风，见好就收才能成功。已婚者要注意外面灯红酒绿的诱惑生活，不要影响到婚姻，另外很多人感情上处于被动状态，只能眼睁睁看着幸福在眼前溜走。\",\"url\":\"http://www.xzw.com/fortune/taurus/3.html\",\"m_url\":\"http://m.xzw.com/fortune/taurus/3.html\"}','{\"date\":\"2016-01-01~2016-12-31\",\"index\":[{\"title\":\"整体运势\",\"star\":\"4\"},{\"title\":\"爱情运势\",\"star\":\"4\"},{\"title\":\"事业学业\",\"star\":\"3\"},{\"title\":\"财富运势\",\"star\":\"3\"}],\"content\":\"在2016年，白羊座将会面临更多的挑战，上半年的困扰主要来人际或是环境变化，某些事情需要你做出抉择。来到下半年，职场与朋友圈中的社交活动多，无形中增加了开销，会有让你烦心的事情发生，而其中一大部分是跟人有关联。人的困扰左右你一整年的心情起伏，至于时间长短那就因人而异了，期间会有一些不可抗拒的压力存在，顺其自然看待地去看待每件事情吧，那么形势也会朝着好的方向发展，会慢慢走出低迷的状态，找到新希望。\",\"zt\":\"2016年金牛座上半年的运势比较旺盛，下半年就相对逊色了不少。虽然有一些困扰是你在上半年想要尽快摆脱的，可如果能冷静理智应对的话，结果还是令人非常满意的。工作上表现突出，办事能力获肯定，也能尝试许多新挑战，生活体验更加有趣。下半年事情渐渐开始变得多了，也日渐忙碌起来，关于人的困扰也增多了，这种情况在5-6月间会开始有征兆出现，其中一部分人的人际困扰相对比较严重，需要做好关系调整。\",\"aiqing\":\"感情生活美好，有不少的感动时刻。单身者人缘大发，桃花旺盛，有很好的爱情机缘；恋爱中的人生活稍显寡淡，其实这种简单也是种幸福。\",\"shiye\":\"想要在今年事业上有新的突破，就要多为自己争取表现机会，利用积累的人脉，下半年琐事比较多，需要更加用心去处理。\",\"caifu\":\"财运方面，上半年有不错的加薪机会，收入丰厚，下半年适合投资，但是需谨慎对待，小心被骗，而偏财运则一般。\",\"jiankang\":\"健康运势欠佳，身体抵抗力下降，会有一些小病找上你。建议定时做身体检查，以防有病痛隐患。\"}','{\"zh\":\"总得来说，今年的爱情运势是先强后弱的。在上半年你的桃花运非常旺盛，单身的你特别容易引起他人的关注。但如果和另一半的沟通停留在表层，下半年可能会使婚恋生活可能会失去活力。此外，尘封已久的感情问题今年将会得到解决。\",\"nv\":\"你在生活领域方面表现得很不错，单身的你外出活动的时候，你会特别容易受到异性的关注哦！已经和爱人组建家庭的你会有想要生宝宝的念头，令人感到开心的是今年怀上宝宝的机率很大哦！虽然可能会和另一半相处的时候发生摩擦，但只要两个人能够坦诚相对，好好地和对方沟通的话，那么一切问题都会迎刃而解了。\",\"nan\":\"爱情的到来会让你做任何事情都充满了动力，不过在投入一段感情之前要深思熟虑对方是否就是你一直寻找的人。在确认之后，最好细想一下自己需要为了这段感情付出怎样的努力。如果这段感情会改变你的生活，你是不是有足够的勇气去接受？如果和另一半感情上出现了问题，今年很适宜正视问题，并且努力去修补。\"}');");
E_D("replace into `xingzuo_yunshi_data` values('8','双子座','双子座的人，思维跳跃，口才伶俐，鬼点子多，常语出惊人，但有三心二意的倾向，情绪起伏多变，难以专心。','5月22日-6月21日','<ul class=\"clearfix\">   <br />\r\n			<li>关键词：沟通</li><br />\r\n			<li>象　　征：孪生子</li><br />\r\n			<li>四象性：风</li><br />\r\n			<li>掌管宫位：第三宫</li><br />\r\n			<li>阴阳性：阳性</li><br />\r\n			<li>最大特征：思想</li><br />\r\n			<li>主管星：水星</li><br />\r\n			<li>颜　　色：黄色</li><br />\r\n			<li>三方官：变动</li><br />\r\n			<li>掌管身体：肩膊、手、肺</li><br />\r\n			<li>珠　宝：猫眼石</li><br />\r\n			<li>幸运号码：7</li><br />\r\n			<li>金　属：水银</li><br />\r\n			<li>金句：一人计短二人计长</li><br />\r\n		</ul>','2016-11-09 17:16:13','403','/static/upload/2016/xingzuo_yunshi_data_8_img.png','{\"date\":\"2016-11-9\",\"index\":{\"zonghe\":\"71\",\"aiqing\":\"64\",\"gongzuo\":\"60\",\"caiyun\":\"95\",\"jiankang\":\"68\",\"kaiyunfangwei\":\"正北\",\"xingyunshuzi\":\"10\",\"yanse\":\"黄色\"},\"content\":\"郁闷的状况要学会自娱自乐才是！越是长大的你，越容易在这两天觉得自己不快乐，或是工作/学业或是学习的压力让你快要喘不过气来！就连人生的意义，这两天也会被你怀疑！要学会自己给自己松绑，下班放学的时候送自己一份可爱的小礼物算是稿赏吧，或者做点别的让自己觉得开心的事情，人活着就是为“快乐”二字！ \",\"img\":\"/up_img/xingzuoyunshi/jinniu/201611/4568hZ37411.jpg\",\"yi\":\"宜微笑，宜充公交卡，宜查黄历\",\"ji\":\"忌苦中作乐，忌收拾旧物，忌庆祝生日\",\"shorttxt\":\"双子座2016年11月9日整体运势\"}','{\"date\":\"2016-11-10\",\"index\":{\"zonghe\":\"83\",\"aiqing\":\"87\",\"gongzuo\":\"83\",\"caiyun\":\"73\",\"jiankang\":\"91\",\"kaiyunfangwei\":\"西北\",\"xingyunshuzi\":\"10\",\"yanse\":\"黑色\"},\"content\":\"牵绊人际关系的那条无形的线脆弱易断裂。精神感官上令人感到有些神经质、难以相处的倾向。在别人眼中视为无聊小事而你却特别在意，紧绷的神经它人如果不小心碰到了就一苹刺猬般张起保卫防护罩，注意因此在人际关系上出现裂痕的可能。还要注意在职场中与部下、同事间相处较为疏远的情况。 \",\"img\":\"/up_img/xingzuoyunshi/jinniu/201611/91Gnpt110Q6.jpg\",\"yi\":\"宜吃水果糖，宜面试，宜买股票\",\"ji\":\"忌花间一壶酒，忌相信未来，忌出尔反尔\",\"shorttxt\":\"双子座2016年11月10日整体运势\"}','{\"date\":\"2016-11-06~2016-11-12\",\"index\":[{\"title\":\"整体运势\",\"star\":\"3\"},{\"title\":\"爱情运势\",\"star\":\"3\"},{\"title\":\"事业学业\",\"star\":\"3\"},{\"title\":\"财富运势\",\"star\":\"3\"},{\"title\":\"健康运势\",\"star\":\"4\"},{\"title\":\"幸运颜色\",\"value\":\"紫色\"},{\"title\":\"幸运星座\",\"value\":\"双子座\"},{\"title\":\"提防星座\",\"value\":\"金牛座\"}],\"content\":\"这一周适合做长远规划，因为接下来可能会有较多的于外出，例如远行、休假，而且你最近的重点大多都落在他人身上，时间都被别人占用，很少有时间做自己的事情，那倒不如为往后的事情做做准备咯。个人的价值观也因此容易被他人影响。工作上还是琐碎事比较多，还有些许的变动，可能是人事上的变化。本周要特别注意身体，健康状况反复不定。\",\"url\":\"http://www.xzw.com/fortune/gemini/2.html\",\"m_url\":\"http://m.xzw.com/fortune/gemini/2.html\"}','{\"date\":\"2016-11-01~2016-11-30\",\"index\":[{\"title\":\"整体运势\",\"star\":\"4\"},{\"title\":\"爱情运势\",\"star\":\"3\"},{\"title\":\"事业学业\",\"star\":\"4\"},{\"title\":\"财富运势\",\"star\":\"3\"}],\"content\":\"这个月双子一直忙碌于工作，确实没多少时间管理生活或是感情事，甚至属于照顾家人伴侣。可以送一些小礼物能够起到安抚的作用，或是忙中挤一点时间出来跟对方吃吃饭，相聚时光有限，必须好好珍惜。之前定下的目标本月可以实现，很多的想法方案都得到认同。投资眼光精确，回报大，月初和月中工作上也有令人满意的奖励。\",\"url\":\"http://www.xzw.com/fortune/gemini/3.html\",\"m_url\":\"http://m.xzw.com/fortune/gemini/3.html\"}','{\"date\":\"2016-01-01~2016-12-31\",\"index\":[{\"title\":\"整体运势\",\"star\":\"3\"},{\"title\":\"爱情运势\",\"star\":\"3\"},{\"title\":\"事业学业\",\"star\":\"3\"},{\"title\":\"财富运势\",\"star\":\"2\"}],\"content\":\"双子座在2016年是下半年运势要比上半年的要来得好些许。在上半年你的烦心的事情多、人际关系困扰多，但为了顾全大局，这些你都得先忍一忍了，虽然有时候心里感觉不舒服，可是忍耐后对自己有好处。到了下半年，事业新体验比较多，工作表现受肯定，工作环境正在酝酿着改变，将会有一番新景象。在半年会跟某些人的距离越来越远，下半年或许不是一帆风顺，不过相较于上半年相比情况已经大有改善了，要求别太高，要懂得知足。\",\"zt\":\"双子座在2016年是下半年运势要比上半年的要来得好些许。在上半年你的烦心的事情多、人际关系困扰多，但为了顾全大局，这些你都得先忍一忍了，虽然有时候心里感觉不舒服，可是忍耐后对自己有好处。到了下半年，事业新体验比较多，工作表现受肯定，工作环境正在酝酿着改变，将会有一番新景象。在半年会跟某些人的距离越来越远，下半年或许不是一帆风顺，不过相较于上半年相比情况已经大有改善了，要求别太高，要懂得知足。\",\"aiqing\":\"今年要维持爱情不变比较困难，有伴者与另一半争吵矛盾不断；而单身者感情运势平淡，但到了下半年运势回升，会有好桃花出现。\",\"shiye\":\"事业上今年没有太大的上升空间，甚至给你一种原地踏步的感觉，算是不好也不坏。职场人际关系需要处理好。\",\"caifu\":\"财运状况动荡不稳，上半年的花销比较多，一不留神钱就哗哗地花出去了，聚财比较难，购物欲望需要控制好。\",\"jiankang\":\"健康上要注意过度疲劳，睡眠时间比较少，职场人士易出现亚健康状况，要懂得释放压力。\"}','{\"zh\":\"单身的你很有可能会在职场中与某个异性擦出火花，因为你的职场桃花运非常旺盛。此外，你的恋人或是贵人都有可能出现在职场上。上半年会接触很多异性，下半年和某个异性在一起的机会非常大，并且还是你喜欢的类型。\",\"nv\":\"你的另一半可能要面对很多的事情，非常地需要你的体谅和理解。平日里很难静下心来的你，今年不但要静心，还要对另一半充满耐心，另一半会因此而对你非常地感激不已。虽然还是会因为一些负面的情绪和另一半有些小吵小闹，但基本上都不会伤及双方的感情。反而两个人之间的感情一直在升温，这是非常令人羡慕的。\",\"nan\":\"在做事方面会受到不少的肯定，人缘也越来越好。最令人感到兴奋的是，你会认识到条件非常不错的异性哦，并且还会与对方有愉快的来往。多参加一些聚会和活动，改变一下造型，你的桃花运会越来越旺盛哦。精神抖数的你会给对方留下深刻的印象，收起你的玩心，认真地和对方谈谈的话，会有相当不错的回报。\"}');");
E_D("replace into `xingzuo_yunshi_data` values('9','巨蟹座','巨蟹座的人，亲切有礼，感情丰富、细腻，有很强的感受力，具有母性的博爱之心，属于居家派的。但情绪起伏，有逃避倾向。','6月22日-7月22日','<ul class=\"clearfix\"> <br />\r\n			<li>关键词：家庭</li><br />\r\n			<li>象　　征：蟹</li><br />\r\n			<li>四象性：水</li><br />\r\n			<li>掌管宫位：第四宫</li><br />\r\n			<li>阴阳性：阴性</li><br />\r\n			<li>最大特征：感觉</li><br />\r\n			<li>主管星：月亮</li><br />\r\n			<li>颜　　色：绿色</li><br />\r\n			<li>三方官：基本</li><br />\r\n			<li>掌管身体：胃 子宫 胸 消化</li><br />\r\n			<li>珠　宝：珍珠</li><br />\r\n			<li>幸运号码：2</li><br />\r\n			<li>金　属：金</li><br />\r\n			<li>金　　句：我妈咪！</li><br />\r\n		</ul>','2016-11-09 17:16:17','404','/static/upload/2016/xingzuo_yunshi_data_9_img.png','{\"date\":\"2016-11-9\",\"index\":{\"zonghe\":\"77\",\"aiqing\":\"81\",\"gongzuo\":\"77\",\"caiyun\":\"67\",\"jiankang\":\"85\",\"kaiyunfangwei\":\"正东\",\"xingyunshuzi\":\"7\",\"yanse\":\"白色\"},\"content\":\"这两天是你来说可是锐不可当的一天，气势十分强盛，延续昨天的好运气这两天的你更是火力全开，不妨趁这两天把过去未完成的事完成吧!让将来即将开展的事更加顺利。感情运让你有桃花绽放喜悦感，但要注意的是过于刺激火热也可能让人吃不消。 \",\"img\":\"/up_img/xingzuoyunshi/jinniu/201611/574Y8L5lQ2R.jpg\",\"yi\":\"宜发红包，宜面试，宜看电影\",\"ji\":\"忌调情，忌远离手机，忌手挽手\",\"shorttxt\":\"巨蟹座2016年11月9日整体运势\"}','{\"date\":\"2016-11-10\",\"index\":{\"zonghe\":\"75\",\"aiqing\":\"79\",\"gongzuo\":\"75\",\"caiyun\":\"65\",\"jiankang\":\"83\",\"kaiyunfangwei\":\"正东\",\"xingyunshuzi\":\"5\",\"yanse\":\"白色\"},\"content\":\"天生拥有的旺盛服务精神，在这两天将有为你唤来幸运的机会哟。看到烦恼、孤单的人温柔地上前说几句安慰的言词。虽然不见得有实质帮助，但在这些小地方的细心不仅让人感觉温暖，还能因此累积人气。而注目度UP的这两天，如果能再加上一点不抢功的心态那就更好了。 \",\"img\":\"/up_img/xingzuoyunshi/jinniu/201611/u3I2S70A705.jpg\",\"yi\":\"宜运动，宜庆祝生日，宜多喝水\",\"ji\":\"忌坦诚相对，忌逛眼镜店，忌独自饮酒\",\"shorttxt\":\"巨蟹座2016年11月10日整体运势\"}','{\"date\":\"2016-11-06~2016-11-12\",\"index\":[{\"title\":\"整体运势\",\"star\":\"4\"},{\"title\":\"爱情运势\",\"star\":\"4\"},{\"title\":\"事业学业\",\"star\":\"3\"},{\"title\":\"财富运势\",\"star\":\"3\"},{\"title\":\"健康运势\",\"star\":\"4\"},{\"title\":\"幸运颜色\",\"value\":\"蓝色\"},{\"title\":\"幸运星座\",\"value\":\"水瓶座\"},{\"title\":\"提防星座\",\"value\":\"天蝎座\"}],\"content\":\"这周开始就要注意健康状况了，心态上的变化也要留意，否则会影响到工作运势。最近做事不要横冲直撞，要是感到棘手的话就情人帮忙或是先放一放，逞强只会铸成大错，另外与客户之间要注意沟通好。很多时候你还是需要他人的鼓励，以此来提升自信和斗志。社交运势变好，但某些互动过程会有不愉快发生，或是与人才钱财纠纷。\",\"url\":\"http://www.xzw.com/fortune/cancer/2.html\",\"m_url\":\"http://m.xzw.com/fortune/cancer/2.html\"}','{\"date\":\"2016-11-01~2016-11-30\",\"index\":[{\"title\":\"整体运势\",\"star\":\"3\"},{\"title\":\"爱情运势\",\"star\":\"4\"},{\"title\":\"事业学业\",\"star\":\"3\"},{\"title\":\"财富运势\",\"star\":\"3\"}],\"content\":\"总体来说运势一般般，压力山大，需要身边人给予帮助和支持。很想要做到最好，可是想太多反而效率更差，给工作带来更多的麻烦。若是这个月有时间，不妨多进修，充充电，提升专业技能，同时也提高自己的个人形象。财运一直处于低迷状态，需要做好储蓄工作，建议少碰赌博，要不然钱财有去无回。本月适合来一场旅行，可以跟朋友、家人或是伴侣一起同行。\",\"url\":\"http://www.xzw.com/fortune/cancer/3.html\",\"m_url\":\"http://m.xzw.com/fortune/cancer/3.html\"}','{\"date\":\"2016-01-01~2016-12-31\",\"index\":[{\"title\":\"整体运势\",\"star\":\"4\"},{\"title\":\"爱情运势\",\"star\":\"3\"},{\"title\":\"事业学业\",\"star\":\"3\"},{\"title\":\"财富运势\",\"star\":\"3\"}],\"content\":\"2016年上半年巨蟹座有些事情需要你重复去处理，而且都是蛮重要的，也花去不少的时间，等处理完毕就会轻松很多。下半年则是各种事情一并袭来，这里那里都有不少事情，但性质跟上半年的不一样，做起来相对轻松不少。上半年和下半年都有外出的机会，5月上旬到9月中旬是你出行高峰期，可以出外旅游。9月中旬过后，生活、工作、爱情都会过得比较低调，但办事的情绪是比较高涨的，会有新生事物的到来，会有一个新局面。\",\"zt\":\"2016年上半年巨蟹座有些事情需要你重复去处理，而且都是蛮重要的，也花去不少的时间，等处理完毕就会轻松很多。下半年则是各种事情一并袭来，这里那里都有不少事情，但性质跟上半年的不一样，做起来相对轻松不少。上半年和下半年都有外出的机会，5月上旬到9月中旬是你出行高峰期，可以出外旅游。9月中旬过后，生活、工作、爱情都会过得比较低调，但办事的情绪是比较高涨的，会有新生事物的到来，会有一个新局面。\",\"aiqing\":\"单身者今年的桃花数量不少，但要小心选择，心急吃不了热豆腐；恋爱中的人上半年感情运势平稳，但下半年波折较多。\",\"shiye\":\"事业发展方向变动较快，计划遭到阻拦，压力比较大，同时要小心小人作祟，行为举止低调为佳。\",\"caifu\":\"今年的事业上的进账较少，所幸也没有太大的支出，偏财运较好，可尝试做投资，会有不错的收获。\",\"jiankang\":\"健康上没什么大病出现，但会有小病痛肆虐，需要通过多运动锻炼身体，提高身体免疫力。\"}','{\"zh\":\"由于人际圈的改变和环境的变动，你会感觉自己处在不安与彷徨的恋爱氛围当中。不管是单身的你还是已经有伴的你，在人生的道路上要更加地努力才会让自己和他人感到满意。此外，在年初脱单的机会很大哦。\",\"nv\":\"拓宽自己的人际关系圈和朋友圈，你的桃花运会越来越好。你应该多交一些不同类型和性格的朋友，这样对你的工作和感情方面都会有所帮助的。在感情上，你要做好轻拿轻放的心理准备的，让时间来为自己证明这段感情是经得起考验的。也许你对另一半或是对方的亲朋戚友的容忍次数会变得越来越多，甚至可能产生逃离这段关系的想法。\",\"nan\":\"你不但不害怕自己没有伴，反而清楚地知道自己有需要完成的事情。虽然上半年可能会与喜欢的人缘分比较浅，但没有关系，因为下半年你会有很多的机会去接近喜欢的人，并且和对方的关系会变得越来越明朗。这是非常值得高兴的事情，任何事情没有结束之前你都不会知道会有什么惊喜等待着自己去发掘。\"}');");
E_D("replace into `xingzuo_yunshi_data` values('10','狮子座','狮子座的人，自信自强，气度非凡，极具领导统御能力，永不服输，是天生的贵族、王者。只是有时过度追求利，甚至有些自负，莽撞、以自我为中心。','7月23日-8月22日','<ul class=\"clearfix\">  <br />\r\n			<li>关键词：乐趣</li><br />\r\n			<li>象　　征：狮子</li><br />\r\n			<li>四象性：火</li><br />\r\n			<li>掌管宫位：第五宫</li><br />\r\n			<li>阴阳性：阳性</li><br />\r\n			<li>最大特征：权力</li><br />\r\n			<li>主管星：太阳</li><br />\r\n			<li>颜　　色：红色、金色、黄色</li><br />\r\n			<li>三方官：固定</li><br />\r\n			<li>掌管身体：心脏、肾部、背脊</li><br />\r\n			<li>珠　宝：琥珀</li><br />\r\n			<li>幸运号码：19</li><br />\r\n			<li>金　属：黄金</li><br />\r\n			<li>金　　句：我最大</li><br />\r\n		</ul>','2016-11-02 18:05:19','405','/static/upload/2016/xingzuo_yunshi_data_10_img.png','{\"date\":\"2016-11-2\",\"index\":{\"zonghe\":\"90\",\"aiqing\":\"94\",\"gongzuo\":\"90\",\"caiyun\":\"80\",\"jiankang\":\"98\",\"kaiyunfangwei\":\"正西\",\"xingyunshuzi\":\"3\",\"yanse\":\"橙色\"},\"content\":\"对自己的感觉非常良好哦！越是在人气旺盛的场合，你这种自信会愈加膨胀，有时你甚至会觉得就是舞会中那位高傲的公主！同龄的同性，是这两天最排斥你的人。你的自信，让他们觉得受伤！适当低调一点，你的处境也会好过一点。 \",\"img\":\"/up_img/xingzuoyunshi/jinniu/201611/o7A0j7973Iv.jpg\",\"yi\":\"宜逃课，宜驾驶汽车，宜读诗饮酒\",\"ji\":\"忌偏执，忌还信用卡，忌不吃晚饭\",\"shorttxt\":\"狮子座2016年11月2日整体运势\"}','{\"date\":\"2016-11-3\",\"index\":{\"zonghe\":\"68\",\"aiqing\":\"71\",\"gongzuo\":\"68\",\"caiyun\":\"58\",\"jiankang\":\"76\",\"kaiyunfangwei\":\"正南\",\"xingyunshuzi\":\"8\",\"yanse\":\"蓝色\"},\"content\":\"这两天的你要注意有被诽谤的可能，尤其在工作场合中，虽然你只是认真的处理自己分内的事情，却没来由的被他人质疑自己在工作里的表现如何，着时令人错愕。如果因为这样刺激你计划着人生的下一个目标，也可以说是个不错的时机吧。 \",\"img\":\"/up_img/xingzuoyunshi/jinniu/201611/z6N99h42228.jpg\",\"yi\":\"宜烹饪，宜求同存异，宜亲吻\",\"ji\":\"忌骑电动车，忌会谈，忌拍人肩膀\",\"shorttxt\":\"狮子座2016年11月3日整体运势\"}','{\"date\":\"2016-10-30~2016-11-05\",\"index\":[{\"title\":\"整体运势\",\"star\":\"3\"},{\"title\":\"爱情运势\",\"star\":\"4\"},{\"title\":\"事业学业\",\"star\":\"3\"},{\"title\":\"财富运势\",\"star\":\"3\"},{\"title\":\"健康运势\",\"star\":\"4\"},{\"title\":\"幸运颜色\",\"value\":\"银白色\"},{\"title\":\"幸运星座\",\"value\":\"天蝎座\"},{\"title\":\"提防星座\",\"value\":\"水瓶座\"}],\"content\":\"本周社交活动减少，而且你更多的是向往家庭生活，喜欢跟家人相处。职场上的表现过于安分守己，建议活跃热烈一点，多展现才能，这段时间沟通能力上升，不妨多跟同事们交流互动。投资总体上是有不错的机会，有投资想法的人可以大胆尝试，财运上可能会有一笔钱财进账，也可能会有一笔会家人付出的开销。对狮子来说，这周该对自己的收入支出做做统计了。\",\"url\":\"http://www.xzw.com/fortune/leo/2.html\",\"m_url\":\"http://m.xzw.com/fortune/leo/2.html\"}','{\"date\":\"2016-11-01~2016-11-30\",\"index\":[{\"title\":\"整体运势\",\"star\":\"4\"},{\"title\":\"爱情运势\",\"star\":\"4\"},{\"title\":\"事业学业\",\"star\":\"3\"},{\"title\":\"财富运势\",\"star\":\"4\"}],\"content\":\"这个月单身的狮子可别错过任何一个好机会了，有缘人很可能就在最近出现了，任何的社交聚会活动尽量别缺席，或是多跟亲朋好友联系，也许会有人给你牵红线。职场上会有一些小问题出现需要你亲自处理，还有新的任务带来巨大的压力，行动前务必要思考清楚。另外别为了工作而忽略了休息时间，拖垮了身体就得不偿失了。有不错的投资运，可适量下注，会有小小的收益。\",\"url\":\"http://www.xzw.com/fortune/leo/3.html\",\"m_url\":\"http://m.xzw.com/fortune/leo/3.html\"}','{\"date\":\"2016-01-01~2016-12-31\",\"index\":[{\"title\":\"整体运势\",\"star\":\"4\"},{\"title\":\"爱情运势\",\"star\":\"3\"},{\"title\":\"事业学业\",\"star\":\"3\"},{\"title\":\"财富运势\",\"star\":\"4\"}],\"content\":\"2016年上半年对狮子座来说有不少的事情积压在心头，当然也给你带来更多的压力。下半年视情况而调整时间分配，日子开始变得比较不那么单调，算是不好不坏吧。烦心、耗时的事情，包括人以及钱的困扰，这方面的困扰影响着狮子上半年的心情起伏跟情绪，想要缓解可能会需要一点时间。从9月开始，生活重心逐渐有一些调整，有许多新鲜不一的生活体验，与人互动接触的机会较多，生活比上半年要来得有趣多了。\",\"zt\":\"2016年上半年对狮子座来说有不少的事情积压在心头，当然也给你带来更多的压力。下半年视情况而调整时间分配，日子开始变得比较不那么单调，算是不好不坏吧。烦心、耗时的事情，包括人以及钱的困扰，这方面的困扰影响着狮子上半年的心情起伏跟情绪，想要缓解可能会需要一点时间。从9月开始，生活重心逐渐有一些调整，有许多新鲜不一的生活体验，与人互动接触的机会较多，生活比上半年要来得有趣多了。\",\"aiqing\":\"感情运势上单身者上半年有正桃花，有机会迷得真爱，下半年运势低沉；婚恋者与对方有矛盾出现，要注意第三者的插足。\",\"shiye\":\"事业运势不错，就是缺乏贵人扶持，建议多接触不同层面的人士，拓展人际关系圈，为事业带来更多好机会。\",\"caifu\":\"上半年财运旺盛，得财不费力，投资也有丰厚进账，到了下半年运势回落，会有破财迹象，要注意保管财物安群。\",\"jiankang\":\"健康方面，尽量少熬夜，减少夜生活，还要注意饮食健康，少吃甜食，多运动才不容易发胖。\"}','{\"zh\":\"尽管今年的桃花运很不错，但感情却没有很好的发展空间。已经有恋人的你需要注意会不会出现其他人破坏两个人之间的关系。在下半年，你的桃花运不断，身边不缺乏各种类型的异性，但你会对此表现得兴趣缺缺。\",\"nv\":\"由于土星进驻了恋爱宫，你会感到恋情给你带来了许多的快乐，并且你会从中领悟出了很多的道理，也开始成长起来了。在谈恋爱的过程中，你会越来越了解自己，懂得自己。你可能会遇到有着土星特质的对象，成熟稳重同时又勤奋上进。又或者会遇到和专家类型的对象，严肃但让人感觉非常地可靠。\",\"nan\":\"在你的潜意识中，你总是会想要挑战有关恋爱方面的不可能。越是有人打压你的恋情，或是反对你的恋情，你却越挫越勇。那些打压或是反对你的话语反而会成为助长你感情路上的肥料，越是遭遇反对，越是能够和另一半携手共度。因为你更加在乎精神上是否相合，所以不管这条路多辛苦都能够撑下去。\"}');");
E_D("replace into `xingzuo_yunshi_data` values('11','处女座','处女座的人，思虑周全，非常谦虚，谨慎保守，很有耐心，擅长分析，凡事要求做到尽善尽美，是个完美主义者。但易给人较大的压力，较被动严肃。','8月23日-9月23日','<ul class=\"clearfix\"> <br />\r\n			<li>关键词：完美</li><br />\r\n			<li>象　　征：处女</li><br />\r\n			<li>四象性：土</li><br />\r\n			<li>掌管宫位：第六宫</li><br />\r\n			<li>阴阳性：阴性</li><br />\r\n			<li>最大特征：分析力</li><br />\r\n			<li>主管星：水星</li><br />\r\n			<li>颜　　色：灰色</li><br />\r\n			<li>三方官：变动</li><br />\r\n			<li>掌管身体：肠、腹</li><br />\r\n			<li>珠　宝：蓝宝石、琥珀</li><br />\r\n			<li>幸运号码：7</li><br />\r\n			<li>金　属：水银</li><br />\r\n			<li>金　　句：Perfect!</li><br />\r\n		</ul>','2016-11-02 18:05:20','406','/static/upload/2016/xingzuo_yunshi_data_11_img.png','{\"date\":\"2016-11-2\",\"index\":{\"zonghe\":\"67\",\"aiqing\":\"71\",\"gongzuo\":\"67\",\"caiyun\":\"57\",\"jiankang\":\"75\",\"kaiyunfangwei\":\"正南\",\"xingyunshuzi\":\"6\",\"yanse\":\"蓝色\"},\"content\":\"很有可能突然接到主管来电要需要麻烦你准备些开会档，因为紧急需要使用之类的情况，有可能在这两天发生，请做好心理准备。对于这两天本来就有工作的你或许感到意外的情绪不会这么负面，也要承担很多工作量。 \",\"img\":\"/up_img/xingzuoyunshi/jinniu/201611/fXT3i9F0763.jpg\",\"yi\":\"宜冲动消费，宜听歌，宜收账\",\"ji\":\"忌野炊，忌替人受过，忌装病\",\"shorttxt\":\"处女座2016年11月2日整体运势\"}','{\"date\":\"2016-11-3\",\"index\":{\"zonghe\":\"69\",\"aiqing\":\"73\",\"gongzuo\":\"69\",\"caiyun\":\"59\",\"jiankang\":\"77\",\"kaiyunfangwei\":\"东南\",\"xingyunshuzi\":\"1\",\"yanse\":\"绿色\"},\"content\":\"这两天是出游的好日子，想要到附近的旅游景点去走走的话，就趁这两天启程吧！越是新鲜的环境，越会让你有一种小鸟出笼获得自由的畅快感觉哦！恋情方面，小打小闹的可能性较多，和恋人之间的小别扭，只要说点甜蜜的话就能安全过关！ \",\"img\":\"/up_img/xingzuoyunshi/jinniu/201611/4p73V2z9g19.jpg\",\"yi\":\"宜看电影，宜施舍，宜写诗\",\"ji\":\"忌求同存异，忌步行，忌未雨绸缪\",\"shorttxt\":\"处女座2016年11月3日整体运势\"}','{\"date\":\"2016-10-30~2016-11-05\",\"index\":[{\"title\":\"整体运势\",\"star\":\"4\"},{\"title\":\"爱情运势\",\"star\":\"4\"},{\"title\":\"事业学业\",\"star\":\"4\"},{\"title\":\"财富运势\",\"star\":\"3\"},{\"title\":\"健康运势\",\"star\":\"4\"},{\"title\":\"幸运颜色\",\"value\":\"绿色\"},{\"title\":\"幸运星座\",\"value\":\"天秤座\"},{\"title\":\"提防星座\",\"value\":\"天蝎座\"}],\"content\":\"本周利于各种的灵感创作、学习，为工作而出差乃平常之事，少了许多陪伴家人伴侣的时间。职场有一些事务正在紧张有序地进行当中，要是跟钱财利益有关的事项，最好是做足充分的沟通谈判，不要损害双方的利益。这段时间金钱上会有好消息传来，收入增加，不过家庭、恋爱或是出行会有不少的开销，或是会有意料之外的开支。\",\"url\":\"http://www.xzw.com/fortune/virgo/2.html\",\"m_url\":\"http://m.xzw.com/fortune/virgo/2.html\"}','{\"date\":\"2016-11-01~2016-11-30\",\"index\":[{\"title\":\"整体运势\",\"star\":\"4\"},{\"title\":\"爱情运势\",\"star\":\"4\"},{\"title\":\"事业学业\",\"star\":\"4\"},{\"title\":\"财富运势\",\"star\":\"4\"}],\"content\":\"工作心情很不错，烦恼减少，手头上的任务都能在指定时间内完成，每天都充满了战斗力。在下半月会有事业上的贵人出现，这个人有可能是职场新人，彼此之间有莫名的契合度。本月的运势表现很不错，上半月单身者有望坠入爱河，会有一段激情浪漫的恋情。最近的空闲时间都比较多，可以抽空出来约会朋友、爱人，吃喝玩乐，好好享受生活。\",\"url\":\"http://www.xzw.com/fortune/virgo/3.html\",\"m_url\":\"http://m.xzw.com/fortune/virgo/3.html\"}','{\"date\":\"2016-01-01~2016-12-31\",\"index\":[{\"title\":\"整体运势\",\"star\":\"4\"},{\"title\":\"爱情运势\",\"star\":\"4\"},{\"title\":\"事业学业\",\"star\":\"4\"},{\"title\":\"财富运势\",\"star\":\"5\"}],\"content\":\"2016年是处女座是个不是大好就是大坏的一年，也是人生新阶段的开展年，这一年你的人际关系、所在环境、作息方式或是生活重心、工作重心都有颇大的变化。上半年处女座们战况连连，或者会碰到不少状况，有的会要做些改变或调整才能安然度过，走出迷茫，下半年处女座的心情经常是郁闷不安，时好时坏地起伏着，原因多半跟人，跟钱有关。尽量保持乐观的心态对待，那么事情就会简单不少。\",\"zt\":\"2016年是处女座是个不是大好就是大坏的一年，也是人生新阶段的开展年，这一年你的人际关系、所在环境、作息方式或是生活重心、工作重心都有颇大的变化。上半年处女座们战况连连，或者会碰到不少状况，有的会要做些改变或调整才能安然度过，走出迷茫，下半年处女座的心情经常是郁闷不安，时好时坏地起伏着，原因多半跟人，跟钱有关。尽量保持乐观的心态对待，那么事情就会简单不少。\",\"aiqing\":\"本年度桃花运旺盛，但数量之多让你眼花缭乱，不知如何选择；有伴的人与爱人关系时好时坏，还要谨防第三者的捣乱。\",\"shiye\":\"事业上有更多的表现机会，有升职加薪的机会，但容易惹来小人嫉妒，背后少不免会有闲言闲语，忍一忍就好。\",\"caifu\":\"财运旺盛，到了下半年更是红火，但偏财运欠佳，建议少赌博，投资要注意风险，千万别太贪心。\",\"jiankang\":\"身体状态佳，就是要注意劳逸结合，做好身心调整。女性要注意妇科方面的疾病。\"}','{\"zh\":\"尽从整体来看，你的桃花运还是很不错的。有了木星的帮助，你今年会遇到很不错的恋人。最令人感到惊喜的是，心仪的对象会主动追求你哦！但要注意的是不要对喜欢的人过于苛刻，不然可能会吓跑对方。\",\"nv\":\"如果一直对为什么没有人追求自己感到疑惑， 其实原因是你的气场太强大了，让人望而却步。但不要灰心丧气，因为今年你会遇到那种“对你好、在乎你、条件非常好”的对象哦，但还是要仔细地观察一段时间才可以让自己陷进去哦。虽然你对爱情比较缺乏安全感，但是这次的对象能够通过你的每个考核，并且和你在一起哦。\",\"nan\":\"从去年开始，你可能就有了想要和对方在一起的对象，或是对你有着非常大吸引力的对象。那么今年你会开始认真地考虑和对方谈一段恋爱，最好的时机是在今年的四五月份，要牢牢把握住机会哦！意料之外的约会对你是非常好的事情，让感情自由地发展吧，你有更多的机会让自己的爱情充满了幸福的味道。\"}');");
E_D("replace into `xingzuo_yunshi_data` values('12','天秤座','天秤座的人很有气质，谈吐应对得体，善于与人沟通协调，是绝佳的谈判人才，属于迷人优雅派人物。只不过，思想易左右摇摆；追求外表光鲜，易失去自我。','9月24日-10月23日','<ul class=\"clearfix\">   <br />\r\n			<li>关键词：合伙</li><br />\r\n			<li>象　　征：天秤</li><br />\r\n			<li>四象性：风</li><br />\r\n			<li>掌管宫位：第七宫</li><br />\r\n			<li>阴阳性：阳性</li><br />\r\n			<li>最大特征：衡量抉择</li><br />\r\n			<li>主管星：金星</li><br />\r\n			<li>颜　　色：褐色</li><br />\r\n			<li>三方官：基本</li><br />\r\n			<li>掌管身体：胃脏</li><br />\r\n			<li>珠　宝：珊瑚、琥珀</li><br />\r\n			<li>幸运号码：3</li><br />\r\n			<li>金　属：木</li><br />\r\n			<li>金句：情人眼里出西施</li><br />\r\n		</ul>','2016-11-01 12:21:04','407','/static/upload/2016/xingzuo_yunshi_data_12_img.png','{\"date\":\"2016-11-01\",\"index\":[{\"title\":\"整体运势\",\"star\":\"3\"},{\"title\":\"爱情运势\",\"star\":\"3\"},{\"title\":\"事业学业\",\"star\":\"3\"},{\"title\":\"财富运势\",\"star\":\"4\"},{\"title\":\"健康指数\",\"value\":\"94%\"},{\"title\":\"商谈指数\",\"value\":\"87%\"},{\"title\":\"幸运颜色\",\"value\":\"黑色\"},{\"title\":\"幸运数字\",\"value\":\"5\"},{\"title\":\"速配星座\",\"value\":\"摩羯座\"}],\"content\":\"今天的你喜欢开开小玩笑，带动气氛，不过要注意言辞，别不小心得罪他人而不自知。做好自己的本职工作，对他人的事情最好保持沉默。要是需要发表意见，就持中肯态度就好了。\",\"url\":\"http://www.xzw.com/fortune/libra/\",\"m_url\":\"http://m.xzw.com/fortune/libra/\"}','{\"date\":\"2016-11-02\",\"index\":[{\"title\":\"整体运势\",\"star\":\"4\"},{\"title\":\"爱情运势\",\"star\":\"3\"},{\"title\":\"事业学业\",\"star\":\"4\"},{\"title\":\"财富运势\",\"star\":\"3\"},{\"title\":\"健康指数\",\"value\":\"87%\"},{\"title\":\"商谈指数\",\"value\":\"92%\"},{\"title\":\"幸运颜色\",\"value\":\"深蓝色\"},{\"title\":\"幸运数字\",\"value\":\"8\"},{\"title\":\"速配星座\",\"value\":\"双鱼座\"}],\"content\":\"总体来说今天运势很不错，想做什么就放胆去做吧，这正是表现能力的好时机。但是投资不宜操之过急，行动之前要多思考一下。恋爱中的人可安排一场浪漫约会，享受爱情的甜蜜。\",\"url\":\"http://www.xzw.com/fortune/libra/1.html\",\"m_url\":\"http://m.xzw.com/fortune/libra/1.html\"}','{\"date\":\"2016-10-30~2016-11-05\",\"index\":[{\"title\":\"整体运势\",\"star\":\"3\"},{\"title\":\"爱情运势\",\"star\":\"3\"},{\"title\":\"事业学业\",\"star\":\"3\"},{\"title\":\"财富运势\",\"star\":\"4\"},{\"title\":\"健康运势\",\"star\":\"3\"},{\"title\":\"幸运颜色\",\"value\":\"紫红色\"},{\"title\":\"幸运星座\",\"value\":\"双子座\"},{\"title\":\"提防星座\",\"value\":\"白羊座\"}],\"content\":\"这段时间事业合作上、人际关系上都有可能出现变动调整，也有可能跟一些客户会面，商谈重要项目。任务量有所增加，适合制定新计划，或是学习培训，部分人会有出差任务。财运上会有赚钱机会，不过也有压力存在，偶尔会有意外支出、用钱多，财政状况显得有点混乱，需要即使整理好。身体易出现毛病，要开始重视健康问题了。\",\"url\":\"http://www.xzw.com/fortune/libra/2.html\",\"m_url\":\"http://m.xzw.com/fortune/libra/2.html\"}','{\"date\":\"2016-11-01~2016-11-30\",\"index\":[{\"title\":\"整体运势\",\"star\":\"4\"},{\"title\":\"爱情运势\",\"star\":\"4\"},{\"title\":\"事业学业\",\"star\":\"4\"},{\"title\":\"财富运势\",\"star\":\"3\"}],\"content\":\"事业得到好运的眷顾，有机会在新的领域中发挥出超人的实力，得到领导赏识。这段时间会有不少发朋友聚会活动，单身朋友要记得多多参与，表现得自然大方，可是会吸引到部分异性。本月投资方面不适宜冲动行事，耐心一点多观察就能就减少不必要的损耗。花钱的地方蛮多的，出门在外有可能会有破财的可能性。\",\"url\":\"http://www.xzw.com/fortune/libra/3.html\",\"m_url\":\"http://m.xzw.com/fortune/libra/3.html\"}','{\"date\":\"2016-01-01~2016-12-31\",\"index\":[{\"title\":\"整体运势\",\"star\":\"4\"},{\"title\":\"爱情运势\",\"star\":\"4\"},{\"title\":\"事业学业\",\"star\":\"3\"},{\"title\":\"财富运势\",\"star\":\"3\"}],\"content\":\"天秤座在2016年状况多，很多事情都让你操碎了心，一直感觉不踏实，要事情解决了你才会心安，建议你顺其自然，不要想太多。上半年或是9月份之前，有比较多跟人有关的无奈事情发生，而9月之后，天秤座的日子会慢慢有变化，而期间业余一些事让你心生愁绪，需要做好心理调整。下半年，会出现事业瓶颈，不要固步自封，试着改变一下原有计划方法，或许会有新突破，化险为夷。\",\"zt\":\"天秤座在2016年状况多，很多事情都让你操碎了心，一直感觉不踏实，要事情解决了你才会心安，建议你顺其自然，不要想太多。上半年或是9月份之前，有比较多跟人有关的无奈事情发生，而9月之后，天秤座的日子会慢慢有变化，而期间业余一些事让你心生愁绪，需要做好心理调整。下半年，会出现事业瓶颈，不要固步自封，试着改变一下原有计划方法，或许会有新突破，化险为夷。\",\"aiqing\":\"感情生活多姿多彩，桃花灿烂。单身者脱单不是问题，有好姻缘到了就赶紧出手吧；恋爱中的人有分分合合的状况，要拿出真心对待。\",\"shiye\":\"充满了挑战和压力，承担的责任也更大，但整体尚算顺利。有关签约的事情要小心谨慎，看清楚才好决定。\",\"caifu\":\"通过自己的努力，存款有明显的增长，正财较好，而偏财一般，风险投资最好少碰，避免做大买卖。\",\"jiankang\":\"身体健康无恙，但建议喝酒吸烟，注意呼吸道疾病。家中老人的身体状况要多加留意。\"}','{\"zh\":\"最令人感到兴奋不已的消息是，在下半年你会和自己喜欢的对象在一起哦！不过你的桃花运大多来自职场和朋友，最大的可能是和某位朋友擦出火花。从朋友转变成恋人似乎会感到不安，但没有关系，因为爱对了人才是重要的事情，和身份无关。\",\"nv\":\"其实你并不是想要找一个真命天子，你需要的是一个能够在你迷茫的时候给你指出一条明道的人或是一个能够在你犹豫不决的时候帮你做决定的人。过去的择偶方式并不是适用于你了，你需要找到一个能够携手共度未来的人。今年就多花点时间和心思打扮一下自己吧，并且有空的时候多出门走一走，你就会遇到自己心目中的真命天子了。\",\"nan\":\"在这一整年里，你的桃花运会非常地强的。如果已经有了伴侣的话，虽然你可能还缺乏一点自信心，但你还是要做好心理准备承担结婚的责任了，珍惜和对方在一起的时间，你和另一半的感情会变得越来越稳固牢靠。但是对于交往还不算久的人来说，你很有可能会因为艳遇或是与某个人搞暧昧而令感情破裂，平时多把心思放在另一半身上吧。\"}');");
E_D("replace into `xingzuo_yunshi_data` values('13','天蝎座','天蝎座的人，外表低调，实则企图心旺盛，充满神秘感，不会轻易地与人互动。对自我要求很高，极具竞争力与战斗力，有洞悉他人心思的能力。','10月24日-11月22日','<ul class=\"clearfix\"> <br />\r\n			<li>关键词：性</li><br />\r\n			<li>象　　征：蝎子</li><br />\r\n			<li>四象性：水</li><br />\r\n			<li>掌管宫位：第八宫</li><br />\r\n			<li>阴阳性：阴性</li><br />\r\n			<li>最大特征：渴望</li><br />\r\n			<li>主管星：冥王星、火星</li><br />\r\n			<li>颜　　色：紫色、黑色</li><br />\r\n			<li>三方官：固定</li><br />\r\n			<li>掌管身体：性器官、鼻</li><br />\r\n			<li>珠　宝：碧玉、黑水晶</li><br />\r\n			<li>幸运号码：4</li><br />\r\n			<li>金　属：铁</li><br />\r\n			<li>金句：Come in...baby!!</li><br />\r\n		</ul>','2016-11-05 19:17:05','408','/static/upload/2016/xingzuo_yunshi_data_13_img.png','{\"date\":\"2016-11-5\",\"index\":{\"zonghe\":\"89\",\"aiqing\":\"93\",\"gongzuo\":\"89\",\"caiyun\":\"79\",\"jiankang\":\"97\",\"kaiyunfangwei\":\"正西\",\"xingyunshuzi\":\"3\",\"yanse\":\"橙色\"},\"content\":\"这两天工作顺畅还可得到伙伴的帮助，趁着活力十足赶紧提出企划，表现突出的话有机会为自己加分，也许会因此而得到奖励。可能一时受到他人影响，引起内在异样的情绪，幸好繁忙的事务及同事的陪伴让你忘记了不快，心情转为平和安稳。 \",\"img\":\"/up_img/xingzuoyunshi/tianxie/201611/bO72f33249C.jpg\",\"yi\":\"宜收账，宜追公交车，宜分享旅游攻略\",\"ji\":\"忌给花浇水，忌看中医，忌野炊\",\"shorttxt\":\"天蝎座2016年11月5日整体运势\"}','{\"date\":\"2016-11-6\",\"index\":{\"zonghe\":\"66\",\"aiqing\":\"70\",\"gongzuo\":\"66\",\"caiyun\":\"56\",\"jiankang\":\"74\",\"kaiyunfangwei\":\"正南\",\"xingyunshuzi\":\"2\",\"yanse\":\"蓝色\"},\"content\":\"主动说出你的想法更有利。这两天你的做事方式在别人眼中有些固执难以理解，继续默默去做的话，你有可能会成为孤家寡人，同时，工作/学业进度也会受到影响。建议你直接告诉大家，你到底想怎么做，大家才有可能帮助到你，为你介绍合适的机会。 \",\"img\":\"/up_img/xingzuoyunshi/tianxie/201611/3480y2784C6.jpg\",\"yi\":\"宜购买打折货品，宜观看现场演出，宜赠送礼物\",\"ji\":\"忌冷战，忌露天聚餐，忌酒后吐真言\",\"shorttxt\":\"天蝎座2016年11月6日整体运势\"}','{\"date\":\"2016-10-30~2016-11-05\",\"index\":[{\"title\":\"整体运势\",\"star\":\"4\"},{\"title\":\"爱情运势\",\"star\":\"3\"},{\"title\":\"事业学业\",\"star\":\"4\"},{\"title\":\"财富运势\",\"star\":\"4\"},{\"title\":\"健康运势\",\"star\":\"4\"},{\"title\":\"幸运颜色\",\"value\":\"黑色\"},{\"title\":\"幸运星座\",\"value\":\"巨蟹座\"},{\"title\":\"提防星座\",\"value\":\"金牛座\"}],\"content\":\"本周职场、财运、情场运势都开始逐渐回升。首先是事业上的工作表现相当抢眼，受到众人的注目，同事之间的误会得以冰释前嫌，人际互动进展良好，对人脉关系开始重视起来。再加上贵人资源，自信心爆棚，简直无往而不利。另外钱财收入增加，投资会有利好消息。最近有时候表现得很感性，偶尔会意气用事，可要控制好情绪了。\",\"url\":\"http://www.xzw.com/fortune/scorpio/2.html\",\"m_url\":\"http://m.xzw.com/fortune/scorpio/2.html\"}','{\"date\":\"2016-11-01~2016-11-30\",\"index\":[{\"title\":\"整体运势\",\"star\":\"4\"},{\"title\":\"爱情运势\",\"star\":\"3\"},{\"title\":\"事业学业\",\"star\":\"4\"},{\"title\":\"财富运势\",\"star\":\"3\"}],\"content\":\"越主动，越有福。本月爱情运较好，但若是不积极还是很难有实质性的发展。部分人结婚的念头十分强烈，可是对方似乎并没有这种想法。学习能力提高，再加上丰富的创造力，任何难题都是小case。与领导同事之间相处会有点困难，不妨多沟通，包容对方。对钱财的管理不太在行，建议请求有经验人士的帮忙，会收到很不错的效果。\",\"url\":\"http://www.xzw.com/fortune/scorpio/3.html\",\"m_url\":\"http://m.xzw.com/fortune/scorpio/3.html\"}','{\"date\":\"2016-01-01~2016-12-31\",\"index\":[{\"title\":\"整体运势\",\"star\":\"4\"},{\"title\":\"爱情运势\",\"star\":\"4\"},{\"title\":\"事业学业\",\"star\":\"3\"},{\"title\":\"财富运势\",\"star\":\"3\"}],\"content\":\"2016年让你费时和费事的事情比较多，生理和心理都出现怠倦，开始对未来有新的思考，尝试改变一成不变的状况。上半年，天蝎座心思时间会被一些事情占据着，而且还很花时间。下半年，想要做些改变的欲望愈来愈强烈，现有的生活模式、做事方式都想要一一摆脱开来，想要来点新尝试，即使是从来没接触过的领域。下半年是天蝎座准备迎向人生新局的酝酿期，多学习，做好万全准备迎接新挑战吧！\",\"zt\":\"2016年让你费时和费事的事情比较多，生理和心理都出现怠倦，开始对未来有新的思考，尝试改变一成不变的状况。上半年，天蝎座心思时间会被一些事情占据着，而且还很花时间。下半年，想要做些改变的欲望愈来愈强烈，现有的生活模式、做事方式都想要一一摆脱开来，想要来点新尝试，即使是从来没接触过的领域。下半年是天蝎座准备迎向人生新局的酝酿期，多学习，做好万全准备迎接新挑战吧！\",\"aiqing\":\"单身者脱单机会大增，有机会来一场浪漫的爱情，但时间长久就要看你自己了；有伴的人要时刻给爱情增加刺激，保持新鲜感。\",\"shiye\":\"事业运势动荡不安，状态比较艰苦，但在过程中不断适应变化，有贵人帮扶，也能度过险关。\",\"caifu\":\"上半年偏财运不错，会有不错的获利，但要见好就收，正财进账减少，下半年运势下滑，要谨记“贪心”二字，会让你输得很惨。\",\"jiankang\":\"上班族会有肩颈劳损的问题，要记得要经常走动，还要注意上火问题，容易引发喉咙痛。\"}','{\"zh\":\"在感情方面，你会经常思索着怎么突破或是改变。你的桃花运在年底会更好，而且决定你能不能拿下喜欢自己的人的重要机会。此外，今年特别适宜谈婚论嫁哦。不过你要记得缘分并不是就在自己的身边，走远一点看可能就会看到让自己倾心的人。\",\"nv\":\"由于今年你会因为各种事情而变得异常忙碌，尤其是在工作方面。你可能会因为忙碌而影响了两个人一起相处的时间，对话变得零碎不已，日积月累就会导致两个人的关系出现了问题。尽管再忙碌也要抽出时间和对方沟通交流，最好能够合理安排好自己的时间，工作之余就好好地和对方一起度过，多考虑一下对方的感受。\",\"nan\":\"九月后可能会有烂桃花缠身，这些烂桃花不仅仅会影响你和另一半的感情，而且还会对你的工作和朋友造成不少的麻烦。建议在接触异性的时候要擦亮眼睛，不要被对方的表象所迷惑了，色欲熏心非常容易酿成大祸。单身的你会参加一些自己感兴趣的聚会或是活动，即便是一个人的生活也不会显得太过冷清。\"}');");
E_D("replace into `xingzuo_yunshi_data` values('14','射手座','射手座的人，生性乐观开朗，热情奔放，崇尚自由，反应灵敏，极具创造力。待人友善又极具豪气，作风非常海派，人缘颇佳。','11月23日-12月21日','<ul class=\"clearfix\"> <br />\r\n			<li>关键词：哲学</li><br />\r\n			<li>象　　征：人马</li><br />\r\n			<li>四象性：火</li><br />\r\n			<li>掌管宫位：第九宫</li><br />\r\n			<li>阴阳性：阳性</li><br />\r\n			<li>最大特征：自由</li><br />\r\n			<li>主管星：木星</li><br />\r\n			<li>颜　　色：浅蓝色</li><br />\r\n			<li>三方官：变动</li><br />\r\n			<li>掌管身体：肝脏、臀部、大腿</li><br />\r\n			<li>珠　宝：紫水晶</li><br />\r\n			<li>幸运号码：6</li><br />\r\n			<li>金　属：锡</li><br />\r\n			<li>金句：不要排斥我！</li><br />\r\n		</ul>','2016-11-09 17:12:05','409','/static/upload/2016/xingzuo_yunshi_data_14_img.png','{\"date\":\"2016-11-9\",\"index\":{\"zonghe\":\"75\",\"aiqing\":\"56\",\"gongzuo\":\"97\",\"caiyun\":\"87\",\"jiankang\":\"60\",\"kaiyunfangwei\":\"西北\",\"xingyunshuzi\":\"5\",\"yanse\":\"橙色\"},\"content\":\"工作/学业是为了让自己过得更好！这两天的你将会对工作/学业和休息的关系，有更深的体会，也会做出适当的调整。自然有些工作/学业狂的身边的人或上司/老师，认为这是你开始变得懒散的体现。但是作为一个人长远的发展来看，这两天的做法是值得赞赏的！ \",\"img\":\"/up_img/xingzuoyunshi/sheshou/201611/7ny4515VJ31.jpg\",\"yi\":\"宜分享旅游攻略，宜关闭朋友圈一天，宜探讨人生重大问题\",\"ji\":\"忌看3D电影，忌买双色球，忌看星星\",\"shorttxt\":\"射手座2016年11月9日整体运势\"}','{\"date\":\"2016-11-10\",\"index\":{\"zonghe\":\"67\",\"aiqing\":\"70\",\"gongzuo\":\"67\",\"caiyun\":\"57\",\"jiankang\":\"75\",\"kaiyunfangwei\":\"正南\",\"xingyunshuzi\":\"10\",\"yanse\":\"蓝色\"},\"content\":\"平常就劳碌的你，这两天只能默默的工作，平静的渡过，将不急迫的事情先暂缓，经过充电过后再重新出发。这两天在爱情方便也难以扩张，还是保持先前的状态，等待时机来临再出击，才有胜利的希望。 \",\"img\":\"/up_img/xingzuoyunshi/sheshou/201611/RZ466M758ed.jpg\",\"yi\":\"宜追问真相，宜叫外卖，宜追悔\",\"ji\":\"忌泛舟水上，忌给妈妈打电话，忌做鬼脸\",\"shorttxt\":\"射手座2016年11月10日整体运势\"}','null','null','{\"date\":\"2016-01-01~2016-12-31\",\"index\":[{\"title\":\"整体运势\",\"star\":\"3\"},{\"title\":\"爱情运势\",\"star\":\"3\"},{\"title\":\"事业学业\",\"star\":\"3\"},{\"title\":\"财富运势\",\"star\":\"4\"}],\"content\":\"2016年射手座事情很多，全都要花费很长一段时间才能完成，接二连三的任务也接踵而来，对你来说是相对较为忙碌的一年。上半年烦心的事情比较多，人事和环境的变动，本人或家人要留意身体健康的状况。下半年射手座依然是忙得不可开交，闲不下来的节奏，某些事情需要重复修改，职场或朋友圈人际关系发生变化，能认识厉害人物。2016年事情比较多，要花不少时间，也有很多地方需要与人磨合，为事业铺路。\",\"zt\":\"2016年射手座事情很多，全都要花费很长一段时间才能完成，接二连三的任务也接踵而来，对你来说是相对较为忙碌的一年。上半年烦心的事情比较多，人事和环境的变动，本人或家人要留意身体健康的状况。下半年射手座依然是忙得不可开交，闲不下来的节奏，某些事情需要重复修改，职场或朋友圈人际关系发生变化，能认识厉害人物。2016年事情比较多，要花不少时间，也有很多地方需要与人磨合，为事业铺路。\",\"aiqing\":\"爱情就像龙卷风一样袭来，但停留时间不长，单身者身边烂桃花居多；恋爱中的人感情受到考验，有更优秀的人出现。\",\"shiye\":\"工作上忙得焦头烂额，但机会多多，才华得到充分展现。如果上半年努力拼搏，在下半年就能迎来好的收获。\",\"caifu\":\"事业为你带来好财运，但生活奢侈，消费无节制，甚至有带你挥霍无度，偏财运一般，要注意理财。\",\"jiankang\":\"今天身体各方面的机能欠佳，情绪低落，比较压抑，要懂得给自己舒缓压力。\"}','{\"zh\":\"在做事情的时候要三思而后行，尤其是处理感情上的问题更要如此。因为今年你的工作、生活方面的压力都比较大，对感情可能没有那么地上心。但其实在你的身边有不少异性喜欢你，但迟钝的你总是很难发现。\",\"nv\":\"由于天王星来到了恋爱宫坐镇，今年就不会出现类似于对象不好或是情况不对劲之类的现象了。所以，如果碰到了自己喜欢的人要把握好机会。此外，单身的你可能会给对方留下非常好的印象哦，也有机会认识对你非常友善的对象，能不能把握住机会就要看你怎么去经营了。但一般来说，你们会聊得很投契。\",\"nan\":\"你会特别想要“做自己”，对另一半的要求可能没有以往那么地配合，也不想再像以往那般忍气吞声。尤其是在工作非常忙碌的时候，对方会觉得你变得难以相处。这样的话会对两个人的感情发展产生不利的影响哦！如果觉得忙碌的工作让你喘不过气来，那么心情不好的时候就去跑跑步吧，把消极的情绪都消磨掉了再回家。\"}');");

